INSERT INTO `benhvien` (`id`, `idTheLoai`, `TenBenhVien`, `TenBenhVienKhongDau`, `Hinh`, `ThongTin`, `Link`, `created_at`, `updated_at`) VALUES
(1, 1, 'Bệnh viện Từ Dũ', 'benh-vien-tu-du', 'Oga8ry00_tải xuống.jpg', '<p><span style=\"color:rgb(77, 81, 86); font-family:arial,sans-serif; font-size:14px\">Bệnh viện Từ Dũ là một bệnh viện phụ sản ở Thành phố Hồ Chí Minh, Việt Nam. Với gần 100 năm làm việc và phát triển, Bệnh viện Từ Dũ là nơi ra đời của rất nhiều thế hệ em bé sinh ra ở tại Thành phố Hồ Chí Minh.</span></p>\r\n', 'tudu.com.vn', '2020-12-27 16:00:29', '2020-12-27 16:00:29'),
(2, 2, 'Bệnh viện Chợ Rẫy', 'benh-vien-cho-ray', 'twxSRXL3_tải xuống (1).jpg', '<p><span style=\"color:rgb(77, 81, 86); font-family:arial,sans-serif; font-size:14px\">Bệnh viện Chợ Rẫy là một bệnh viện đa khoa trung ương cấp quốc gia, phục vụ toàn miền Nam, nằm tại Thành phố Hồ Chí Minh. Đây cũng là 1 trong những bệnh viện tuyến cuối lớn nhất tại Việt Nam.</span></p>\r\n', 'choray.vn', '2020-12-27 16:01:29', '2020-12-27 16:01:29'),
(3, 3, 'Bệnh viện Trung Ương Huế', 'benh-vien-trung-uong-hue', 'p2FCWYbz_tải xuống (2).jpg', '<p><span style=\"color:rgb(77, 81, 86); font-family:arial,sans-serif; font-size:14px\">Bệnh viện Trung ương Huế, được thành lập năm 1894, là bệnh viện Tây y đầu tiên của Việt Nam. Bệnh viện tọa lạc tại số 16 đường Lê Lợi, phường Vĩnh Ninh, thành phố Huế, tỉnh Thừa Thiên - Huế.</span></p>\r\n', 'bvtwhue.com.vn', '2020-12-27 16:02:42', '2020-12-27 16:02:42'),
(5, 4, 'Bệnh viện C Đà Nẵng', 'benh-vien-c-da-nang', 'z869eDwQ_tải xuống (3).jpg', '<p><span style=\"color:rgb(77, 81, 86); font-family:arial,sans-serif; font-size:14px\">Đà Nẵng C Hospital is the second-largest public hospital in the Vietnamese city of Đà Nẵng, after Đà Nẵng Hospital. It is located at 122 Hai Phong Street.</span></p>\r\n', 'bvcdn.org.vn', '2020-12-27 16:04:01', '2020-12-27 16:04:01'),
(6, 5, 'Bệnh Viện Trung Ương Thái Nguyên', 'benh-vien-trung-uong-thai-nguyen', 'LpR5JXZN_tải xuống (4).jpg', '<p><span style=\"color:rgb(77, 81, 86); font-family:arial,sans-serif; font-size:14px\">Bệnh viện Đa khoa Trung ương Thái Nguyên là bệnh viện hạng I trực thuộc Bộ Y tế, bệnh viện tuyến cuối của Bộ Y tế ở khu vực Trung du Miền núi phía Bắc được thành lập từ năm 1951, có nhiệm vụ khám chữa bệnh cho nhân dân các dân tộc miền núi vùng Đông bắc Việt Nam.</span></p>\r\n', 'bvdktuthainguyen.gov.vn', '2020-12-27 16:05:20', '2020-12-27 16:05:20'),
(7, 6, 'Bệnh Viện Bạch Mai', 'benh-vien-bach-mai', 'wCex88se_tải xuống (5).jpg', '<p><span style=\"color:rgb(77, 81, 86); font-family:arial,sans-serif; font-size:14px\">Bệnh viện Bạch Mai nằm ở số 78 đường Giải Phóng, phường Phương Mai, quận Đống Đa, thành phố Hà Nội, là một trong những bệnh viện lớn nhất của Việt Nam. Bệnh viện Bạch Mai là bệnh viện đầu tiên trong nước được nhận danh hiệu đặc biệt.</span></p>\r\n', 'bachmai.gov.vn', '2020-12-27 16:06:26', '2020-12-27 16:06:26'),
(8, 7, 'Bệnh viện Hoàn Mỹ Đà Nẵng', 'benh-vien-hoan-my-da-nang', 'lJ0J1xhG_tải xuống (6).jpg', '<p><span style=\"color:rgb(77, 81, 86); font-family:arial,sans-serif; font-size:14px\">Bệnh viện Hoàn Mỹ Đà Nẵng là một bệnh viện đa khoa tư nhân, có trụ sở tại 161 Nguyễn Văn Linh, Thạc Gián, Thanh Khê, Đà Nẵng. Hiện tại, Bệnh viện Hoàn Mỹ Đà Nẵng có quy mô chỉ tiêu gần 400 giường bệnh nội trú.</span></p>\r\n', 'hoanmydanang.com', '2020-12-27 16:07:40', '2020-12-27 16:07:40');

