--
-- Cấu trúc bảng cho bảng `tintuc`
--

CREATE TABLE `tintuc` (
  `id` int(10) UNSIGNED NOT NULL,
  `TieuDe` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `TieuDeKhongDau` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `TomTat` text COLLATE utf8_unicode_ci NOT NULL,
  `NoiDung` longtext COLLATE utf8_unicode_ci NOT NULL,
  `Hinh` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `NoiBat` int(11) NOT NULL DEFAULT 0,
  `SoLuotXem` int(11) NOT NULL DEFAULT 0,
  `NgayDang` date DEFAULT NULL,
  `idLoaiTin` int(10) UNSIGNED NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `tintuc`
--

INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `NgayDang`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(1, 'Một số phương thuốc hay chữa đàm nhiệt khái thấu', 'mot-so-phuong-thuoc-hay-chua-dam-nhiet-khai-thau', '<p><strong>Đàm nhiệt khái khấu là chứng bệnh rất hay gặp trong mùa thu và đông, nhất là ở người vốn tạng nhiệt. Nguyên nhân do khí trời táo nhiệt, đờm với nhiệt quấn quít lấy nhau phát sinh chứng ho, thở, đờm nhiều và dính đặc, gọi là đàm nhiệt khái thấu. Bệnh hay gặp trong ho gà, viêm phổi, viêm khí phế quản, áp xe phổi mủ, hen suyễn, tâm phế mạn...</strong></p>\r\n', '<p><span style=\"font-family:noto serif; font-size:17px\">Người bệnh có triệu chứng: phát sốt rồi sinh ho, đờm nhiều và dính đặc, sắc vàng như mủ, hơi thở gấp, trong hung cách bức bách khó chịu; mạch phù, hoạt, rêu lưỡi vàng và nhờn bẩn. Dùng bài&nbsp;</span><em>Tả bạch tán</em><span style=\"font-family:noto serif; font-size:17px\">: tang bạch bì 40g, địa cốt bì 40g, sinh cam thảo 20g. 3 vị sấy khô tán bột mịn. Mỗi lần dùng 20g bột cùng với 20g gạo tẻ, 30 lá trúc diệp, đun với 2 bát nước, cạn còn 1 bát, bỏ bã, uống nóng 1 lần. Nếu có sốt gia tri mẫu 12g, hoàng cầm 12g.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Đàm nhiệt khái thấu là chứng  hay gặp trong một số bệnh hô hấp.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/27/anh_thay_tr13_resize.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Đàm nhiệt khái thấu là chứng  hay gặp trong một số bệnh hô hấp.\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Đàm nhiệt khái thấu là chứng hay gặp trong một số bệnh hô hấp.</em></p>\r\n\r\n<p>Khi sốt đã giảm, đờm loãng dễ ra, trong hung cách bớt bức bách khó chịu, khát nước, rêu lưỡi vàng nhờn, nên chuyển dùng bài&nbsp;<em>Thanh phế thang</em>: mạch môn đông 12g, tri mẫu 12g, cam thảo 4g, hoàng cầm 8g, thiên môn đông 12g, bối mẫu 12g, quất hồng bì 4g, tang bạch bì 12g. Tất cả cho vào nồi, đổ 3 bát nước, đun cạn còn 1 bát, bỏ bã, uống 1 lần.</p>\r\n\r\n<p>Nếu người vẫn sốt, đau đầu, ho khan không đờm, khí nghịch mà suyễn, ngực đầy tâm phiền, họng khô mũi ráo, lưỡi khô không rêu, mạch hư đại mà sác, hay gặp ở bệnh viêm phế quản, giai đoạn phục hồi sau viêm phổi, họng tê mất tiếng, dùng bài&nbsp;<em>Thanh táo cứu phế thang:</em>&nbsp;thạch cao 20g, tang diệp 12g, hạnh&nbsp; nhân 12g, a giao 12g, tỳ bà diệp 12g, cam thảo 4g, nhân sâm 12g, hồ ma nhân 12g, mạch môn đông 20g. Tất cả cho vào nồi, đổ 5 bát nước, đun cạn còn 2 bát, bỏ bã, chia uống 2 lần trong ngày.</p>\r\n\r\n<p>Nếu đờm nhiều, thêm bối mẫu 12g, qua lâu 12g, huyết khô gia sinh địa 24g, nhiệt nhiều, thêm thủy ngưu giác 12g, linh dương giác 4g hoặc thêm ngưu hoàng 4g.</p>\r\n\r\n<p><img alt=\"Tang bạch bì (vỏ rễ cây dâu) là vị thuốc trong bài Tả bạch tán trị đàm nhiệt khái thấu.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/27/tang_bach_bi.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Tang bạch bì (vỏ rễ cây dâu) là vị thuốc trong bài Tả bạch tán trị đàm nhiệt khái thấu.\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Tang bạch bì (vỏ rễ cây dâu) là vị thuốc trong bài Tả bạch tán trị đàm nhiệt khái thấu.</em></p>\r\n\r\n<p>Nếu để lâu không chữa, đại tiểu tiện sẽ bí và sáp, suyễn thấu mãi không dứt, đờm đặc dính, sắc vàng, đó là nhiệt nhiều ở trong hung cách, uống bài&nbsp;<em>Nhân sâm tả phế thang</em>: sơn chi 12g, liên kiều 12g, đại hoàng 12g, chỉ xác 4g, nhân sâm 8g, cát cánh 3g, bạc hà (cho sau) 4g, hoàng cầm 12g, cam thảo 4g, hạnh nhân 12g, tang bạch bì 12g. Tất cả cho vào nồi, đổ 3 bát nước, đun còn 2 bát, bỏ bạc hà vào, đun tiếp còn 1 bát, bỏ bã, uống làm 1 lần.</p>\r\n\r\n<p>Nếu tới thời kỳ nhiệt thịnh làm hại tới phế lạc, mỗi khi ho bật ra máu tươi..., dùng bài&nbsp;<em>Ngọc nữ tiễn&nbsp;</em>hoặc&nbsp;<em>Thái bình thang:</em></p>\r\n\r\n<p><em>Thái bình thang:</em>&nbsp;thạch cao sống 40g, bạch mao căn 40g, sinh địa 40g, ngẫu tiết thán 40g, đại hoàng thán 12g. Tất cả cho vào nồi, đun với 1 bát đồng tiện và 3 bát nước, cạn còn 2 bát, bỏ bã. Chia uống 2 lần.</p>\r\n\r\n<p><em>Ngọc nữ tiễn:</em>&nbsp;sinh thạch cao 24g, tri mẫu 12g, mạch môn đông 16g, sinh địa 32g, ngưu tất 12g. Tất cả cho vào nồi, đổ 5 bát nước đun còn 2 bát, bỏ bã, uống làm 2 lần.</p>\r\n', '1TNtfSL5_anh_thay_tr13_resize.jpg', 1, 0, '2020-12-27', 1, '2020-12-27 14:57:51', '2020-12-27 14:57:51'),
(2, 'Bài thuốc trị viêm tai giữa', 'bai-thuoc-tri-viem-tai-giua', '<p><strong>Viêm tai giữa là bệnh gặp ở cả người lớn và trẻ em. Nếu không chữa trị kịp thời sẽ dẫn đến viêm tai xương chũm và gây nhiều biến chứng nguy hiểm như thủng màng nhĩ, giảm thính lực, xơ hóa màng nhĩ... Nguyên nhân gây bệnh thường do viêm mũi họng, viêm xoang, viêm amidan, viêm VA... Ngoài ra còn có thể do chấn thương, viêm ở vòm mũi họng, thoái hóa đuôi cuốn mũi dưới dẫn đến tắc vòi nhĩ...</strong></p>\r\n', '<p>Người bệnh có triệu chứng đau tai, chảy mủ tai, giảm sức nghe, còn có ù tai, cảm giác nặng tai hay nghe lọc ọc trong tai. Nếu ở trẻ em có thể kèm theo chán ăn, khó ngủ, quấy đêm, sốt, tiêu chảy, nôn trớ, chảy nghẹt mũi, ho, vật vã... Nguyên nhân theo y học cổ truyền là do phong nhiệt và nhiệt độc gây ra. Lúc đầu là cấp tính, nếu không chữa trị cẩn thận sẽ trở nên mạn tính dễ tái phát. Sau đây là một số bài thuốc trị theo từng thể.</p>\r\n\r\n<h2><span style=\"font-size:17px\">Viêm tai giữa cấp</span></h2>\r\n\r\n<p>Do phong nhiệt, nhiệt độc xâm phạm vào can đởm. Người bệnh có triệu chứng: sốt, sợ lạnh, đau đầu, ù tai, đau trong tai, chảy mủ tai vàng dặc hoặc dính máu, rêu lưỡi vàng, mạch huyền sác. Phép chữa: sơ phong thanh nhiệt, trừ thấp nhiệt ở kinh can đởm. Dùng một trong các bài:</p>\r\n\r\n<p><em>Bài 1 - Sài hồ thanh can thang gia giảm:&nbsp;</em>sài hồ, long đởm thảo, hoàng cầm, chi tử, ngưu bàng tử, mỗi vị đều 12g, bạc hà 6g, kim ngân hoa 20g. Nếu tai chảy mủ lẫn máu thì thêm sinh địa 16g, đan bì 12g. Sắc uống.</p>\r\n\r\n<p><em>Bài 2 - Long đởm tả can thang gia giảm:&nbsp;</em>long đởm thảo, hoàng cầm, mộc thông, sinh địa, sa tiền tử, trạch tả, mỗi vị đều 12g, chi tử, đương quy, mỗi vị 8g, cam thảo 4g. Nếu sốt cao, tai chảy mủ đặc có máu, mặt đỏ, chất lưỡi đỏ thêm kim ngân hoa 16g, liên kiều 12g. Nếu táo bón, thêm đại hoàng 6g. Nếu sốt ít, trong tai đau tức nhiều, mủ ra ít thì bỏ sinh địa, thêm ý dĩ 16g, thuyền thoái, thạch xương bồ, thương truật, mỗi vị 6g. Sắc uống.</p>\r\n\r\n<p><img alt=\"Viêm tai giữa thường do viêm mũi họng, viêm xoang, viêm amidan, viêm VA...\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/22/viem-tai-giua_taimuihongsg_resize.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Viêm tai giữa thường do viêm mũi họng, viêm xoang, viêm amidan, viêm VA...\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Viêm tai giữa thường do viêm mũi họng, viêm xoang, viêm amidan, viêm VA...</em></p>\r\n\r\n<h2><span style=\"font-size:17px\">Viêm tai giữa mạn</span></h2>\r\n\r\n<p>Người bệnh có triệu chứng đau tai kéo dài, không sốt là do hư hỏa ở thận. Nếu đau tai kéo dài kèm theo ăn kém, người gầy, tiêu chảy là do tỳ hư thấp nhiệt. Bệnh chia làm 3 thể:</p>\r\n\r\n<p><strong><em>Thể can kinh thấp nhiệt:</em></strong>&nbsp;Người bệnh đau nhức tai, mủ chảy đặc dính, mùi hôi, lượng nhiều. Phép chữa: thanh can lợi thấp. Dùng bài&nbsp;<em>Long đởm tả can thang&nbsp;</em>(giống phần viêm tai giữa cấp).</p>\r\n\r\n<p><strong><em>Thể thận hư hóa viêm:&nbsp;</em></strong>Người bệnh chảy mủ tai thường xuyên, mủ loãng, tai ù nghe kém, hoa mắt chóng mặt, ngủ ít, lưng gối đau mỏi, mạch tế sác. Phép chữa: dưỡng âm thanh nhiệt, bổ thận thông khiếu. Dùng một trong các bài:</p>\r\n\r\n<p><em>Bài 1: Tri bá địa hoàng thang:&nbsp;</em>thục địa 12g, hoài sơn 16g, sơn thù, trạch tả, đan bì, phục linh, tri mẫu, hoàng bá, mỗi vị 8g. Sắc uống hoặc tán bột làm viên, ngày uống 18g, chia 3 lần.</p>\r\n\r\n<p><em>Bài 2 - Đại bổ âm hoàn:</em>&nbsp;hoàng bá, tri mẫu, mỗi vị 12g, thục địa, quy bản, mỗi vị 16g. Sắc uống hoặc làm viên, ngày uống 16g, chia 3 lần. Uống lâu dài.</p>\r\n\r\n<p><img alt=\"Sài hồ là vị thuốc trong bài Sài hồ thanh can thang gia giảm trị viêm tai giữa cấp.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/22/vi-thuoc-hai-sai_resize.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Sài hồ là vị thuốc trong bài Sài hồ thanh can thang gia giảm trị viêm tai giữa cấp.\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Sài hồ là vị thuốc trong bài Sài hồ thanh can thang gia giảm trị viêm tai giữa cấp.</em></p>\r\n\r\n<p><strong><em>Thể tỳ hư:&nbsp;</em></strong>Thường gặp ở trẻ em. Trẻ&nbsp; có triệu chứng tai chảy mủ loãng kéo dài, sắc mặt vàng bủng, ăn kém chậm tiêu, đại tiện lỏng, mệt mỏi, mạch hoãn nhược. Phép chữa: kiện tỳ hóa thấp. Dùng một trong các bài:</p>\r\n\r\n<p><em>Bài 1: Thanh tỳ thang gia giảm:</em>&nbsp;hoàng liên, bạch biển đậu, bạch thược, phục linh, cốc nha, mỗi vị 8g; trạch tả, sơn dược, mỗi vị 12g; thuyền thoái 4g. Sắc uống.</p>\r\n\r\n<p><em>Bài 2: Sâm linh bạch truật tán gia giảm:</em>&nbsp;đảng sâm, ý dĩ, liên nhục, mỗi vị 12g, bạch truật, phục linh, hoàng liên, sa nhân, hoàng bá, trần bì, cát cánh, mỗi vị 8g, cam thảo 4g, sơn dược, biển đậu, mỗi vị 16g. Tất cả tán bột, ngày&nbsp; uống 20g, chia 3 lần.</p>\r\n\r\n<p><em>Bài 3: Bổ trung ích khí thang gia giảm:&nbsp;</em>đảng sâm, hoàng kỳ, sài hồ, bạch truật, phục linh, mỗi vị 12g, thăng ma, đương quy, hoàng bá, hoàng liên, mỗi vị 8g, cam thảo 4g, trần bì 6g. Tất cả tán bột, ngày uống 20g, chia 3 lần.</p>\r\n\r\n<p><strong>Để phòng bệnh, cần lưu ý:&nbsp;</strong>Khi vệ sinh tai tránh chà xát mạnh làm tổn thương niêm mạc tai, có thể gây thủng màng nhĩ và gây viêm tai giữa; không để nước bẩn vào trong tai (đặc biệt khi đi bơi và gội đầu); điều trị triệt để nếu mắc bệnh về mũi họng. Với trẻ em, thường xuyên vệ sinh tay cho trẻ và để các đồ vật không sạch sẽ cách xa tầm với của trẻ; nếu trẻ bú bình hãy giữ trẻ ngồi thẳng, trẻ bú mẹ không cho bú nằm.</p>\r\n', 'PjGSJmHm_viem-tai-giua_taimuihongsg_resize.jpg', 0, 0, '2020-12-27', 1, '2020-12-27 14:58:52', '2020-12-27 14:58:52'),
(3, 'Đu đủ - món ăn bổ dưỡng, đẹp người, mịn da', 'du-du-mon-an-bo-duong-dep-nguoi-min-da', '<p><span style=\"color:rgb(85, 85, 85); font-family:roboto,arial,helvetica,sans-serif; font-size:14px\">Đu đủ có nguồn gốc ở vùng nhiệt đới châu Mỹ. Vào thế kỷ thứ 16, người Tây Ban Nha đưa cây đu đủ vào vùng Caribe và các nước Đông Nam Á, từ đó tiếp tục được trồng rộng rãi ở Ấn Độ, Srilanca, châu Đại Dương và châu Phi.</span></p>\r\n', '<p>Cây đu đủ trồng hiện nay là giống lai tự nhiên, có tên khoa học: Carica papaya L., họ Đu đủ (Papayaceae). Đu đủ trở thành giống cây ăn quả quan trọng ở Việt Nam cũng như nhiều nước nhiệt đới khác. Nó còn có tên khác: thù đủ, phiên mộc qua, thạch qua, đông qua thụ, vạn thọ quả, phiên quả, mộc quả, mộc đông quả, nhũ quả. Bộ phận dùng làm thuốc là quả, lá, rễ và nhựa.</p>\r\n\r\n<p>Quả đu đủ chín chứa nước, glucid, protein, cellulose, calci, P; các vitamin A, C, B. Quả xanh có nhựa mủ (trong có papain, carpain, myrosin); saccharose, acid hữu cơ (acid tartic, acid malic); cácvitamin và carotenoid.</p>\r\n\r\n<p>Theo nghiên cứu, 100g quả đu đủ chín có 74-80mg vitamin C, 500-1250IU carotene. Quả đu đủ màu vàng nhạt có nhiều beta carotene, cryptoxanthin, violaxanthin, cryptoxanthin monoepoxide. Quả đu đủ màu hồng có nhiều lycopene. Đu đủ còn có vitamin B1, B2&nbsp;và các khoáng chất (kali, magiê, sắt, kẽm...). Chất papain chịu được nhiệt độ 70<sup>0</sup>C trong 30 phút, có tác dụng tiêu hóa thịt và protein, chất này có nhiều trong lá, thân, quả xanh và có ít trong quả chín. Chất carpain còn có tác dụng làm chậm nhịp tim. Cao lá đu đủ có tác dụng kháng khuẩn, kháng virus, kháng u bướu. Quả đu đủ có nhiều thành phần hữu ích trong việc phòng chống các bệnh tim mạch và ung thư, ngăn ngừa các chất có hại cho làn da, giữ cho da khỏe đẹp và tăng sức đề kháng cho cơ thể.</p>\r\n\r\n<p>Theo Đông y, quả đu đủ chín vị ngọt tính mát, lành tính, không có độc tố. Ăn vào mùa nào cũng tốt cho sức khỏe: ăn mùa xuân hè có tác dụng thanh tâm nhuận phế, giải nhiệt, giải độc; ăn vào mùa thu đông có tác dụng nhuận táo, ôn bổ tỳ vị, dưỡng can, nhuận phế, chỉ khái, hóa đàm. Ăn đu đủ thường xuyên có tác dụng bổ máu, giúp phục hồi chức năng gan. Trong đu đủ có nhiều vitamin C và carotene, có tác dụng chống ôxy hóa và tăng sức đề kháng cho cơ thể.</p>\r\n\r\n<p><img alt=\"Bộ phận dùng làm thuốc là quả, lá, rễ và nhựa đu đủ.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/22/unnamed.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Bộ phận dùng làm thuốc là quả, lá, rễ và nhựa đu đủ.\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Bộ phận dùng làm thuốc là quả, lá, rễ và nhựa đu đủ.</em></p>\r\n\r\n<h2><span style=\"font-size:17px\">Các bài thuốc có quả đu đủ</span></h2>\r\n\r\n<p><em>Nhựa mủ đu đủ làm thuốc trong y học hiện đại&nbsp;</em>(chiết papain, carpain...)</p>\r\n\r\n<p><em>Tẩy giun kim cho trẻ nhỏ:&nbsp;</em>cho trẻ ăn đu đủ chín (50-100g) mỗi ngày, trong 7-10 ngày (sau bữa cơm chiều).</p>\r\n\r\n<p><em>Quả đu đủ xanh hay lá</em>&nbsp;để làm mềm những cục thịt cứng, nhuận tràng, lợi tiêu hóa. Quả non hầm với chân giò lợn để lợi sữa.</p>\r\n\r\n<p><em>Chữa ho, viêm phổi, mất tiếng:&nbsp;</em>hoa đu đủ hấp với đường phèn.</p>\r\n\r\n<p><em>Chữa bong gân, sai khớp:&nbsp;</em>đu đủ xanh 10g, lá na 10g, muối ăn 5g, vôi tôi 5g. Tất cả giã nát, phết lên gạc, đắp lên chỗ sưng đau sau khi đã nắn chỉnh hình khớp.</p>\r\n\r\n<p><em>Chữa rắn độc cắn:</em>&nbsp;lá đu đủ, rễ chỉ thiên, lá hoặc quả ớt, mỗi vị 50g. Tất cả rửa sạch, giã nát, thêm ít nước, gạn nước uống, bã đắp vào vết rắn cắn.</p>\r\n\r\n<p>Hoặc rễ đu đủ 20g, lá xuyên tiêu 10g, hồng bì 5 hạt. Giã nát, thêm nước, gạn nước cho uống, bã đắp.</p>\r\n\r\n<p><em>Chữa ho, viêm họng:&nbsp;</em>hoa đu đủ đực 15g, xạ can 10g, mạch môn 10g, lá húng chanh 10g. Tất cả cho vào bát nhỏ, thêm ít muối, hấp cơm, nghiền nát. Ngậm và nuốt nước.</p>\r\n\r\n<p><em>Chữa ho gà:&nbsp;</em>hoa đu đủ đực sao vàng 20g, trần bì 20g, vỏ rễ dâu (tẩm mật sao) 20g, bách bộ 12g, phèn phi 12g. Tất cả sấy khô tán bột. Ngày uống 3 lần. Trẻ em 1-5 tuổi, mỗi lần 1-4g; trẻ 5-10 tuổi, mỗi lần 5-8g.</p>\r\n\r\n<p><em>Thuốc lợi sữa:</em>&nbsp;đu đủ xanh 50g, lá sung non 50g, chân giò 1 cái, gạo nếp 100g. Đu đủ gọt vỏ bỏ hạt; lá sung rửa sạch, băm nát; chân giò cạo bỏ lông, rửa sạch chặt miếng. Tất cả nấu cháo, chia 2 lần ăn trong ngày.</p>\r\n\r\n<p><em>Chữa lở mặt, lở đầu:</em>&nbsp;nhựa quả đu đủ xanh 1g, bột hàn the 1g, thêm ít nước, trộn đều, bôi lên vết lở hàng ngày.</p>\r\n\r\n<p><strong>Kiêng kỵ:</strong>&nbsp;Phụ nữ có thai không nên dùng nhựa và ăn đu đủ xanh.</p>\r\n\r\n<p>Đu đủ được ứng dụng trong y học dân gian của nhiều nước. Ở Ấn Độ, quả chín còn chế siro và rượu vang có tác dụng long đờm, an thần và bổ; nhựa mủ từ quả xanh tác dụng trị giun sán, có khi dùng làm thuốc chữa nốt tàn nhang và các mụn ngoài da; lá dùng làm thuốc đắp trị đau thần kinh... Ở Indonesia và Nepal dùng lá đu đủ trị sốt rét hay các bệnh sốt khác, làm thuốc tẩy cho ngựa; nước sắc rễ trị ghẻ cóc, trĩ và viêm niệu đạo...</p>\r\n', 'I0BqQaUk_unnamed.jpg', 1, 0, '2020-12-27', 2, '2020-12-27 15:00:18', '2020-12-27 15:00:18'),
(4, 'Bạch truật bổ khí kiện tỳ, an thai', 'bach-truat-bo-khi-kien-ty-an-thai', '<p><strong>Bạch truật là rễ củ phơi hay sấy khô của cây Bạch truật (Atractylodes macrocephala Koidz.), thuộc họ Cúc (Asteraceae). Về thành phần hoạt chất, bạch truật có các hợp chất sterol, tinh dầu, sinh tố A... Tác dụng tăng sức bền, tăng khả năng thực bào, bảo vệ gan, lợi mật, chống loét, lợi niệu, chống u bướu, chống đông máu, làm giãn mạch, hạ huyết áp, giảm đường huyết.</strong></p>\r\n', '<p>Theo Đông y, bạch truật vị ngọt đắng, tính ôn; vào kinh tỳ và vị. Tác dụng bổ khí, kiện tỳ, táo thấp, lợi thủy, chỉ hãn, an thai. Trị chứng tỳ vị khí hư, thủy thũng, đàm ẩm, khí hư tự hãn và an thai. Liều dùng: 6 - 12g. Nếu dùng chữa táo thấp thì để sống, dùng để bổ tỳ vị thì phải sao tẩm.</p>\r\n\r\n<p><em>Lưu ý:</em>&nbsp;Trên thị trường có vị thuốc “bạch truật nam”, đó là thân rễ của cây thổ tam thất, hay bạch truật nam (Gynura pseudochina DC.), thuộc họ Cúc (Asteraceae). Rễ củ để nguyên gọi là thổ tam thất.</p>\r\n\r\n<h2><span style=\"font-size:17px\">Bạch truật được dùng làm thuốc trong các trường hợp</span></h2>\r\n\r\n<p><strong><em>Kiện tỳ, cầm tiêu chảy:</em></strong></p>\r\n\r\n<p><em>Bài 1:</em>&nbsp;<em>Thang lý trung:</em>&nbsp;đảng sâm 12g, sinh khương 8g, bạch truật 12g, cam thảo 4g. Sắc uống. Trị chứng tỳ hư thấp trệ, đại tiện lỏng, người mệt, kém ăn.</p>\r\n\r\n<p><em>Bài 2:</em>&nbsp;<em>Bột sâm truật:</em>&nbsp;đảng sâm 12g, bạch truật 12g, phục linh 12g, ý dĩ 12g, liên nhục 12g, nhục đậu khấu 12g, kha tử 12g, trần bì 12g, sơn tra 8g, thần khúc 8g, mộc hương 4g, sa nhân 4g, cam thảo 4g. Sắc uống ngày 1 thang hoặc tán bột. Trị các chứng tỳ hư thấp trệ, đại tiện lỏng, người mệt, ăn uống không tiêu, bụng đầy hơi.</p>\r\n\r\n<p><strong><em>Kiện vị tiêu thực</em></strong><em>&nbsp;(làm khoẻ dạ dày, dễ tiêu hoá):&nbsp;</em>dùng&nbsp;<em>Thang chỉ truật:&nbsp;</em>bạch truật (sao) 12g, chỉ thực 6g. Sắc uống ngày 1 thang hoặc tán bột làm viên hoàn. Mỗi lần uống 8g, ngày uống 2 - 3 lần, chiêu với nước cơm. Trị tỳ, vị đều hư nhược, tiêu hoá không tốt, không muốn ăn uống.</p>\r\n\r\n<p><img alt=\"Bạch truật -  vị thuốc hay trị tỳ vị hư nhược, ăn uống kém.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/20/bach_truat_resize.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Bạch truật -  vị thuốc hay trị tỳ vị hư nhược, ăn uống kém.\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/20/ty_vi_hu_an_uong_kem_resize.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Bạch truật -&nbsp; vị thuốc hay trị tỳ vị hư nhược, ăn uống kém.</em></p>\r\n\r\n<h2><span style=\"font-size:17px\"><em>Cố biểu, chỉ hãn:</em></span></h2>\r\n\r\n<p><em>Bài 1: Thuốc sắc bạch truật:&nbsp;</em>bạch truật 12g, hoàng kỳ 12g, phù tiểu mạch 20g. Sắc uống ngày 1 thang. Trị chứng tim hồi hộp, lo âu, tự ra mồ hôi.</p>\r\n\r\n<p><em>Bài 2: Thuốc bột bạch truật:&nbsp;</em>bạch truật 12g, phòng phong 12g, mẫu lệ 24g. Sắc uống ngày 1 thang hoặc tán thành bột, mỗi lần uống 12g, chiêu với nước đun sôi nguội. Trị chứng tỳ hư, tự ra mồ hôi, người mỏi mệt, hơi thở ngắn.</p>\r\n\r\n<p><strong><em>Lợi niệu tiêu thũng:</em></strong>&nbsp;dùng trong trường hợp tỳ hư, thuỷ thấp không chuyển hoá được gây phù nề.</p>\r\n\r\n<p><em>Bài 1: Thang linh quế truật cam:&nbsp;</em>phục linh 12g, quế chi 8g, bạch truật 8g, cam thảo 8g. Sắc uống ngày 1 thang. Trị các chứng tỳ hư, ho hen có đờm, tim đập nhanh, mắt mờ.</p>\r\n\r\n<p><em>Bài 2: Bột toàn sinh bạch truật:&nbsp;</em>bạch truật 12g, đại phúc bì 12g, gừng tươi 12g, ngũ gia bì 12g, địa cốt bì 12g, phục linh bì 20g. Sắc uống. Trị phù nề toàn thân, phụ nữ có thai bị phù.</p>\r\n\r\n<h2><span style=\"font-size:17px\"><em>Thuốc an thai</em></span></h2>\r\n\r\n<p><em>Bài 1: Đương quy tán:</em>&nbsp;bạch truật 32g, đương quy 64g, hoàng cầm 64g, bạch thược 64g, xuyên khung 64g. Các vị sấy khô, tán bột. Ngày uống 8 - 12g, uống với rượu loãng. Dùng cho phụ nữ có thai mà huyết kém, thai nhiệt không yên.</p>\r\n\r\n<p><em>Bài 2: Thái sơn bàn thạch thang:&nbsp;</em>nhân sâm 5g, đương quy 8g, hoàng cầm 5g, xuyên khung 4g, thục địa 10g, chích thảo 4g, hoàng kỳ 15g, tục đoạn 5g, bạch truật 10g, thược dược 6g, sa nhân 4g, nhu mễ 5g. Sắc uống ngày 1 thang. Công dụng: ích khí kiện tỳ, dưỡng huyết an thai.</p>\r\n\r\n<h2><span style=\"font-size:17px\">Món ăn thuốc có bạch truật</span></h2>\r\n\r\n<p><strong><em>Trị phụ nữ đau bụng đầy tức trướng hơi từng cơn:&nbsp;</em></strong><em>Cháo lòng lợn bạch truật:</em>&nbsp;bạch truật 40g, cau 1 quả, gừng nướng 40g, ruột lợn 1 đoạn, gạo tẻ 60g. Ruột lợn làm sạch, thái đoạn; các dược liệu thái lát, đập giập sắc lấy nước, bỏ bã. Gạo vo sạch nấu cháo với lòng lợn, khi cháo chín nhừ cho nước sắc thuốc vào, thêm gia vị đun sôi. Ăn khi đói.</p>\r\n\r\n<p><strong><em>Trị hội chứng lỵ mạn tính:&nbsp;</em></strong>dùng&nbsp;<em>Cao lỏng bạch truật:</em>&nbsp;bạch truật 300g sắc lấy nước, bỏ bã, cô đặc thành cao lỏng (tỷ lệ 1/1). Mỗi lần dùng 2 - 3 thìa, ngày uống 2 lần với nước sôi nguội có chút đường.</p>\r\n\r\n<p><strong><em>Trị tiêu chảy, đầy bụng chán ăn:&nbsp;</em></strong>món&nbsp;<em>Cháo bạch truật vỏ quất:</em>&nbsp;bạch truật 24g, vỏ quất 14g, gạo tẻ 100g. Bạch truật, vỏ quất sắc lấy nước bỏ bã. Gạo vo sạch nấu cháo, cháo chín cho nước sắc dược liệu vào, đun sôi, có thể thêm đường hoặc muối và gia vị. Ăn khi đói.</p>\r\n\r\n<p><strong><em>Dùng cho phụ nữ bị suy nhược, có thai dọa sẩy:</em></strong>&nbsp;món&nbsp;<em>Cháo nếp sâm kỳ truật táo:</em>&nbsp;bạch truật 12g, đảng sâm 12g, hoàng kỳ 30g, đại táo 14g, gạo nếp 50g. Sắc 4 vị thuốc lấy nước bỏ bã. Gạo vo sạch nấu cháo, cháo chín cho nước thuốc vào vào đun sôi lại trong vài phút. Ăn ngày 2 lần sáng, chiều.</p>\r\n\r\n<p><strong><em>Dùng cho trẻ em hay bị chảy bọt rãi:</em></strong>&nbsp;món&nbsp;<em>Nước hồ bạch truật:&nbsp;</em>sinh bạch truật 10g giã nhỏ, cho ít nước cơm, thêm nước vừa đủ, chưng nhỏ lửa trên bếp. Ngày ăn 3 lần.</p>\r\n\r\n<p><strong><em>Món ăn cho người cao tuổi, người suy nhược, trẻ nhỏ bị ăn kém, tiêu chảy mạn tính:</em></strong>&nbsp;món&nbsp;<em>Bánh khảo bạch truật:</em>&nbsp;sinh bạch truật 250g nghiền nhỏ, rang chín, đại táo 250g (đồ chín bỏ hạt), bột gạo (hoặc bột mì) 500g, thêm nước giã trộn thành 10 cái bánh, hấp chín. Ăn điểm tâm ngày 1 - 2 cái.</p>\r\n\r\n<p><strong>Kiêng kỵ:</strong>&nbsp;Người có chứng âm hư hoả vượng không dùng.</p>\r\n', 'lR3GANga_bach_truat_resize.jpg', 0, 0, '2020-12-27', 2, '2020-12-27 15:01:08', '2020-12-27 15:01:08'),
(5, 'Cá ba sa - món ăn bổ hư nhược, khu phong trừ thấp', 'ca-ba-sa-mon-an-bo-hu-nhuoc-khu-phong-tru-thap', '<div class=\"sapo_detail\" style=\"margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 700; font-stretch: inherit; font-size: 17px; line-height: 27px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; color: rgb(45, 45, 45);\">Cá ba sa là đặc sản của Việt Nam, giá thành rẻ mà giá trị xuất khẩu cao, được nuôi chủ yếu tại các tỉnh đồng bằng sông Cửu Long. Thịt cá mềm thơm, béo ngậy ngon hết nấc và rất giàu dinh dưỡng.</div>\r\n\r\n<div id=\"content_detail_news\" style=\"margin: 0px 0px 30px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline;\">\r\n<p><strong><em>Các món ăn từ cá ba sa được nhiều người ưa chuộng như cá ba sa chiên giòn, cá ba sa nấu măng chua, nướng muối ớt, kho tộ, cá ba sa hầm đu đủ, canh đậu phụ cá ba sa... Không chỉ thơm ngon, cá ba sa cũng là vị thuốc quý phòng trị nhiều bệnh.</em></strong></p>\r\n</div>\r\n', '<p>Cá ba sa còn gọi cá tra, tên khoa học:&nbsp; Pangasius hypophthalmus, thuộc họ (Pangasiadae). Cá ba sa giàu protein, ít lipid và carbohydrat, nhiều muối vô cơ: Ca, P, Fe; sinh tố B, PP... Trong 100g thành phẩm ăn được có 23,42g chất đạm, 3,42g chất béo, trong đó chất béo chưa bão hòa có 1,78g, 0,025mg cholesterol và 70,6mg natri, cung cấp 124,52 calo.</p>\r\n\r\n<p>Chất béo chưa bão hòa có ích trong bảo vệ màng tế bào, làm giảm cholesterol máu. Bên cạnh các acid béo hữu ích, còn có ADH (acid docohexanoic), AEP (acid écosapentaenoic) hay còn gọi là omega-3, giúp làm giảm lượng triglyceride cao trong máu, giảm nguy cơ xơ cứng động mạch, chống loạn nhịp, từ đó giảm các bệnh về tim mạch, giảm nguy cơ đột quỵ.</p>\r\n\r\n<p>Theo Đông y, cá ba sa vị ngọt, tính bình; vào phế, vị. Tác dụng hòa trung, khai vị, bổ hư nhược, nhuận da cơ, khu phong trừ thấp. Dùng rất tốt cho người bị hư lao, suy nhược, ăn kém chậm tiêu, sản phụ ít sữa, trẻ em thiểu dưỡng gầy còm.</p>\r\n\r\n<p>Mỗi ngày có thể dùng 200 - 500g cá, bằng cách nấu, kho, hầm, chiên rán. Sau đây là một số thực đơn chữa bệnh có cá ba sa:</p>\r\n\r\n<p><strong><em>Cá ba sa kho mặn:</em></strong>&nbsp;cá ba sa (bỏ vây, bỏ ruột) 500g-1.000g, nước dừa tươi 0,5 lít, tỏi&nbsp; 50g, ớt sừng 5 trái, hành 50g, nước mắm 3 thìa, gia vị vừa ăn. Cá cắt thành 4 - 5 khúc, để ráo nước, cho vào chảo chiên hai mặt hơi vàng, để ráo dầu, ướp nước mắm và tỏi ớt băm nhỏ. Cho vào nồi đất, tiếp tục cho nước, đun sôi 15 phút, tiếp cho thêm nước dừa, đun đến vừa cạn, rắc hành lá, ớt cắt khoanh lên là được. Ăn với cơm nóng rất ngon. Món này rất tốt cho người ăn uống kém, rối loạn tiêu hóa.</p>\r\n\r\n<p><img alt=\"Canh chua cá ba sa là món ăn rất tốt cho người tỳ vị hư nhược, ăn uống kém, rối loạn tiêu hóa.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/27/canh_chua_ca_ba_sa_resize.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Canh chua cá ba sa là món ăn rất tốt cho người tỳ vị hư nhược, ăn uống kém, rối loạn tiêu hóa.\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Canh chua cá ba sa là món ăn rất tốt cho người tỳ vị hư nhược, ăn uống kém, rối loạn tiêu hóa.</em></p>\r\n\r\n<p><strong><em>Cá ba sa hầm đu đủ:</em></strong>&nbsp;cá ba sa (bỏ vây, bỏ ruột) thái lát, đu đủ xanh 200g gọt lớp vỏ xanh ngoài và loại bỏ hạt, thái lát to. Thêm gia vị, nước liều lượng thích hợp, đun nhỏ lửa hầm nhừ, dùng tốt cho sản phụ sau đẻ ít sữa.</p>\r\n\r\n<p><strong><em>Canh chua cá ba sa:</em></strong>&nbsp;cá ba sa 500g (loại bỏ vây, ruột) thái lát to, thêm khế chua hoặc me, ớt, hành, gia vị, nấu canh chua. Món này rất tốt cho người tỳ vị hư nhược, ăn uống kém, rối loạn tiêu hóa.</p>\r\n\r\n<p><strong><em>Canh đậu phụ cá ba sa:</em></strong>&nbsp;cá ba sa 500g (loại bỏ vây, ruột) thái lát to, đậu phụ 2 - 3 bìa (khoảng 200g) thái lát, cho thêm mắm, tương, tiêu ớt, gia vị, liều lượng thích hợp. Nấu chín nhừ, thêm nước chanh hoặc nước me tương ớt. Món này thích hợp cho người tỳ vị hư nhược, ăn uống kém, rối loạn tiêu hóa.</p>\r\n\r\n<p><strong>Kiêng kỵ:&nbsp;</strong>Không dùng cá có biểu hiện ôi thiu. Không dùng gừng làm gia vị, sẽ tạo ra mùi vị khó chịu, khó ăn.</p>\r\n', 'gWbH9mnj_canh_chua_ca_ba_sa_resize.jpg', 1, 0, '2020-12-27', 3, '2020-12-27 15:02:06', '2020-12-27 15:02:06'),
(6, 'Dược thiện từ cá diếc', 'duoc-thien-tu-ca-diec', '<p><strong>Không cao lương mỹ vị, cũng không “sang chảnh” như các đặc sản thường thấy, tuy nhiên nếu khéo léo chế biến, các món ăn từ cá diếc lại vô cùng thơm ngon khó cưỡng như: cá diếc chiên giòn hoặc kho với thịt ba chỉ, kho tương, kho dưa... Cá diếc còn là vị thuốc Đông y phòng trị nhiều bệnh.</strong></p>\r\n', '<p>Cá diếc còn có tên tức ngư, phụ ngư. Tên khoa học: Carassin auratus L., họ cá chép (Cyprinidae). Cá diếc rất giàu dinh dưỡng: thịt cá chứa 17,6% protid, 1,8% lipid, 70mg% Ca, 152mg% P, 0,8mg% sắt và vitamin B1, acid nicotinic... Vì vậy, cá diếc là thực phẩm lý tưởng cho người bị bệnh lâu ngày, cơ thể suy nhược; khí huyết bất túc khiến ăn uống kém, ợ chua; tỳ hư phù nề, tiểu tiện khó.</p>\r\n\r\n<p>Theo Đông y, cá diếc vị ngọt, tính bình; vào tỳ, vị và đại tràng. Mật cá có vị đắng, tính lạnh. Cá diếc tác dụng kiện tỳ, hành thủy lợi thấp, khai vị, hạ khí, thông nhũ, thanh nhiệt giải độc. Dùng tốt cho người bị suy nhược, mỏi mệt ăn kém, tiêu chảy, kiết lỵ, phù, đại tiểu tiện xuất huyết. Ngày dùng 200-25g; bằng cách nấu, hầm, chiên, nướng. Xin giới thiệu một số thực đơn chữa bệnh có cá diếc:</p>\r\n\r\n<p><strong><em>Bột cá diếc:</em></strong><em>&nbsp;</em>cá diếc sấy khô 100g, gừng khô 60g, bán hạ chế 60g. Tất cả nghiền thành bột. Ngày uống 2 lần, mỗi lần 10g, uống với nước ấm. Chữa viêm phế quản mạn tính.</p>\r\n\r\n<p><img alt=\"Canh cá diếc củ cải rất tốt cho người bị đầy bụng, ăn kém, suy nhược cơ thể...\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/13/1450079879-canh-ca-cu-cai--7-_resize.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Canh cá diếc củ cải rất tốt cho người bị đầy bụng, ăn kém, suy nhược cơ thể...\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Canh cá diếc củ cải rất tốt cho người bị đầy bụng, ăn kém, suy nhược cơ thể...</em></p>\r\n\r\n<p><strong><em>Cá diếc nướng:</em></strong><em>&nbsp;</em>cá diếc 1 con khoảng 250g, làm sạch bỏ ruột, để nguyên vẩy, gỡ bỏ mang, cho một lượng phèn chua bằng hạt lạc đã đập vụn vào bụng cá, đem cá nướng chín. Ăn với dấm mắm gia vị. Dùng tốt cho người bị hội chứng lỵ, đại tiện nhiều lần trong ngày.</p>\r\n\r\n<p><strong><em>Cá diếc nướng tẩm trà:&nbsp;</em></strong>cá diếc 1 con, làm sạch bỏ ruột, để nguyên vẩy, gỡ bỏ mang. Cho lá chè non vào bụng cá, bọc giấy nướng chín. Ăn khi đói, ngày 1-2 lần, dùng trong vài ngày. Tốt cho người bệnh đái tháo đường, tiêu khát, uống nhiều nước.</p>\r\n\r\n<p><strong><em>Cá diếc hầm sa nhân cam thảo:&nbsp;</em></strong>cá diếc 1 con, sa nhân 8g, cam thảo 4g. Cá làm sạch bỏ ruột, để nguyên vảy, gỡ bỏ mang; sa nhân, cam thảo giã vụn cho vào bụng cá; cho vào nồi, đổ nước hầm nhừ. Không cho ớt, muối mắm, có thể cho các gia vị khác. Ăn liên tục đợt 3 tuần. Dùng tốt cho bệnh nhân phù thũng toàn thân.</p>\r\n\r\n<p><strong><em>Cá diếc hầm chân giò:</em></strong>&nbsp;cá diếc 200g, móng giò lợn 1 cái, thông thảo 10g. Cho tất cả vào nồi, thêm nước và gia vị, hầm nhừ, bỏ bã thông thảo. Món này rất tốt cho sản phụ sau đẻ ít sữa, tắc sữa.</p>\r\n\r\n<p><strong><em>Cá diếc hầm đậu đỏ:&nbsp;</em></strong>cá diếc 200g, xích tiểu đậu 100g. Cá diếc làm sạch bỏ ruột, để nguyên vảy, gỡ bỏ mang cho vào nồi cùng xích tiểu đậu, nước, hầm nhừ, thêm gia vị nhưng hạn chế muối. Món này rất tốt cho người bị phù nề tay chân (cước khí), phụ nữ có mang phù nề; tác dụng an thai.</p>\r\n\r\n<p><strong><em>Canh cá diếc sa nhân:</em></strong>&nbsp;cá diếc to 2 con, sa nhân 4g, trần bì 3g. Cá làm sạch bỏ ruột để cho ráo nước; sa nhân và trần bì tán bột, thêm lá lốt, ớt, gừng, hành, tỏi, bột tiêu, liều lượng thích hợp trộn đều cùng với muối cho trong bụng cá. Dùng dầu thực vật chiên cá cho chín vàng, gắp ra để ráo dầu. Cho gừng hành vào chảo đã rán cá, thêm nước dùng và gia vị, thả cá vào đun sôi đều. Ăn trong các bữa ăn. Dùng tốt cho người tỳ vị hư nhược, đầy trướng bụng, ăn kém hoặc đau quặn bụng, tiêu chảy.</p>\r\n\r\n<p><strong><em>Canh cá diếc củ cải:</em></strong>&nbsp;cá diếc 200g, củ cải 200-400g. Cá diếc mổ bỏ ruột, rửa sạch, cắt khúc; cải củ rửa sạch, thái khúc. Cho cá và củ cải vào nồi, thêm nước và gia vị, hầm nhừ (có thể cho thêm khế chua). Khi ăn thêm tương dấm hoặc vắt chanh. Ăn khi đói. Món này rất tốt cho người bị đầy bụng, lạnh bụng không tiêu, ăn kém, suy nhược cơ thể.</p>\r\n\r\n<p><strong><em>Canh cá diếc hoàng kỳ:</em></strong>&nbsp;cá diếc nấu với hoàng kỳ, khởi tử, rượu vang, gừng sống, hồ tiêu, giấm và đường. Món ăn bổ huyết, dưỡng da, làm cho da dẻ hồng hào, sắc mặt tươi tắn.</p>\r\n\r\n<p><strong>Kiêng kỵ:</strong>&nbsp;Người có urê huyết cao, hôn mê gan không ăn cá diếc.</p>\r\n', 'bmcTqyZ9_1450079879-canh-ca-cu-cai--7-_resize.jpg', 0, 0, '2020-12-27', 3, '2020-12-27 15:02:57', '2020-12-27 15:02:57'),
(7, 'Thịt trai tăng phong độ cho chàng', 'thit-trai-tang-phong-do-cho-chang', '<p><strong>Thịt trai không chỉ là món ăn bổ dưỡng, rất tốt cho sức khỏe, đồng thời còn là vị thuốc quý được Đông y dùng từ lâu đời. Thịt trai còn là vị thuốc bổ thận rất hiệu nghiệm. Đặc biệt những người bị tăng huyết áp, nam giới bị u xơ tuyến tiền liệt hoặc yếu sinh lý ăn cháo trai một thời gian sẽ thấy có tác dụng cải thiện rõ rệt.</strong></p>\r\n', '<p>Trong 100g thịt trai có&nbsp; 89,9g nước, 4,6g protid, 1,1g lipid, 2,5g gluxid, 1,9g tro, 668mg canxi, 107mg photpho, 1,5mg sắt, 0,46mg vitamin B2, 3,1mg vitamin PP... cung cấp 39Kcal. Ngoài ra, trong thịt trai còn có nhiều loại vitamin, chất khoáng khác và giàu chất kẽm.</p>\r\n\r\n<p>Để chữa yếu sinh lý, nên chế biến món ăn từ trai như sau: Chọn con trai to, lấy thịt trai băm nhỏ với mộc nhĩ, giò sống, hành củ, gia vị vừa đủ. Viên thành viên nhỏ cho vào vỏ trai, nướng trên bếp than. Ăn nóng với nước mắm, chanh, ớt.</p>\r\n\r\n<p>Món ăn dân dã phổ biến nhất từ thịt trai chính là món cháo trai, cách chế biến cũng rất đơn giản, chỉ cần khéo léo một chút là sẽ có một món ăn ngon - bổ - rẻ cho phái mạnh.Trước tiên cần ngâm rửa sạch con trai, luộc qua để lấy nước nấu cháo và tách lấy phần thịt, bỏ phần vỏ. Lấy nước luộc trai cho gạo vào và hầm thật nhừ thành cháo. Thịt con trai thái nhỏ cho vào hầm nhừ cùng cháo, sau đó nêm gia vị, rau thơm và ăn khi còn nóng. Đối với nam giới trung niên hoặc cao tuổi bị yếu sinh lý, nấu cháo trai ăn thường xuyên sẽ thấy tác dụng tốt.</p>\r\n\r\n<p>Canh trai rau hẹ: Đây là món ăn từ thịt trai chữa yếu sinh lý, cải thiện chuyện chăn gối cực kỳ tốt cho phái mạnh. Khi thịt trai kết hợp với rau hẹ - một loại rau có tác dụng cực kỳ tốt cho sinh lý nam giới sẽ tạo thành món ăn bổ thận, tráng dương, tăng cường sinh lực mà các chàng không thể bỏ qua. Chuẩn bị: 150 thịt trai sông, 120g rau hẹ, các gia vị cần thiết. Chế biến: Rửa sạch con trai và luộc qua lấy nước và thịt trai. Thịt trai thái miếng nhỏ và xào với gia vị, dầu ăn cho săn lại. Rau hẹ rửa sạch, thái nhỏ hoặc thái khúc. Đun nước luộc trai cho sôi rồi thả thịt trai và rau hẹ vào đun thêm vài phút cho chín rồi nêm gia vị và thưởng thức.</p>\r\n', 'oqY2A5AP_thit-trai-tang-phong-do-cho-chang1609050642.jpg', 1, 0, '2020-12-27', 4, '2020-12-27 15:06:26', '2020-12-27 15:06:26'),
(8, 'Tránh xa thực phẩm gây dậy thì sớm', 'tranh-xa-thuc-pham-gay-day-thi-som', '<p><strong>Dậy thì là giai đoạn phát triển sinh lý quan trọng của cơ thể, thường kéo dài 3-5 năm. Căn cứ vào những biểu hiện dậy thì bất thường ở trẻ trai trước 10 tuổi và trước 9 tuổi ở trẻ gái được coi là sớm. Một trong những nguyên nhân cơ bản của tình trạng này là do loại thực phẩm mà trẻ được sử dụng hàng ngày. Những thực phẩm làm tăng nguy cơ trẻ dậy thì sớm cần tránh gồm:</strong></p>\r\n', '<p><em>Thịt cổ gia cầm:</em>&nbsp;Trong thành phần của thịt ở vùng cổ gia cầm như gà, ngan, ngỗng...&nbsp; thường có chứa nhiều thuốc tăng trọng. Khi trẻ ăn nhiều thịt cổ, đồng nghĩa trẻ sẽ bị kích thích dậy thì sớm.</p>\r\n\r\n<p><em>Rau củ trái mùa:&nbsp;</em>Đều chứa các chất độc hại tồn dư từ việc trồng rau quả trái mùa và sử dụng để ép trái cây phải chín, việc này sẽ tạo ra nguy cơ dậy thì sớm khi các em ăn loại rau củ này.</p>\r\n\r\n<p><em>Thực phẩm chiên, rán:</em>&nbsp;Khi nấu ở nhiệt độ cao, các món ăn như gà rán, khoai tây chiên... sẽ bị biến đổi chất, khi trẻ ăn vào có thể gây rối loạn nội tiết và dẫn đến cơ thể dậy thì sớm hơn.</p>\r\n\r\n<p><em>Đồ ăn nhiều muối:&nbsp;</em>Nếu bạn cho con mình ăn nhiều thực phẩm chứa muối trong một thời gian dài sẽ khiến hệ tiêu hóa, thận của trẻ gặp nguy. Bởi những món ăn chứa hàm lượng muối cao sẽ kích hoạt hormon có liên quan tới sinh sản đó là neurokinin B, dẫn đến cơ thể dậy thì sớm.</p>\r\n\r\n<p><em>Nội tạng động vật:</em>&nbsp;Các món ăn từ nội tạng động vật sẽ khiến trẻ dễ bị béo phì, tăng cân, đồng thời kích hoạt dậy thì sớm.</p>\r\n\r\n<p><em>Sữa đậu nành:&nbsp;</em>Các sản phẩm từ đậu nành có chứa isoflavone giống estrogen, có thể dẫn đến dậy thì sớm ở em gái.</p>\r\n\r\n<p><em>Đồ ăn sẵn:</em>&nbsp;Có chứa chất bảo quản, chất tạo màu, tạo mùi. Các chất này giống như hormon giới tính, do vậy không tốt cho cơ thể các em đang tuổi lớn.</p>\r\n\r\n<p><em>Thuốc bổ:&nbsp;</em>Nhiều bà mẹ tự mua vitamin tổng hợp cho bé uống, hoặc dùng thuốc Bắc chế biến món ăn như canh gà thuốc Bắc, gà tần... cho trẻ ăn, sẽ làm cơ thể thay đổi quá trình bài tiết, gây dậy thì sớm. Vì trong hầu hết trong các thuốc bổ này có chứa hormon tăng trưởng rất mạnh, gây dậy thì sớm.</p>\r\n', 'dBI1Uqoh_tranh-xa-thuc-pham-gay-day-thi-som1607848479.jpg', 0, 0, '2020-12-27', 4, '2020-12-27 15:07:28', '2020-12-27 15:07:28'),
(9, 'HPV cũng gây ung thư ở nam giới', 'hpv-cu-?ng-gay-ung-thu-o-nam-gio-i', '<p><strong>Đa số đàn ông bị ảnh hưởng bởi HPV không biểu hiện bất kỳ triệu chứng nào, nhưng biến chứng của nó lại không nhỏ. Tương tự như ở phụ nữ, HPV cũng là nguyên nhân gây ung thư ở đàn ông.</strong></p>\r\n', '<p style=\"text-align:justify\">Human Papillomavirus là tên khoa học của nhiều chủng virus gây u nhú ở người, có tên viết tắt là HPV. Virus này gây ra những sang thương dạng u nhú trên cơ thể người, đặc biệt là các dạng sùi mào gà.</p>\r\n\r\n<p style=\"text-align:justify\">Đến nay theo thống kê của các nhà khoa học, có tới hơn 200 chủng HPV, trong đó phân loại thành các nguy cơ cao gây ung thư và nguy cơ thấp gây u nhú thông thường. Điển hình nhất trong các chủng HPV là một số chủng gây sùi mào gà ở cả 2 giới. Trong các bệnh lây truyền qua đường tình dục, nguyên nhân do HPV khá phổ biến.</p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Nguyên nhân</span></h2>\r\n\r\n<p style=\"text-align:justify\">HPV truyền qua tiếp xúc trực tiếp và thân mật, bao gồm cả quan hệ tình dục, vì thế cả nam và nữ đều có nguy cơ lây nhiễm như nhau. Virus có thể lây lan từ người này sang người khác qua quan hệ tình dục (bằng miệng, hậu môn hoặc âm đạo) hoặc tiếp xúc da thịt. Nếu ai đó bị nhiễm virus, họ có thể truyền virus, ngay cả khi chưa bao giờ có triệu chứng rõ ràng.</p>\r\n\r\n<p style=\"text-align:justify\">Nguy cơ nhiễm HPV tăng lên do một số yếu tố, bao gồm: lịch sử tình dục nhiều bạn tình, tuổi trẻ, người cắt bao quy đầu, da bị tổn thương, xây xước.</p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Triệu chứng nhiễm HPV ở nam giới</span></h2>\r\n\r\n<p style=\"text-align:justify\">Đa số đàn ông bị ảnh hưởng bởi HPV không biểu hiện bất kỳ triệu chứng nào. Một số người phát triển sang thương dạng u nhú, mụn cóc trên dương vật, tinh hoàn, hậu môn, háng và đùi, lưỡi và sau họng. Kích thước có thể nhỏ hoặc lớn, phẳng hoặc nhô cao trên da hình hoa mào gà, hoa súp lơ. Đơn độc hoặc thành nhóm ở khu vực xung quanh hậu môn hoặc bộ phận sinh dục... Những mụn cóc này thường không đau.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"HPV\" src=\"https://media.suckhoedoisong.vn/Images/nguyenkhanh/2020/12/17/HPV.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"HPV cũng gây ung thư ở nam giới\" /><em>Hai chủng HPV 16-18 có mối liên hệ với ung thư.</em></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Biến chứng nặng nề</strong></p>\r\n\r\n<p style=\"text-align:justify\">HPV không phải là ung thư, nhưng nhiễm trùng có thể gây ra những thay đổi trong cơ thể có thể dẫn đến ung thư. Nhưng tiến triển này rất chậm, trong nhiều năm sau khi một người bị nhiễm virus.</p>\r\n\r\n<p style=\"text-align:justify\">Tại Hoa Kỳ, hơn 42.000 ca ung thư liên quan đến HPV mỗi năm, trong đó khoảng 18.300 trường hợp xảy ra ở nam giới. Ở nam giới, HPV được liên kết với 90% các trường hợp ung thư hậu môn, hơn 60% ca ung thư dương vật và khoảng 70% ca ung thư vòm họng. Nguy cơ ung thư do HPV ở những người đàn ông có quan hệ tình dục đồng giới cao gấp 17 lần so với người có quan hệ dị giới.</p>\r\n\r\n<p style=\"text-align:justify\">Các triệu chứng của ung thư hậu môn liên quan đến HPV có thể bao gồm: chảy máu, chảy mủ, đau hoặc ngứa hậu môn. Sưng hạch bạch huyết ở vùng hậu môn hoặc háng. Thay đổi thói quen đại tiện hoặc hình dạng của phân.</p>\r\n\r\n<p style=\"text-align:justify\">Ung thư dương vật có thể dẫn đến: Thay đổi mô trên dương vật, chẳng hạn như thay đổi màu sắc, dày da. Xuất hiện vết loét đau hoặc không đau có thể chảy máu.</p>\r\n\r\n<p style=\"text-align:justify\">Ung thư phía sau thành họng do HPV có thể gây: đau họng hoặc đau tai liên tục, ho dai dẳng, khó thở hoặc khó nuốt; giảm cân, thay đổi giọng nói hoặc khàn giọng, sưng ở cổ.</p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Điều trị không dễ</span></h2>\r\n\r\n<p style=\"text-align:justify\">Điều trị mụn cóc do HPV có thể hiệu quả bằng chấm các dung dịch ngoài da, đốt điện, laser... hoặc can thiệp phẫu thuật, tùy thuộc vào kích thước, vị trí và hình dạng của chúng.</p>\r\n\r\n<p style=\"text-align:justify\">Tuy nhiên, loại bỏ mụn cóc không ngăn ngừa được lây nhiễm sang bạn tình. HPV tồn tại và phát triển trong tế bào, ẩn sâu dưới các lớp trung bì da, nó thúc đẩy quá trình tăng sinh mạnh mẽ khiến da người bệnh bị đùn sùi lên thành mụn cóc. Chính vì thế chỉ sử dụng các biện pháp đốt sùi thông thường không diệt virus tận gốc, khi quá trình nó nhân bản là trong tế bào.</p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Phòng ngừa thế nào?</span></h2>\r\n\r\n<p style=\"text-align:justify\">Tiêm vắc-xin và sử dụng bao cao su đúng cách trong quan hệ tình dục có thể làm giảm nguy cơ mắc HPV. Tuy nhiên, bao cao su không thể bảo vệ 100% lây nhiễm HPV, vì HPV có thể lây nhiễm vào một số khu vực mà bao cao su không bao phủ. Vì thế cách tốt nhất vẫn là quan hệ chung thủy với một bạn tình.</p>\r\n\r\n<p style=\"text-align:justify\">Theo nghiên cứu, hiện đã có những loại vắc-xin ngăn ngừa hiệu quả 2 chủng HPV 16 và 18. Hai chủng này có mối liên hệ cao nhất với bệnh ung thư. Các nghiên cứu đã báo cáo không có tác dụng phụ nghiêm trọng khi tiêm vắc-xin này.</p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Cần làm gì nếu phải sống chung với HPV?</span></h2>\r\n\r\n<p style=\"text-align:justify\">Ước tính khoảng 90% các trường hợp nhiễm HPV thường tự khỏi trong vòng 2 năm mà không cần điều trị và không gây ra bất kỳ tác hại nào. Tuy nhiên, việc điều trị virus rất khó và việc phát triển khả năng miễn dịch có thể mất vài tháng hoặc thậm chí nhiều năm. Do đó, nếu nhận thấy mình bị mụn cóc sinh dục, nên tránh tiếp xúc tình dục cho đến khi được điều trị hiệu quả. Cũng chưa rõ thời gian có thể lây lan sau khi điều trị là bao lâu nên nếu có quan hệ tình dục cần sử dụng bao cao su.</p>\r\n', 'ohpWVjRx_hpv-cung-gay-ung-thu-o-nam-gioi1608214730.jpg', 1, 0, '2020-12-27', 5, '2020-12-27 15:08:34', '2020-12-27 15:08:34');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `NgayDang`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(11, 'Phân biệt u vú lành tính và ung thư vú', 'phan-biet-u-vu-lanh-tinh-va-ung-thu-vu', '<div class=\"sapo_detail\" style=\"margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 700; font-stretch: inherit; font-size: 17px; line-height: 27px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; color: rgb(45, 45, 45);\">Ung thư vú đứng hàng đầu trong các bệnh ung thư thường gặp ở phụ nữ. Không phải khối u nào ở vú cũng là ung thư. Việc phân biệt bệnh ung thư vú và u vú lành tính khá khó khăn đối với những người không có kiến thức về y khoa do 2 bệnh này có nhiều triệu chứng tương tự nhau.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div id=\"content_detail_news\" style=\"margin: 0px 0px 30px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline;\">&nbsp;</div>\r\n', '<p>Ở cơ thể con người, các tế bào có sự phân chia để tái tạo mã di truyền DNA, giúp các tế bào mới phát triển và thay thế. Sự phân chia và thay thế này tuân theo chu kỳ nhưng có thể bị gián đoạn do những thay đổi trong DNA của tế bào, do độc tố hay do tiếp xúc với nội tiết tố bất thường. Khi đó, các tế bào có thể sản xuất ra một khối u lành tính hoặc ác tính.</p>\r\n\r\n<p>Các tế bào lành tính gần giống với các tế bào bình thường về nguồn gốc. Khi chúng sinh sôi, nảy nở sẽ tạo thành những nốt u hoặc khối u. Các khối u này hầu hết có xu hướng tái tạo chậm và không tạo ra những thay đổi chức năng, không có khả năng xâm lấn.</p>\r\n\r\n<p>Tế bào ác tính có thể hình thành các chức năng trao đổi chất, hình dạng bất thường và phát triển không theo trật tự nào do xuất hiện từ các bất thường trong chuỗi DNA. Sự bất thường đó có thể phát hiện và ngăn ngừa bằng chụp CT, chụp PET, xét nghiệm máu...</p>\r\n\r\n<p>U vú lành tính xuất hiện có thể do các lý do như nhiễm trùng, chấn thương, tăng trưởng không phải ung thư. Cụ thể:</p>\r\n\r\n<p><em>Nhiễm trùng:</em>&nbsp;Trường hợp này có thể xảy ra khi núm vú bị thương hoặc nứt do cho con bú, khiến vi khuẩn có thể xâm nhập và gây nhiễm trùng.</p>\r\n\r\n<p><em>Chấn thương:</em>&nbsp;Khi vú bị chấn thương do va chạm, các mạch máu nhỏ có thể vỡ ra, gây tụ máu và cảm giác giống như một khối u. Việc chấn thương ở vú cũng có thể làm tổn thương đến các tế bào mỡ trong mô vú và gây hoại tử.</p>\r\n\r\n<p><em>Tăng trưởng không phải ung thư gây ra khối u ở vú:</em>&nbsp;Trường hợp này thường xảy ra ở phụ nữ độ tuổi từ 30 - 35, các bướu sợi tuyến lành tình rất rắn xuất hiện trong ngực, thường không gây đau. Một số trường hợp, bướu sợi tuyến phát triển xuất hiện ở cả tuổi thiếu niên.</p>\r\n\r\n<p><em>Ung thư vú (khối u ác tính)</em>&nbsp;được hình thành bởi nhiều nguyên nhân như tiền sử gia đình từng có người mắc bệnh ung thư vú hoặc ung thư buồng trứng, tiền căn thai sản, do uống thuốc hormon, do thừa cân, do hút thuốc - uống rượu, do yếu tố nội tiết...</p>\r\n\r\n<p><img alt=\"Ung thư vú ở giai đoạn sớm có thể chữa khỏi gần như  100%.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/11/15/Untitl1ed.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Ung thư vú ở giai đoạn sớm có thể chữa khỏi gần như  100%.\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Ung thư vú ở giai đoạn sớm có thể chữa khỏi gần như&nbsp; 100%.</em></p>\r\n\r\n<h2><span style=\"font-size:17px\">Cách phân biệt</span></h2>\r\n\r\n<p><em>Các dấu hiệu của u vú lành tính tùy thuộc vào nguyên nhân gây ra:&nbsp;</em>Có triệu chứng ngứa da, núm vú nhạy cảm, đau và khó chịu ở vú, cục u bất thường, khi chạm vào khối u có thể di chuyển dễ dàng.</p>\r\n\r\n<p><em>Các triệu chứng của ung thư vú gồm:&nbsp;</em>Núm vú tiết dịch, hình dạng vú thay đổi, da vú sần vỏ cam, mẩn đỏ, khối u cứng chắc, hình dạng không đều, bám chặt vào da...</p>\r\n\r\n<p>U vú lành tính thường mở rộng chậm hơn, không xâm lấn các mô xung quanh và có nhiều khả năng ổn định theo thời gian. Khối u lành tính tuy không nguy hiểm nhưng khi phát triển đến một kích thước lớn thì có thể chèn ép và gây ảnh hưởng đối với các mô lân cận.</p>\r\n\r\n<p>Khối u lành tính sinh trưởng trên bề mặt da và thường có màng ngăn cách với các tế bào khác nên có thể dễ phát hiện qua việc sờ nắn. Khi thấy khối u xuất hiện kèm cảm giác đau thì người bệnh cũng không nên quá lo lắng vì thường các khối u gây triệu chứng đau là khối u lành tính.</p>\r\n\r\n<p>Ung thư vú thì lại khác, có đặc điểm là phát triển nhanh, xâm lấn và mở rộng sang các mô xung quanh, trừ khi bị tiêu diệt. Đặc biệt, nó thường lây lan qua máu hoặc mạch bạch huyết đến các phần ở xa của cơ thể, phát triển các mô ác tính mới tại các hạch bạch huyết, xương, phổi, gan...</p>\r\n\r\n<p>Đặc tính của ung thư vú là xâm lấn sâu vào các tế bào khác, hình khối không rõ ràng và không đau. Bệnh nhân chỉ có thể sờ thấy khối u nếu chúng nằm ngay dưới da, trường hợp nằm sâu vào xương sườn thì rất khó phát hiện nhưng chúng có thể gây hiện tượng đau lưng, đau vai gáy. Sau một thời gian, khi phát triển lớn, chúng sẽ làm biến dạng khối vú, tiết dịch từ núm, thay đổi màu da...</p>\r\n', 'EQUZaA6B_phan-biet-u-vu-lanh-tinh-va-ung-thu-vu1605443502.jpg', 0, 0, '2020-12-27', 5, '2020-12-27 15:10:06', '2020-12-27 15:10:06'),
(12, 'Tự ti - Hội chứng tâm lý tuổi teen', 'tu-ti-hoi-chung-tam-ly-tuoi-teen', '<p><strong>Lứa tuổi teen có biến đổi mạnh mẽ về tâm sinh lý, do vậy dễ rơi vào một số trạng thái tâm lý không ổn định. Tự ti là trạng thái không tin vào khả năng bản thân, khiến teen mất đi sự bình tĩnh - sự điều tiết hết sức cần thiết đối với tâm lý tuổi mới lớn. Dần dần, nó khiến teen trở nên e dè, ngại tiếp xúc, chỉ thích sống một mình, xa rời gia đình, bạn bè.</strong></p>\r\n', '<p>Đáng ngại hơn, tự ti sẽ khiến bạn trẻ dễ rơi vào trạng thái stress, thường xuyên thấy mệt mỏi, da xanh xao, mất ngủ, thừa cân... Hơn nữa, tự ti, ngại giao tiếp, sống thu mình trong tập thể sẽ đẩy teen dễ mắc trầm cảm, hoang tưởng và lệch lạc giới tính tuổi dậy thì (do sự thiếu hiểu biết, tâm lý không vững vàng nên dễ bị lôi kéo).</p>\r\n\r\n<p>Chính vì nghi ngờ khả năng của bản thân, bạn trẻ thường lấy ưu điểm của người khác so sánh với mình, rồi sinh ra chán nản, không phát huy được sở trường vốn có. Chỉ một sự thất bại nho nhỏ đã khiến các bạn mất đi sự cân bằng tâm lý, nhạy cảm với lời nói của người khác, cho rằng họ cười nhạo mình, dần dần né tránh bằng cách không muốn giao tiếp với người khác hoặc gặp chuyện gì khó là co mình lại, thoái thác tham gia.</p>\r\n\r\n<p>Khi tự ti, các teen thường hạ thấp mình, coi nhẹ bản thân. Làm như vậy không phải khiêm tốn mà là tự đánh mất khả năng bản thân, tạo cho người khác ấn tượng bạn là con người yếu đuối và kém năng lực.</p>\r\n\r\n<p>Các bạn trẻ hãy cố gắng vượt qua trạng thái tự ti. Mỗi người đều có những ưu và khuyết điểm nhất định. Điều quan trọng là phải biết phát huy ưu điểm, đồng thời tìm ra khuyết điểm của mình để chấp nhận và khắc phục nó. Bạn không nhất thiết phải nổi bật ở mọi lĩnh vực, chỉ cần nổi trội hơn người khác ở mặt nào đó là có thể che giấu điểm yếu, khiến mọi người có thiện cảm với mình rồi.</p>\r\n\r\n<p>Không nên so sánh mình với người khác. Càng so sánh, bạn sẽ càng tự ti hơn mà thôi. Hãy tự hào về bản thân. Đừng bao giờ ngần ngại đối mặt với khó khăn, thử thách. Có thể bạn sẽ thất bại nhưng cái chúng mình nhận được sẽ là kinh nghiệm.</p>\r\n\r\n<p>Tích cực học tập, trau dồi kiến thức là liều thuốc rất quan trọng tiếp thêm sự tự tin cho bạn. Chịu khó đọc sách, nghiên cứu tài liệu, các bạn sẽ hiểu biết và thành công hơn trong cuộc sống.</p>\r\n', 'xl6zx8Wd_tu-ti-hoi-chung-tam-ly-tuoi-teen1609050767.png', 1, 0, '2020-12-27', 6, '2020-12-27 15:10:53', '2020-12-27 15:10:53'),
(13, 'Sa sinh dục, bệnh khó nói...', 'sa-sinh-duc-benh-kho-noi-', '<p><strong>Sa sinh dục là tình trạng bàng quang, tử cung hoặc trực tràng bị sa xuống trong âm đạo hay nặng hơn là cổ tử cung sa ra khỏi âm hộ. Chứng sa sinh dục không nguy hiểm nhưng ảnh hưởng nhiều đến sinh hoạt và lao động của phụ nữ, đặc biệt là gặp rắc rối trong quan hệ vợ chồng.</strong></p>\r\n', '<p>Sa sinh dục thường được gọi là sa dạ con hay sa tử cung. Nhưng trên thực tế không chỉ dạ con mà cả bàng quang và trực tràng sa vào trong âm đạo. Trong trường hợp nặng, các tạng trên có thể sa ra ngoài âm hộ.</p>\r\n\r\n<p>Tử cung là một bộ phận nằm sâu trong ổ bụng. Nó được giữ tại chỗ bằng lớp cơ và các tổ chức vùng đáy chậu (dàn của phần dưới khung xương chậu), bởi các thành âm đạo và các dây chằng trong bụng và chậu hông. Nếu vì một lý do nào đó, các bộ phận cố định hay giữ tử cung bị giãn, nhão ra và một khi áp lực trong ổ bụng tăng (như khi thở, rặn, ho) và sức nặng của tử cung sẽ đẩy, kéo nó tụt dần xuống thấp gây nên sa sinh dục với các mức độ nặng nhẹ khác nhau.</p>\r\n\r\n<p><img alt=\"Hình ảnh sa tử cung.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/20/hinh-anh-sa-tu-cung-do-2-o-phu-nu-bi-sa-tu-cung-sau-sinh-hinh-anh-sa-tu-cung-do-2-3-1583814680-193-width600height400.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Hình ảnh sa tử cung.\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Hình ảnh sa tử cung.</em></p>\r\n\r\n<p>Chứng sa sinh dục phần lớn gặp ở phụ nữ trong độ tuổi từ 40-60 tuổi. Những phụ nữ sinh đẻ nhiều, đẻ quá sớm, không được đỡ đẻ an toàn và đúng kỹ thuật đều dễ bị sa sinh dục. Những người đi làm quá sớm sau sinh cũng có nguy cơ bị sa sinh dục cao.</p>\r\n\r\n<p>Những phụ nữ lao động nặng nhọc, vất vả cũng là đối tượng dễ bị sa sinh dục, nhất là những nữ công nhân, nông dân, lao động tay chân cả ngày phải làm việc ở tư thế đứng, gánh gồng, đội vác nặng vì áp lực ổ bụng lên đáy chậu luôn cao.</p>\r\n\r\n<p>Sa sinh dục cũng có thể gặp cả ở phụ nữ chưa sinh đẻ do thể trạng yếu, dây chằng mỏng, tử cung ở tư thế trung gian nên khi có áp lực mạnh trong ổ bụng sẽ đẩy tử cung sa dần xuống. Còn ở những người đã sinh đẻ nhiều lần, các dây chằng yếu, tầng sinh môn rách hay giãn mỏng, dưới sự tăng áp lực lên ổ bụng, thành âm đạo bị sa và kéo tử cung sa theo.</p>\r\n\r\n<p>Tùy theo từng người, tùy mức độ sa sinh dục ít hay nhiều, mới sa hay sa từ lâu, sa đơn thuần hay còn có tổn thương phối hợp, có kèm theo sa bàng quang hay trực tràng mà xuất hiện những dấu hiệu thường gặp sau đây: khó chịu, cảm giác tức nặng vùng cửa mình, ở bụng dưới, nhất là khi đứng, nhưng khi nằm thì cảm giác khó chịu đó lại không còn. Đôi khi có cảm giác như muốn rặn đẻ vì các tĩnh mạch ở vùng đáy chậu bị sa sung huyết, đồng thời do áp lực trong thành bụng dồn xuống vùng đáy chậu đã bị suy yếu, hay bị đau vùng thắt lưng.</p>\r\n\r\n<p>Khối sa lồi ở vùng âm hộ, tầng sinh môn, ban đầu kích thước khối sa nhỏ, không thường xuyên xuất hiện, chỉ xuất hiện khi lao động nặng hoặc đi lại nhiều, nằm nghỉ thì khối sa tụt vào trong âm đạo hoặc tự đẩy lên được, càng về sau khối sa càng to, sa thường xuyên và không tự đẩy lên được nữa. Khi đó sẽ xuất hiện triệu chứng tức nặng bụng dưới, cảm giác vướng víu khó chịu vùng âm hộ - tầng sinh môn ảnh hưởng đến lao động và sinh hoạt hàng ngày.</p>\r\n\r\n<p>Nếu kèm theo sa bàng quang thì có dấu hiệu đi tiểu khó, tiểu dắt, són tiểu khi cười to, khi ho mạnh hay khi bị rùng mình, thường đái không hết nước tiểu nên bàng quang dễ bị viêm gây tiểu buốt. Trong trường hợp sa bàng quang nhiều thì lúc đầu đi tiểu rất khó khăn, phải dùng ngón tay đẩy bàng quang lên trên và vào trong mới đi tiểu được. Đôi khi bệnh nhân phải đến viện vì bí tiểu cấp.</p>\r\n\r\n<p>Nếu kèm theo sa trực tràng thì đại tiện sẽ có cảm giác vẫn chưa hết phân ở trực tràng, có thể bị táo bón. Nhiều người bị sa sinh dục nhưng kinh nguyệt vẫn bình thường và vẫn có khả năng có thai. Nhưng những phụ nữ này thường dễ sẩy thai và đẻ non. Chảy máu, khí hư ra nhiều do cổ tử cung viêm nhiễm, cọ xát khiến người bệnh đi lại khó khăn, hạn chế lao động...</p>\r\n\r\n<p>Khi thấy có biểu hiện sa sinh dục, chị em nên đi khám để được bác sĩ tư vấn, điều trị phù hợp.</p>\r\n', 'ufmKaUBM_sa-sinh-duc-benh-kho-noi1608404938.jpg', 0, 0, '2020-12-27', 6, '2020-12-27 15:11:56', '2020-12-27 15:11:56'),
(14, 'Ngộ độc khí than sưởi ấm - SOS', 'ngo-doc-khi-than-suoi-am-sos', '<p><strong>Thời gian qua, Trung tâm Chống độc, BV Bạch Mai thường xuyên tiếp nhận nhiều trường hợp ngộ độc khí CO. Hầu hết bệnh nhân đều nhập viện trong tình trạng nặng.</strong></p>\r\n', '<p style=\"text-align:justify\">Hiện tượng ngộ độc này xảy ra chủ yếu do người dân đốt các nhiên liệu ở trong phòng kín bằng các cách khác nhau. Ví dụ dùng bếp than tổ ong, than củi, than hoa... để sưởi ấm, đun nấu trong phòng kín.</p>\r\n\r\n<p style=\"text-align:justify\">TS.BS. Nguyễn Trung Nguyên, Giám đốc Trung tâm Chống độc cho biết: Ngay trong tuần vừa qua, Trung tâm Chống độc mới tiếp nhận 1 bệnh nhân ngộ độc khí CO rất nặng, có tổn thương não. Theo TS.BS. Nguyễn Trung Nguyên, khí CO không màu, không mùi nên rất khó nhận biết.</p>\r\n\r\n<p style=\"text-align:justify\">Nếu đốt than ở phòng càng kín thì nguy cơ ngộ độc khí CO càng tăng, quá trình nhiễm độc khí xảy ra rất nhanh. Khí CO là một khí rất độc, tất cả người ở trong môi trường trong phòng có nồng độ cao như thế đều có thể bị tổn thương não và tử vong nhanh chóng. Để tránh ngộ độc khí CO, các bác sĩ khuyến cáo: Người dân tuyệt đối không đốt than, củi để sưởi ấm trong phòng kín... bởi các cách làm này có thể gây nguy hiểm đến tính mạng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Ngộ độc khí than\" src=\"https://media.suckhoedoisong.vn/Images/nguyenkhanh/2020/12/10/cap_cuu_ngo_doc.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Ngộ độc khí than\" /><em>Cấp cứu cho bệnh nhân ngộ độc khí than tại BVĐK tỉnh Hà Tĩnh.</em></p>\r\n', 'qxvVAmAT_ngo-doc-khi-than-suoi-am-sos1607610171.jpg', 1, 0, '2020-12-27', 7, '2020-12-27 15:13:04', '2020-12-27 15:13:04'),
(15, 'Bệnh nhân đái tháo đường suýt nhiễm trùng huyết do dùng thuốc trôi nổi', 'benh-nhan-dai-thao-duong-suyt-nhiem-trung-huyet-do-dung-thuoc-troi-noi', '<div class=\"sapo_detail\" style=\"margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 700; font-stretch: inherit; font-size: 17px; line-height: 27px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; color: rgb(45, 45, 45);\">Nhiễm trùng là một biến chứng thường gặp, gây tình trạng nhiễm khuẩn từ nhẹ tới nặng ở bệnh nhân mắc đái tháo đường (ĐTĐ). Việc điều trị nhiễm trùng cho người bệnh ĐTĐ cũng phức tạp hơn so với người bình thường. Do đó, nếu bệnh không được phát hiện sớm, điều trị đúng... có nguy cơ diễn biến nặng ảnh hưởng tới sức khỏe, tính mạng người bệnh, như trường hợp trong bài viết này.</div>\r\n\r\n<div id=\"content_detail_news\" style=\"margin: 0px 0px 30px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline;\">\r\n<h2 style=\"font-style:inherit; text-align:justify\"><span style=\"font-size:17px\">Dùng thuốc trôi nổi, bệnh nặng hơn và chi phí điều trị tốn kém</span></h2>\r\n</div>\r\n', '<p style=\"text-align:justify\">Bệnh viện Nội tiết TW mới đây tiếp nhận ca bệnh Đ.T.Đ (54 tuổi, Gia Lâm - Hà Nội), trong tình trạng loét thành bụng do đắp thuốc lá, nguy cơ nhiễm trùng huyết. Bệnh nhân mắc ĐTĐ đã 12 năm.</p>\r\n\r\n<p style=\"text-align:justify\">Trước khi nhập viện vài tuần, bệnh nhân bị ngứa vùng bụng bên phải, gãi thấy nhói buốt.</p>\r\n\r\n<p style=\"text-align:justify\">Sau đó, tại chỗ đó xuất hiện nốt mụn nhỏ, kích thước bằng hạt đỗ xanh. Nốt mụn đó nhanh chóng phát triển to bằng hạt ngô, rồi hạt nhãn.</p>\r\n\r\n<p style=\"text-align:justify\">Lo lắng với nốt mụn đó, bà được nghe lời giới thiệu đã lặn lội tìm gặp thầy lang ở Bắc Giang.</p>\r\n\r\n<p style=\"text-align:justify\">Tại đây, bà Đ. được thầy lang chẩn đoán tắc tĩnh mạch. Thầy lang chỉ định cho bà Đ. là: rửa tổn thương bằng betadine và bôi thuốc bột, xoa bóp thuốc nước (không rõ cả 2 loại là thuốc gì).</p>\r\n\r\n<p style=\"text-align:justify\">Cứ thế, bà Đ. tuân thủ theo lời thầy lang. Sau vài ngày, nốt mụn vỡ ra và có vẻ khô trên bề mặt, nhưng bà lại thấy vùng bụng cứng lên, cơn đau nhức nhối ngày càng tăng. Đến khi đau đớn không chịu nổi, bà Đ. mới đến Bệnh viện Nội tiết TW để khám.</p>\r\n\r\n<p style=\"text-align:justify\">Bệnh nhân nhập viện trong tình trạng ổ ápxe thành bụng đã vỡ tạo thành vết loét lớn, kích thước 8cm×8cm, đe dọa nhiễm trùng huyết, có thể ảnh hưởng tính mạng.</p>\r\n\r\n<p style=\"text-align:justify\">Các bác sĩ Khoa Điều trị tích cực đã điều trị tích cực cho bệnh nhân trong 2 tuần, thực hiện ổn định đường huyết, chích rạch, tháo mủ ổ ápxe,&nbsp; loại bỏ hoại tử và kết hợp kháng sinh liều cao. Đến nay, tình trạng sức khỏe của bệnh nhân đã ổn định.</p>\r\n\r\n<p style=\"text-align:justify\">Bác sĩ điều trị cho biết, quá trình này không những gây tổn thất cho bệnh nhân về phương diện tinh thần, gây ra nhiều đau đớn, lo lắng mà còn tốn kém (chi phí cho ca điều trị lên tới khoảng 50 triệu đồng). Trong khi đó, nếu ngay từ đầu, bệnh nhân đến khám ở cơ sở y tế chuyên khoa về nội tiết thì sẽ được điều trị sớm và đúng cách, nốt mủ nhỏ sẽ nhanh chóng lành bệnh.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Bệnh nhân đái tháo đường suýt nhiễm trùng huyết do dùng thuốc trôi nổi\" src=\"https://media.suckhoedoisong.vn/Images/nguyenkhanh/2020/11/26/anh_2.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Bệnh nhân đái tháo đường suýt nhiễm trùng huyết do dùng thuốc trôi nổi\" /><em>Từ nốt mụn nhỏ, sau vài ngày đắp thuốc không rõ nguồn gốc đã biến chứng thành vùng hoại tử lớn ở thành bụng.</em></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Nhiễm trùng ở bệnh nhân ĐTĐ thường diễn biến phức tạp</span></h2>\r\n\r\n<p style=\"text-align:justify\">Trong những năm qua, tại Bệnh viện Nội tiết TW cũng như các khoa nội tiết của các BVĐK trên toàn quốc, hằng năm vẫn phải cấp cứu, điều trị nhiều ca bệnh biến chứng nhiễm trùng rất phức tạp.</p>\r\n\r\n<p style=\"text-align:justify\">Các bác sĩ cho biết: Khi nồng độ đường trong máu cao, giúp cho vi khuẩn phát triển thuận lợi, chính vì vậy người mắc ĐTĐ rất dễ bị nhiễm trùng và diễn biến kéo dài hơn. Thậm chí, chỉ với các vết trầy xước nhỏ, cũng dễ bị vi khuẩn tấn công.</p>\r\n\r\n<p style=\"text-align:justify\">BS.Tôn Thất Kha, Trưởng khoa Điều trị tích cực - Bệnh viện Nội tiết TW cảnh báo: “Bệnh cảnh nhiễm trùng ở bệnh nhân ĐTĐ rất phức tạp và nguy hiểm mặc dù ban đầu trông có vẻ nhẹ. Vì thế, bệnh nhân ĐTĐ khi có bất kỳ nhiễm trùng nào dù nhẹ đều nên đến khám ở chuyên khoa nội tiết của các bệnh viện, giảm thiểu chi phí điều trị và tránh những biến chứng đáng tiếc”.</p>\r\n\r\n<p style=\"text-align:justify\">Các bác sĩ cũng khuyến cáo, khi bệnh nhân ĐTĐ mà mắc kèm theo bệnh khác nữa, thì điều cần ghi nhớ là phải mang theo các xét nghiệm mới nhất, đơn thuốc đang dùng và thông báo cho bác sĩ biết về tình trạng bệnh mình đang mắc phải, để bác sĩ cân nhắc khi kê đơn, tránh tương tác bất lợi cho người bệnh.</p>\r\n\r\n<p style=\"text-align:justify\">Trong quá trình điều trị, nếu xảy ra bất thường (xuất hiện thêm các triệu chứng mới hoặc không kiểm soát được đường huyết...), cần thông báo ngay cho bác sĩ biết để có phương pháp xử lý thích hợp, kịp thời. Đặc biệt là bệnh nhân không nên nghe theo lời&nbsp; giới thiệu “ở đâu đó có thầy lang giỏi, bài thuốc hay”... mà tìm đến điều trị, lấy thuốc, sẽ nguy hiểm khi bệnh diễn tiến nặng hơn.</p>\r\n', '2YQmI8Ij_benh-nhan-dai-thao-duong-suyt-nhiem-trung-huyet-do-dung-thuoc-troi-noi1606405037.jpg', 0, 0, '2020-12-27', 7, '2020-12-27 15:13:51', '2020-12-27 15:13:51'),
(16, 'Dậy thì sớm gây ảnh hưởng gì?', 'day-thi-som-gay-anh-huong-gi-', '<p><strong>Con gái tôi học lớp 3 đã có hiện tượng dậy thì. Tôi rất lo và chưa biết rõ dậy thì sớm gây những ảnh hưởng gì. Mong bác sĩ tư vấn kỹ hơn về vấn đề này.</strong></p>\r\n', '<p>Dậy thì sớm ở trẻ là tình trạng dậy thì khởi phát trước 8 tuổi ở bé gái và 10 tuổi ở bé trai. Trẻ dậy thì sớm thường có dấu hiệu tăng trưởng rất nhanh, chiều cao phát triển từ 7 - 15 cm/năm. Ở trẻ gái có tuyến vú to bất thường, có thể to một bên, âm đạo tăng tiết nhầy. Kinh nguyệt có trước 8 tuổi. Lông mu và lông nách xuất hiện trước đó hoặc sau khi tuyến vú to ra. Đối với trẻ trai giọng trầm, trứng cá, cơ bắp vạm vỡ, mọc lông mu, mọc ria mép, thể tích tinh hoàn tăng trên 4ml, dương vật to, dài, sẫm màu.</p>\r\n\r\n<p>Đối với những trẻ dậy thì sớm nếu không được phát hiện và điều trị kịp thời sẽ để lại hậu quả nghiêm trọng. Đến tuổi trưởng thành chắc chắn trẻ sẽ thấp hơn so với chiều cao tiêu chuẩn vì hormon sinh dục kích thích sự phát triển của xương làm các đầu xương đóng sớm khiến trẻ không thể phát triển chiều cao. Ngoài ra, dậy thì sớm còn khiến trẻ bị khủng hoảng cảm xúc, khủng hoảng tâm lý. Trẻ lúc nào cũng hoang mang, lo lắng và tự ti về thân hình của mình. Hơn nữa, trẻ dậy thì sớm thường tò mò, bắt chước chuyện người lớn dẫn đến hậu quả mang thai ngoài ý muốn.</p>\r\n\r\n<p>Khi thấy trẻ dậy thì sớm, nên đưa con đi khám để bác sĩ tư vấn cụ thể, không để ảnh hưởng quá nhiều đến sự phát triển của cơ thể.</p>\r\n', 'IlcJyuoI_day-thi-som-gay-anh-huong-gi1609049970.jpg', 1, 0, '2020-12-27', 8, '2020-12-27 15:16:58', '2020-12-27 15:16:58'),
(17, 'Tập thể dục sớm khi trời lạnh, nên không?', 'tap-the-duc-som-khi-troi-lanh-nen-khong-', '<div class=\"sapo_detail\" style=\"margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 700; font-stretch: inherit; font-size: 17px; line-height: 27px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; color: rgb(45, 45, 45);\">Thời tiết đã chuyển lạnh nhưng bố tôi vẫn giữ thói quen dậy sớm tập thể dục và tắm vào buổi sáng. Gia đình con cái khuyên ngăn thế nào cũng không được. Mong quý báo cho biết, người cao tuổi có nên duy trì thói quen tập thể dục buổi sáng sớm khi trời lạnh không, những nguy cơ nào có thể xảy ra?</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div id=\"content_detail_news\" style=\"margin: 0px 0px 30px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline;\">&nbsp;</div>\r\n', '<p>Tập thể dục, thể thao buổi sáng là một thói quen tốt để tăng cường sức khỏe và cũng là thú vui tuổi già đối với nhiều người. Tuy nhiên, đối với người cao tuổi, không nên tập quá sớm khi nhiệt độ ngoài trời xuống thấp do cơ thể người già đã suy yếu không kịp thích ứng, dễ tạo điều kiện cho những bệnh nguy hiểm phát tác như tai biến mạch máu não, tăng huyết áp, hen, dị ứng, viêm phế quản... Tuy nhiên, người cao tuổi cũng không nhất thiết phải bỏ hẳn thói quen tập thể dục buổi sáng mà chỉ cần lùi thời gian lại, chờ khi có ánh sáng mặt trời, khí hậu ấm áp hơn, sương lạnh cũng tan bớt. Khi đi tập, nên mặc đủ ấm, đội mũ để tránh gió, khi đã vận động thấy người nóng lên mới có thể cởi bớt trang phục, tuyệt đối không nên mặc phong phanh ngay khi đi bởi rất dễ nhiễm lạnh. Nếu bố bạn có tiền sử tăng huyết áp, bạn cần nhắc nhở ông uống thuốc đầy đủ theo đơn chỉ định của bác sĩ bởi số đo huyết áp của người cao tuổi thường thay đổi vào lúc sáng sớm, nhất là khi nhiệt độ xuống thấp.</p>\r\n', 'NuXC2xsj_tap-the-duc-som-khi-troi-lanh-nen-khong1608624777.jpg', 0, 0, '2020-12-27', 8, '2020-12-27 15:17:34', '2020-12-27 15:17:34'),
(18, 'Các bệnh ngoài da thường gặp ở người cao tuổi', 'cac-benh-ngoai-da-thuong-gap-o-nguoi-cao-tuoi', '<p><strong>Do tuổi tác, sức đề kháng của người cao tuổi bị suy giảm một cách đáng kể, da trở nên nhăn nheo, giảm tính đàn hồi, khô hơn, dẫn đến da của người cao tuổi rất dễ mắc bệnh.</strong></p>\r\n', '<p>Cùng với đó là sự thay đổi màu sắc của da, mạch máu nhỏ bị vỡ gây xuất huyết trên da, mạch máu tắc nghẽn làm thiếu máu nuôi dưỡng, ngứa, các loại u bướu... Vì thế người cao tuổi rất dễ gặp các bệnh như khô da, ngứa, dày sừng da, sừng hóa da, zona...</p>\r\n\r\n<h2><span style=\"font-size:17px\">Ngứa da</span></h2>\r\n\r\n<p>Ngứa da ở tuổi già không gây tổn hại đến sức khỏe nhưng mang lại rất nhiều phiền toái và khó chịu. Ngứa da, gãi nhiều làm da bị trầy xước và có chỗ dày lên như các vết sừng. Nguyên nhân ngứa da chủ yếu là do tuổi đã cao, sức khỏe suy yếu, các chức năng cơ thể bị thoái hóa, các hormon sinh dục bị mất cân bằng dẫn đến khô da. Bệnh nặng lên bởi thời tiết lạnh, hanh khô, tắm quá nhiều hoặc tắm nước quá nóng, dùng xà bông hay chất tẩy rửa mạnh...</p>\r\n\r\n<p>Để hạn chế ngứa, người cao tuổi chỉ nên tắm nước đủ ấm (không tắm nước nóng quá) và hạn chế dùng xà phòng. Có thể dùng một số loại kem dưỡng ẩm giúp giữ ẩm cho da. Mặc quần áo mỏng và rộng, tránh bó sát vào người dễ gây cọ xát và kích ứng da. Đồ ăn cần thanh đạm, không nên ăn quá mặn, quá nhiều dầu mỡ, hạn chế các loại thức ăn cay và có chứa các chất mang tính kích thích. Ít uống các loại đồ uống như rượu, chè đặc hay cà phê. Ăn nhiều rau củ và hoa quả.</p>\r\n\r\n<p>Lưu ý, triệu chứng ngứa ở người cao tuổi cũng có khi không đơn thuần do ngứa tuổi già mà có thể là biểu hiệu của nhiều loại bệnh khác nhau. Vì vậy, trong trường hợp này, để điều trị triệt để triệu chứng ngứa, người cao tuổi cần đi khám tại chuyên khoa da liễu.</p>\r\n\r\n<p><img alt=\"Ngứa da có thể là biểu hiện của nhiều bệnh.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/16/Mt_s_bnh_ngoai_da_thng_gp__ngi_cao_tui_resize.png\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Ngứa da có thể là biểu hiện của nhiều bệnh.\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Ngứa da có thể là biểu hiện của nhiều bệnh.</em></p>\r\n\r\n<h2><span style=\"font-size:17px\">Dày sừng da (đồi mồi)</span></h2>\r\n\r\n<p>Dày sừng da là bệnh hay gặp ở người sau 50 tuổi, do sự lão hóa của da. Dày sừng da thường xuất hiện ở mặt, cổ, cánh tay và xuất hiện nhiều hơn ở người tiếp xúc với ánh nắng mặt trời. Ban đầu, các đốm này màu nâu nhạt, sau đó ngày càng trở nên đậm màu hơn với kích thước to nhỏ không đều.</p>\r\n\r\n<p>Để làm chậm sự lão hóa của da, có thể tăng cường sự tuần hoàn bằng massage da mặt, xoa bóp cánh tay hàng ngày hoặc dùng kem dưỡng da. Hạn chế để da tiếp xúc trực tiếp nhiều với ánh nắng mặt trời, khiến các đốm này ngả dần sang màu nâu sậm.</p>\r\n\r\n<h2><span style=\"font-size:17px\">Bệnh Zona</span></h2>\r\n\r\n<p>Bệnh Zona (hay còn gọi là bệnh giời leo). Là một bệnh do virus Zoster gây ra nhưng nếu thể nặng thì gây đau nhiều, kéo dài nhiều ngày, thậm chí nhiều tháng. Bệnh thường chỉ xảy ra một bên của cơ thể do gây tổn thương các rễ thần kinh. Người bệnh sốt, đau, rát, ngứa ở vùng da bị Zona, sau một vài ngày, tại vùng da bệnh xuất hiện một mảng dát màu hồng đỏ, có các mụn nước mọc lên từng chùm sát vào nhau hoặc thành mảng. Khi nghi ngờ bị bệnh Zona, người cao tuổi cần đi khám bệnh càng sớm càng tốt nhằm làm giảm thời gian trị bệnh (thuốc sẽ làm hạn chế sự phát triển của virus, qua đó hạn chế sự tấn công của chúng vào thần kinh) và đặc biệt làm giảm các cơn đau cho người bệnh. Tùy theo vị trí và mức độ của bệnh, bác sĩ sẽ có chỉ định điều trị thích hợp. Cần vệ sinh da vùng bị bệnh sạch sẽ để tránh bội nhiễm.</p>\r\n\r\n<p><img alt=\"Dày sừng da xuất hiện ở người ngoài 50 tuổi.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/16/Mt_s_bnh_ngoai_da_thng_gp__ngi_cao_tui2_resize.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Dày sừng da xuất hiện ở người ngoài 50 tuổi.\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Dày sừng da xuất hiện ở người ngoài 50 tuổi.</em></p>\r\n\r\n<h2><span style=\"font-size:17px\">Lời khuyên của thầy thuốc</span></h2>\r\n\r\n<p>Ðể làm giảm mắc bệnh ngoài da ở người cao tuổi, trước hết cần nâng cao sức đề kháng cho cơ thể. Bởi vì trong cuộc sống hàng ngày khó có một chế độ dinh dưỡng nào dành riêng cho da của người cao tuổi, trong khi đó, nâng cao sức đề kháng của cơ thể người cao tuổi thì có ảnh hưởng rất tốt đến mọi cơ quan, trong đó có tổ chức da.</p>\r\n\r\n<p>Ăn uống đủ chất và đủ số lượng trong từng bữa ăn là hết sức quan trọng. Trong các bữa ăn, nên hạn chế ăn thịt mà tăng cường ăn cá, rau và quả. Cá, rau và quả có rất nhiều chất dinh dưỡng cần thiết cho mọi cơ quan trong cơ thể vì chúng cung cấp các loại vi chất chống lão hóa và đồng thời trong rau có nhiều chất xơ là loại góp phần chống táo bón ở người cao tuổi. Tốt nhất mỗi ngày nên ăn 0,5kg hoa quả tươi hoặc rau xanh.</p>\r\n\r\n<p>Nơi người cao tuổi nằm ngủ hàng ngày cũng cần thoáng, mát, tránh ẩm, ướt đề phòng các loại vi nấm phát triển, nhất là vi nấm gây bệnh ngoài da. Nên thường xuyên rèn luyện cơ thể bằng các bài thể dục hoặc đi bộ.</p>\r\n', '2gPjQZkj_cac-benh-ngoai-da-thuong-gap-o-nguoi-cao-tuoi1608086413.jpg', 1, 0, '2020-12-27', 9, '2020-12-27 15:18:31', '2020-12-27 15:18:31'),
(19, 'Cấp cứu đột quỵ: Sự sống tính trong từng phút giây', 'cap-cuu-dot-quy-su-song-tinh-trong-tung-phut-giay', '<p><strong>Đột quỵ có thể xảy ra ở bất cứ thời điểm nào, dù là đang làm việc gắng sức hay nghỉ ngơi. Bệnh nhân đột quỵ cần được cấp cứu càng sớm càng tốt; để bảo đảm “thời gian vàng”, nhằm giảm thiểu những nguy hiểm cận kề có thể xảy ra.</strong></p>\r\n', '<p style=\"text-align:justify\">Đột quỵ hay còn được gọi là tai biến mạch máu não. Đây là tình trạng não bộ bị tổn thương nghiêm trọng do mạch máu não bị tắc nghẽn, bị vỡ, gây gián đoạn việc vận chuyển máu để nuôi dưỡng não bộ. Khiến các tế bào thiếu hụt dinh dưỡng, oxy; các tế bào sẽ chết đi sau vài phút nếu vấn đề thiếu hụt này không được khắc phục.</p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Những cảnh báo đáng lưu tâm</span></h2>\r\n\r\n<p style=\"text-align:justify\">Tùy theo từng nguyên nhân, đột quỵ là do tắc nghẽn mạch máu não hay do vỡ mạch máu não, được phân loại thành nhồi máu não hay xuất huyết não.</p>\r\n\r\n<p style=\"text-align:justify\"><em>Nhồi máu não:&nbsp;</em>Còn gọi là thiếu máu não cục bộ, được xem là tình trạng giảm lưu lượng máu cung cấp đến cơ quan não một cách đột ngột, khiến cho động mạch não bị thuyên tắc một phần hoặc toàn phần. Tình trạng nhồi máu não chiếm đại đa số những ca lâm sàng đột quỵ thường gặp, với tỉ lệ khoảng 80%.</p>\r\n\r\n<p style=\"text-align:justify\"><em>Đột quỵ xuất huyết não:&nbsp;</em>Có tên gọi khác là xuất huyết nội sọ, là một trường hợp khác của tai biến mạch máu não, xảy ra tình trạng máu động mạch tràn vào nhu mô não đột ngột, gây tổn thương não cấp tính. Mặc dù tỷ lệ của xuất huyết não thấp hơn nhồi máu não rất nhiều, chỉ chiếm khoảng 20%; nhưng tình trạng bệnh lý này có mức độ nguy hiểm cao hơn, gây biến chứng nặng nề và tỷ lệ tử vong cao.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"đột quỵ\" src=\"https://media.suckhoedoisong.vn/Images/nguyenkhanh/2020/11/18/Cap_cuu_dot_quy_1.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Cấp cứu đột quỵ: Sự sống tính trong từng phút giây\" /></p>\r\n\r\n<p style=\"text-align:justify\">Mỗi vùng trên não có một chức năng, nhiệm vụ khác nhau, tùy theo vùng tổn thương, mà gây ra những triệu chứng khác nhau cho người bệnh. Nếu tổn thương vùng vận động có thể gây ra yếu liệt tay chân, liệt nửa người. Vùng cảm giác sẽ gây ra mất cảm giác, cảm giác tê bì; vùng ngôn ngữ sẽ không nói được hoặc không hiểu được ngôn ngữ…</p>\r\n\r\n<div class=\"style-box box-right\" style=\"margin: auto auto 15px; padding: 20px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; background: rgb(255, 232, 232); width: 330px; border-radius: 5px; float: right;\">Tổ chức Y tế thế giới ghi nhận, đột quỵ là nguyên nhân gây tử vong đứng hàng thứ 2 trên thế giới, với con số khoảng 6,5 triệu người tử vong mỗi năm; tức trung bình mỗi 6 giây có một ca tử vong do đột quỵ. Đột quỵ còn là nguyên nhân gây tàn phế hàng đầu trên thế giới, có hơn 17 triệu ca bệnh mỗi năm. Trung bình 6 người có một người bị đột quỵ, nếu như không có biện pháp phòng tránh kịp thời.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Tùy theo mức độ tổn thương, cũng như số lượng các khu vực tổn thương khác nhau mà có một hoặc nhiều triệu chứng khác nhau. Trong đó, 3 triệu chứng thường thấy nhất là nói ngọng, méo cười, tay chân buông xuôi. Khi người bệnh có các dấu hiệu này, cần nghĩ ngay đến đột quỵ. Cần nhanh chóng đưa bệnh nhân đi cấp cứu, để không bỏ lỡ “thời gian vàng” trong điều trị.</p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Hậu quả của đột quỵ</span></h2>\r\n\r\n<p style=\"text-align:justify\">Tổ chức Y tế thế giới ghi nhận, đột quỵ là nguyên nhân gây tử vong đứng hàng thứ 2 trên thế giới, với con số khoảng 6,5 triệu người tử vong mỗi năm; tức trung bình mỗi 6 giây có một ca tử vong do đột quỵ. Đột quỵ còn là nguyên nhân gây tàn phế hàng đầu trên thế giới, có hơn 17 triệu ca bệnh mỗi năm. Trung bình 6 người có một người bị đột quỵ, nếu như không có biện pháp phòng tránh kịp thời.</p>\r\n\r\n<p style=\"text-align:justify\">Tại Việt Nam, theo thông tin từ Bộ Y tế, mỗi năm nước ta có khoảng 200.000 trường hợp đột quỵ, là nguyên nhân gây tử vong hàng đầu, có tỷ lệ từ 10-20%, cao hơn nhiều lần so với một số nguyên nhân tử vong phổ biến khác. Trong các trường hợp bệnh nhân sống sót sau đột quỵ thì khả năng tàn phế, lệ thuộc cao. 10-13% bệnh nhân tàn phế, nằm liệt giường; 12% hồi phục một phần; 25% bệnh nhân có thể độc lập đi lại.</p>\r\n\r\n<p style=\"text-align:justify\">Đột quỵ gây nhiều hậu quả nặng nề về chi phí điều trị. Sau đột quỵ, bệnh nhân thường khó hòa nhập trở lại với cuộc sống bình thường, gây nhiều ảnh hưởng về kinh tế, chi phí thuốc, điều trị, hồi phục… Với người nhà bệnh nhân, đột quỵ tạo gánh nặng kinh tế trực tiếp, thông qua việc mất sức lao động. Ảnh hưởng kinh tế gián tiếp thông qua việc chăm sóc, điều trị cũng như những áp lực về mặt tinh thần.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"đột quỵ\" src=\"https://media.suckhoedoisong.vn/Images/nguyenkhanh/2020/11/18/Cap_cuu_dot_quy_2.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Cấp cứu đột quỵ: Sự sống tính trong từng phút giây\" /></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Nguyên nhân gây đột quỵ</span></h2>\r\n\r\n<p style=\"text-align:justify\"><em>Đột quỵ thiếu máu cục bộ:&nbsp;</em>Có 3 nguyên nhân chính làm tắc nghẽn mạch máu não, thiếu máu não: bệnh ở tim hình thành cục máu đông, theo dòng máu lên não khiến tắc mạch máu não; xơ vữa động mạch; tăng huyết áp làm hư hại các mạch máu nhỏ.</p>\r\n\r\n<p style=\"text-align:justify\"><em>Đột quỵ xuất huyết não:&nbsp;</em>Có nhiều nguyên nhân khác nhau gây ra, trong đó tăng huyết áp là nguyên nhân hàng đầu. Các nguyên nhân khác bao gồm: Dị dạng mạch máu não, thoái hóa mạch máu, khối u hay bất thường về đông máu.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"đột quỵ\" src=\"https://media.suckhoedoisong.vn/Images/nguyenkhanh/2020/11/18/Cap_cuu_dot_quy_3.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Cấp cứu đột quỵ: Sự sống tính trong từng phút giây\" /></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Cấp cứu đột quỵ như thế nào?</span></h2>\r\n\r\n<p style=\"text-align:justify\">Khi phát hiện người có các dấu hiệu đột quỵ, cần xử trí đúng cách:</p>\r\n\r\n<p style=\"text-align:justify\">- Đỡ người bệnh để không bị té ngã gây chấn thương.</p>\r\n\r\n<p style=\"text-align:justify\">- Để người bệnh nằm xuống chỗ thoáng, nghiêng qua một bên nếu nôn ói. Móc hết đàm, nhớt để bệnh nhân dễ thở.</p>\r\n\r\n<p style=\"text-align:justify\">- Gọi xe đưa người bệnh đến cơ sở y tế gần nhất.</p>\r\n\r\n<p style=\"text-align:justify\">- Không tự ý cho uống hoặc nhỏ thuốc hạ huyết áp hay bất kỳ loại thuốc nào khác.</p>\r\n\r\n<p style=\"text-align:justify\">- Đưa người bệnh vào bệnh viện càng nhanh càng tốt, để có thể cứu sống kịp thời các phần não chưa chết nhưng đang bị thiếu máu nuôi, đang bị sặc, bị chèn ép.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<div class=\"style-box\" style=\"margin: auto auto 15px; padding: 20px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; background: rgb(255, 232, 232); width: 660px; border-radius: 5px;\"><strong>MỘT SỐ YẾU TỐ LÀM GIA TĂNG NGUY CƠ MẮC PHẢI ĐỘT QUỴ:</strong><br />\r\n- Người lớn tuổi: Trên 50 tuổi có nguy cơ tăng cao.<br />\r\n- Giới: Tỷ lệ xảy ra ở nam giới nhiều hơn nữ giới.<br />\r\n- Tăng huyết áp. Đái tháo đường (tiểu đường). Xơ vữa động mạch, tăng mỡ (cholesterol) trong máu, nhất là loại LDL. Bệnh tim. Hút thuốc lá, nghiện rượu. Béo phì, ít vận động...<br />\r\n- Người có tiền sử đột quỵ.</div>\r\n', 'rAHKL35g_cap-cuu-dot-quy-su-song-tinh-trong-tung-phut-giay1605686295.jpg', 0, 0, '2020-12-27', 9, '2020-12-27 15:19:31', '2020-12-27 15:19:31'),
(20, 'Mắt bị ngứa, đỏ vì sao?', 'mat-bi-ngua-do-vi-sao-', '<div class=\"sapo_detail\" style=\"margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 700; font-stretch: inherit; font-size: 17px; line-height: 27px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; color: rgb(45, 45, 45);\">Tình trạng mắt bị ngứa đỏ (không phải do đau mắt) khá thường gặp và ảnh hưởng không nhỏ tới sức khỏe và sinh hoạt của mỗi người. Việc tìm ra nguyên nhân của tình trạng này giúp người bệnh phòng ngừa hiệu quả.</div>\r\n\r\n<div id=\"content_detail_news\" style=\"margin: 0px 0px 30px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline;\">\r\n<p><strong>Dị ứng theo mùa</strong></p>\r\n</div>\r\n', '<p>Chất gây dị ứng như phấn hoa và cỏ có thể gây sưng và viêm mắt, có thể dẫn đến mẩn đỏ. Dị ứng cũng có xu hướng làm cho đôi mắt bị ngứa, khiến bạn dụi mắt tạo ra viêm nhiều hơn và mẩn đỏ.</p>\r\n\r\n<h2><span style=\"font-size:17px\">Viêm bờ mi</span></h2>\r\n\r\n<p>Viêm bờ mi gây ngứa, đỏ, chảy nước mắt, bề mặt nhãn cầu sưng, khô. Viêm bờ mi gây nhiều khó chịu cho bệnh nhân, việc điều trị có khi rất dai dẳng vì xác định nguyên nhân thường khó có thể chính xác hoàn toàn hoặc là do nhiều nguyên nhân cùng gây nên. Có 3 kiểu viêm bờ mi mắt như sau:</p>\r\n\r\n<p><em>Viêm bờ mi mắt do tụ cầu:&nbsp;</em>Đây là loại viêm bờ mi mắt hay gặp nhất, nhiễm Staphylococcus aureus ở mi mắt gây viêm mi, kết mạc và giác mạc. Gặp ở nữ giới (80%) và những người trẻ. Bệnh nhân thấy cảm giác nóng, ngứa và rát da, đặc biệt vào buổi sáng, hai mi mắt dính vào nhau.</p>\r\n\r\n<p><em>Viêm ở phần trước mi:</em>&nbsp;Viêm bờ mi vùng góc mắt với đặc điểm đỏ, ướt, nứt nẻ và đóng vảy ở góc ngoài, góc trong hoặc cả hai góc mắt (toét mắt) thường kèm viêm kết mạc nhú gai, đôi khi có tiết tố nhày mủ và tiết tố dính. Có loét và xuất huyết bờ mi.</p>\r\n\r\n<p><em>Viêm mạn tính điển hình:</em>&nbsp;có những vảy cứng, giòn ở gốc lông mi, bằng mắt thường đôi khi chỉ thấy những vảy trắng. Khám bờ mi bằng sinh hiển vi thấy vảy cứng bao quanh mỗi lông mi.</p>\r\n\r\n<p><img alt=\"Khô mắt là 1 nguyên nhân gây ngứa mắt.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/27/kho_mt_la_1_nguyen_nhan_gay_nga_mt_resize.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Khô mắt là 1 nguyên nhân gây ngứa mắt.\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Khô mắt là 1 nguyên nhân gây ngứa mắt.</em></p>\r\n\r\n<h2><span style=\"font-size:17px\">Khô mắt</span></h2>\r\n\r\n<p>Khô mắt là tình trạng lớp phim nước mắt bị tổn thương, do lượng nước mắt tiết ra không đủ hoặc do tốc độ bốc hơi nhanh, gây tổn thương bề mặt nhãn cầu và gây cảm giác khó chịu trong mắt. Phim nước mắt vừa có vai trò làm ẩm bề mặt giác mạc, kết mạc và bôi trơn mi mắt, đồng thời cung cấp dinh dưỡng cho biểu mô, giúp bề mặt giác mạc luôn trơn láng... Do đó có tác dụng làm mắt trong suốt đồng thời đẩy những chất lạ bám vào mắt trôi đi.</p>\r\n\r\n<p>Khi bị khô mắt, bạn sẽ cảm giác khó chịu, khô, ngứa, rát bỏng, có dị vật trong mắt, nhìn mờ, nhạy cảm với ánh sáng, mắt đỏ, tăng tiết nhầy, chảy nước mắt... Khi mắt bạn có những triệu chứng trên, nên đi khám để được bác sĩ chuyên khoa mắt chẩn đoán và xác định chính xác tình trạng khô mắt.</p>\r\n\r\n<p>Giải pháp: Sử dụng nước mắt nhân tạo và tránh thuốc nhỏ mắt để giúp mắt được bôi trơn và giảm ngứa. Ngoài ra, uống sữa, bổ sung dinh dưỡng tốt cho mắt bằng việc ăn các thức ăn chứa nhiều vitamin A như rau cải xanh, cà rốt, gan cá, thịt bò...và đừng quên giữ vệ sinh nhà cửa sạch sẽ, tránh bụi bặm...</p>\r\n\r\n<h2><span style=\"font-size:17px\">Kính áp tròng</span></h2>\r\n\r\n<p>Việc đeo kính áp tròng hàng ngày cũng ảnh hưởng không nhỏ đến đôi mắt của bạn. Đeo kính áp tròng lâu ngày sẽ dẫn đến ngứa mắt, tăng nguy cơ phát triển bệnh khô mắt, dị ứng khiến đôi mắt nhạy cảm, đỏ và ngứa.</p>\r\n\r\n<p>Kính áp tròng là loại kính đặc biệt được đeo trực tiếp lên đồng tử mắt. Chính vì vậy mà vấn đề vệ sinh kính áp tròng được đặt lên hàng đầu, bởi chỉ cần để kính áp tròng bị bẩn, nó có thể gây ảnh hưởng nghiêm trọng đến mắt của bạn. Đối với những người sử dụng kính áp tròng, ký sinh trùng Acanthamoeba là kẻ thù tiềm ẩn. Loại ký sinh trùng nhỏ bé này khi xuất hiện ở kính áp tròng và được gắn lên mắt người, chúng có khả năng ăn mòn giác mạc và sinh sôi nảy nở khó kiểm soát. Dưới sự tấn công của chúng, mắt chúng ta dễ gặp các triệu chứng như ngứa rát, nhìn mờ, đau mắt, chảy nước mắt, sưng phồng mí,... Nghiêm trọng hơn, chúng có thể làm giảm thị lực và dẫn đến mù lòa.</p>\r\n\r\n<h2><span style=\"font-size:17px\">Nước hồ bơi</span></h2>\r\n\r\n<p>Chất clo trong nước hồ bơi gây khô mắt dẫn đến mẩn đỏ, ngứa mắt. Để làm sạch nước, các bể bơi thường sử dụng hoạt chất clo. Clo là một chất khử trùng thông thường, các hợp chất clo được sử dụng trong các bể bơi để giữ sạch sẽ và vệ sinh. Những bể bơi không được tẩy trùng kỹ sẽ là môi trường sống cho rất nhiều các loại khuẩn như Cryptosporidium - nguyên nhân chính gây bệnh tiêu chảy ở người hay khuẩn Chlamydia Trachomatis - hung thủ của viêm kết mạc ở mắt. Nhưng bể bơi chứa quá nhiều clo lại gây kích ứng da, mắt và một số tác hại nguy hiểm khác.</p>\r\n\r\n<p>Khi thấy tình trạng ngứa, đỏ mắt kéo dài, cần đi khám chuyên khoa để được điều trị, không tự ý dùng thuốc tra nhỏ mắt, khiến tình trạng bệnh nặng hơn và ngày càng khó chữa.</p>\r\n', 'sRnxFNrW_mat-bi-ngua-do-vi-sao1609050349.jpg', 1, 0, '2020-12-27', 10, '2020-12-27 15:21:21', '2020-12-27 15:21:21');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `NgayDang`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(21, 'Biện pháp điều trị phì đại tiền liệt tuyến hiệu quả', 'bien-phap-dieu-tri-phi-dai-tien-liet-tuyen-hieu-qua', '<p><strong>Phì đại tiền liệt tuyến (u xơ tiền liệt tuyến) là bệnh lý phì đại lành tính của tiền liệt tuyến, hay gặp ở nam giới khi tuổi tăng dần. Khi tiền liệt tuyến phì đại sẽ chèn ép vào niệu đạo và cổ bàng quang gây bí đái, tiểu khó, tiểu nhiều lần, làm giảm chất lượng cuộc sống, đồng thời việc ứ đọng nước tiểu ở bàng quang lâu dài có thể gây một số biến chứng như viêm đường tiết niệu, suy thận, sỏi và ung thư bàng quang.</strong></p>\r\n', '<p>Theo Tổ chức Y tế Thế giới, số nam giới mắc bệnh phì đại tiền liệt tuyến ở tuổi 60 chiếm 59%, tuổi 70 chiếm 77% và tuổi 80 chiếm 90%.</p>\r\n\r\n<h2><span style=\"font-size:17px\">Biểu hiện khi tiền liệt tuyến phì đại</span></h2>\r\n\r\n<p>Tuyến tiền liệt thường phát triển ở tuổi dậy thì, đến khoảng 20-25 tuổi thì bắt đầu ổn định. Tuy nhiên, sau 40 tuổi, tuyến tiền liệt thường có khuynh hướng phát triển bất thường, được gọi là phì đại lành tính tuyến tiền liệt.</p>\r\n\r\n<p>Phì đại tiền liệt tuyến có thể phát triển từ từ trong một thời gian dài mà không gây nên bất kỳ nguy hiểm nào. Khi tiền liệt tuyến phì đại chèn ép vào niệu đạo gây nên những rối loạn tiểu tiện, với 2 hội chứng đặc trưng sau:</p>\r\n\r\n<p>Hội chứng tắc nghẽn đường tiểu: Đi tiểu không hết, vẫn còn nước tiểu ứ đọng trong bàng quang, phải gắng sức rặn, tiểu ngắt quãng, tia nước tiểu yếu, thậm chí không thành tia, đái chỉ nhỏ giọt hoặc đái bị tắc xong lại đái tiếp, đi tiểu rất lâu... và nặng hơn có thể bị bí đái hoàn toàn.</p>\r\n\r\n<p>Hội chứng kích thích: Luôn luôn có cảm giác rất mói đái, đái không hết, dễ bị đái són, có nhu cầu đi tiểu nhiều lần trong ngày và đêm...</p>\r\n\r\n<p>Nếu nặng có thể gây bí đái mạn tính dẫn đến viêm bàng quang, viêm đường tiết niệu, thậm chí suy thận. Người bệnh thường đến khám khi xuất hiện các triệu chứng về rối loạn tiểu tiện hoặc bí đái cấp tính.</p>\r\n\r\n<p><img alt=\"Hình ảnh chụp cộng hưởng từ của bệnh nhân bị phì đại tiền liệt tuyến chọn lựa phương pháp nút mạch.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/22/6_resize.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Hình ảnh chụp cộng hưởng từ của bệnh nhân bị phì đại tiền liệt tuyến chọn lựa phương pháp nút mạch.\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Hình ảnh chụp cộng hưởng từ của bệnh nhân bị phì đại tiền liệt tuyến chọn lựa phương pháp nút mạch.</em></p>\r\n\r\n<h2><span style=\"font-size:17px\">Phương pháp điều trị phì đại&nbsp;</span><span style=\"font-size:17px\">tiền liệt tuyến</span></h2>\r\n\r\n<p>Không phải tất cả các bệnh nhân bị u phì đại lành tính tiền liệt tuyến (u xơ tiền liệt tuyến) đều phải điều trị. Những người không bị ảnh hưởng nhiều bởi các triệu chứng của u xơ tiền liệt tuyến thì thường không điều trị nhưng phải được kiểm tra định kỳ để xem tình trạng bệnh có trở nên xấu đi hay không mà xử trí kịp thời. Lựa chọn phương pháp điều trị như thế nào tùy thuộc vào rất nhiều yếu tố, nhưng chủ yếu là dựa vào các dấu hiệu lâm sàng (tức là mức độ ảnh hưởng đến tiểu tiện) và khối lượng của tiền liệt tuyến.</p>\r\n\r\n<p><em>Điều trị nội khoa:&nbsp;</em>Chỉ định khi bệnh chưa gây biến chứng, tiền liệt tuyến phì đại nhẹ. Phương pháp này đòi hỏi phải sử dụng thuốc kéo dài, với một số tác dụng phụ của thuốc như rối loạn cương dương, hạ huyết áp,...</p>\r\n\r\n<p><em>Điều trị ngoại khoa:&nbsp;</em>Là phương pháp điều trị triệt để, áp dụng cho trường hợp bệnh nhân điều trị nội khoa thất bại hoặc có những biến chứng.</p>\r\n\r\n<p>Phương pháp phẫu thuật: Mổ nội soi hoặc mổ mở có gây mê nên nguy cơ mất máu, thời gian nằm viện dài. Di chứng sau phẫu thuật: tiểu không tự chủ hoặc giảm khả năng tình dục do rối loạn cương dương hay xuất tinh ngược.</p>\r\n\r\n<p><em>Điều trị bằng can thiệp nội mạch:&nbsp;</em>Nút mạch điều trị phì đại tiền liệt tuyến là phương pháp được áp dụng phổ biến trên thế giới. Tại Việt Nam, một số trung tâm điện quang can thiệp đã ứng dụng thành công kỹ thuật này để điều trị cho bệnh nhân, ghi nhận đem lại hiệu quả cao.</p>\r\n\r\n<p>Nút mạch phì đại tiền liệt tuyến là phương pháp điều trị xâm lấn tối thiểu. Nguyên lý tiếp cận và loại bỏ nhánh mạch cấp máu cho tiền liệt tuyến, nhờ đó giúp làm giảm kích thước tiền liệt tuyến, giảm chèn ép vào niệu đạo và loại bỏ hoàn toàn các triệu chứng.</p>\r\n\r\n<p>Kỹ thuật này có nhiều ưu điểm: Bệnh nhân không phải gây mê, hoàn toàn tỉnh táo trong quá trình can thiệp. Thời gian nằm viện ngắn, chỉ một vài ngày đã có thể ra viện. Không chảy máu, ít nhiễm trùng. Không để lại di chứng như tiểu không tự chủ, không làm giảm khả năng tình dục.</p>\r\n\r\n<p>Tuy nhiên, phương pháp này cũng gặp phải một số khó khăn do động mạch tiền liệt tuyến kích thước thường nhỏ, bệnh nhân phì đại tiền liệt tuyến có tuổi cao, mạch máu bị xơ vữa.</p>\r\n', 'O4GtcBtX_bien-phap-dieu-tri-phi-dai-tien-liet-tuyen-hieu-qua1608626208.jpg', 0, 0, '2020-12-27', 10, '2020-12-27 15:21:59', '2020-12-27 15:21:59'),
(22, 'Trẻ hóa làn da, giữ mãi thanh xuân - Bí kíp đơn giản từ tinh hoa Pháp', 'tre-hoa-lan-da-giu-mai-thanh-xuan-bi-kip-don-gian-tu-tinh-hoa-phap', '<div class=\"sapo_detail\" style=\"margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 700; font-stretch: inherit; font-size: 17px; line-height: 27px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; color: rgb(45, 45, 45);\">30 tuổi, chỉ 1 vài vết nám trên gò má hay vài nếp nhăn nơi khóe mắt mỗi khi cười, cũng đủ để phụ nữ lo lắng hay trăn trở “tuổi già” đã ập đến. Nhưng nếu biết chăm sóc làn da và sức khỏe đúng cách, phụ nữ hoàn toàn có thể kéo dài tuổi thanh xuân và luôn tự tin tỏa sáng. Một trong những giải pháp hữu hiệu nhất, đó là sử dụng Collagen thủy phân.</div>\r\n\r\n<div id=\"content_detail_news\" style=\"margin: 0px 0px 30px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline;\">\r\n<p><strong>Collagen thủy phân - Chìa khóa tăng cường sự đàn hồi cho làn da.</strong></p>\r\n</div>\r\n', '<p>Nếu trái tim được ví von như “chìa khóa” vận hành toàn bộ cơ thể, Collagen lại được ví như “chìa khóa” cho sự bền bỉ dẻo dai của làn da và đóng vai trò quan trọng trong thay thế và phục hồi các tế bào da chết, làm tươi mới làn da.</p>\r\n\r\n<p>Khi 20 tuổi, da rắn chắc, đàn hồi, ngậm nước căng bóng mịn màng bởi hàm lượng Collagen cao trong da. Nhưng khi 40 tuổi, lượng collagen sụt giảm, da khô, nhão, chảy xệ, đồng thời xuất hiện các dấu hiệu của dòng thời gian như nếp nhăn, nám sạm,...</p>\r\n\r\n<p>Trước sức hút mạnh mẽ về vai trò của collagen đối với da, các chị em phụ nữ đua nhau sử dụng các sản phẩm có chứa collagen, từ serum, kem dưỡng da hay mặt nạ,…, nhưng nếu dùng sai cách thì khó hiệu quả vì kích thước của Collagen lại quá lớn, khó vượt qua hàng rào bảo vệ để đến được lớp trung bì.</p>\r\n\r\n<p>Vì vậy, các nhà khoa học Pháp đã bào chế collagen thủy phân từ các loại cá sạch ở đại dương - với kích thước phân tử siêu nhỏ, thẩm thấu sâu đến lớp hạ bì của da.</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/quangcao/2020/12/09/vietskin11.png\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Collagen thủy phân là giải pháp tối ưu làm chậm sự lão hóa</em></p>\r\n\r\n<p>Nghiên cứu cho thấy, việc bổ sung thường xuyên collagen thủy phân có thể mang lại hiệu quả gấp 10-20 lần so với collagen thông thường.</p>\r\n\r\n<p>Một nghiên cứu thử nghiệm lâm sàng ở phụ nữ từ 35 đến 55 tuổi, sử dụng Collagen peptide mỗi ngày, cho thấy các nếp nhăn trên mắt giảm rõ rệt với mức giảm tối đa 50%, độ đàn hồi da tăng 7%. Hơn nữa, hàm lượng của Pro-collagen loại I đã tăng 65%, elastin tăng 18% và fibrillin tăng 6% sau 8 tuần sử dụng.</p>\r\n\r\n<table border=\"1\" style=\"background:rgb(246, 246, 246); border-collapse:collapse; border-radius:10px; border-spacing:0px; border:1px solid rgb(204, 204, 204); font-family:noto serif; font-size:17px; font-stretch:inherit; font-variant-east-asian:inherit; font-variant-numeric:inherit; line-height:26px; margin:auto; padding:15px; text-rendering:geometricprecision; vertical-align:baseline\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; vertical-align:baseline\">\r\n			<p style=\"text-align:center\"><strong>Vieskin Collagen - Collagen thủy phân nhập khẩu từ Pháp, lụa chọn của chuyên gia làm đẹp</strong></p>\r\n\r\n			<p>Bộ mỹ phẩm dưỡng da Vieskin Collagen chứa collagen thủy phân nhập khẩu trực tiếp từ tập đoàn Eurofins, Pháp, với kích thước phân tử siêu nhỏ, hoạt tính sinh học cao, hấp thu nhanh và thẩm thấu tốt hơn gấp 20 lần so với collagen thông thường, Vieskin mang đến trải nghiệm tốt nhất cho làn da</p>\r\n\r\n			<p>- Ngay sau lần sử dụng đầu tiên: Da được cung cấp đủ ẩm, sẽ trở nên mịn màng, ẩm mướt, căng bóng, ngậm nước</p>\r\n\r\n			<p>- Sau 1 tuần: Làn da được hồi phục từ sâu bên trong, cảm nhận làn da khỏe mạnh, rạng rỡ, tươi sáng giàu sức sống.</p>\r\n\r\n			<p>- Sau 1 liệu trình: Làm chậm quá trình lão hóa da, làm mờ nếp nhăn, ngăn chặn tình trạng da chảy xệ, nám tàn nhang,...</p>\r\n\r\n			<p>- Duy trì sử dụng Vieskin Collagen mỗi ngày giúp làn da được cung cấp đủ dưỡng chất, luôn duy trì được độ đàn hồi giúp da căng sáng, ẩm mịn, giữ mãi nét thanh xuân cho làn da.</p>\r\n\r\n			<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/quangcao/2020/12/09/vietskin12.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n			<p style=\"text-align:center\"><em>Vieskin Collagen - Bảo vật được các chuyên gia làm đẹp khuyên dùng để làn da luôn tươi trẻ</em></p>\r\n\r\n			<p>Dòng sản phẩm Vieskin gồm 3 sản phẩm chuyên biệt:</p>\r\n\r\n			<p>-&nbsp;<strong>VieSkin Collagen-S&nbsp;</strong><a href=\"https://collagenvh.com.vn/san-pham/vieskin-collagen-s-gold-shine/\" rel=\"nofollow\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\" target=\"_blank\"><strong>Gold Shine</strong></a>: Tinh chất chứa Collagen thủy phân kết hợp cùng tinh chất hoa đào làm tăng tác dụng chống lão hóa, giúp làm dịu và sáng da tự nhiên, cho làn da căng mướt, khỏe mạnh.</p>\r\n\r\n			<p>-&nbsp;<strong>VieSkin Collagen-S&nbsp;</strong><a href=\"https://collagenvh.com.vn/san-pham/vieskin-collagen-s-luxury/\" rel=\"nofollow\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\" target=\"_blank\"><strong>Luxury</strong></a>: Tinh chất chứa Collagen thủy phân cao cấp và cô đặc, kết hợp cùng chiết xuất Cúc la mã giúp làm lành và phục hồi da, đẩy nhanh quá trình thay lớp biểu bì, làm mờ nếp nhăn và vết chân chim, giảm thâm nám,...cho làn da mềm mại, mịn màng, căng bóng, giữ mãi nét thanh xuân.Sử dụng sản phẩm trong và sau phun xăm còn giúp làm lành và lên màu đẹp hơn.</p>\r\n\r\n			<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/quangcao/2020/12/09/vietskin13.png\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n			<p style=\"text-align:center\"><em>Dòng sản phẩm Vieskin Collagen chăm sóc da chuyên sâu</em></p>\r\n\r\n			<p><strong>Hơn 235.068+ khách hàng hài lòng với hiệu quả sản phẩm mang lại</strong></p>\r\n\r\n			<p>Trong hành trình 8 năm đồng hành cùng phụ nữ Việt, Vieskin Collagen đã khẳng định được chất lượng và thương hiệu, với hơn 235.068 khách hàng yêu thích và tin dùng vì hiệu quả vượt trội mà sản phẩm mang lại cho làn da.</p>\r\n\r\n			<p>Chị Ngọc H, 35 tuổi, hiện đang là chủ 1 spa tại Hà Nội và cũng là khách hàng thân thiết của Vieskin Collagen suốt 3 năm nay, chia sẻ:&nbsp;<em>“Vieskin là sản phẩm luôn có ở bàn trang điểm cũng như ngay tại spa, cơ sở phun xăm thẩm mỹ của tôi. Với tôi Vieskin không chỉ là bảo bối làm đẹp giúp tôi luôn duy trì làn da căng mướt, giữ mãi tuổi thanh xuân mà còn là bí quyết giúp tôi hoàn thiện kỹ thuật phun xăm thẩm mỹ của mình, mang lại đôi môi và chân mày lên màu chuẩn đẹp, không thâm loang. Không chỉ vậy, tất cả khách hàng của tôi tại spa cũng đều cải thiện rõ làn da chỉ sau 1 liệu trình.”</em></p>\r\n\r\n			<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/quangcao/2020/12/09/vietskin14.png\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n			<p style=\"text-align:center\"><em>Hơn 235.068 khách hàng hài lòng về Vieskin Collagen-S</em></p>\r\n\r\n			<p>Bộ sản phẩm Vieskin Collagen - Sản phẩm của Việt Hùng Pharma, nhà tài trợ vàng Diễn dàn Marketing và Sales ngành Dược PMASS 2020</p>\r\n\r\n			<p>Để được tư vấn về chăm sóc da, độc giả vui lòng liên hệ hotline: 0934.606.161 - 0979. 088.088 hoặc truy cập website:&nbsp;<a href=\"https://collagenvh.com.vn/\" rel=\"nofollow\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\" target=\"_blank\">Collagenvh.com.vn</a>.</p>\r\n\r\n			<p>Đọc kỹ hướng dẫn sử dụng trước khi dùng</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 'sh78HKMJ_tre-hoa-lan-da-giu-mai-thanh-xuan-bi-kip-don-gian-tu-tinh-hoa-phap1607480792.png', 1, 0, '2020-12-27', 11, '2020-12-27 15:22:52', '2020-12-27 15:22:52'),
(23, 'Vì sao dưỡng da mãi vẫn không đẹp lên?', 'vi-sao-duong-da-mai-van-khong-dep-len-', '<p><strong>Sở hữu làn da đẹp không tì vết là ước mơ của tất cả chị em phụ nữ. Để có được một làn da đẹp thì bạn không những phải chú trọng tới việc ăn uống mà còn cần lưu tâm tới chế độ sinh hoạt hợp lý và khoa học. Bên cạnh đó, việc sử dụng kem chống nắng trước khi ra đường cũng là một điều quan trọng và cần thiết để giúp làn da luôn săn chắc, khỏe mạnh.</strong></p>\r\n', '<p>Tuy nhiên, một số chị em vẫn giữ những thói quen tưởng chừng như không gây hại gì nhưng lại chính là nguyên nhân khiến làn da không những cải thiện mà còn xấu đi theo năm tháng.</p>\r\n\r\n<h2><span style=\"font-size:17px\">Làm sạch da mặt không kỹ</span></h2>\r\n\r\n<p>Nhiều người vẫn quan niệm rằng nếu không trang điểm thì không cần phải tẩy trang. Tuy nhiên suy nghĩ đó hoàn toàn sai lầm bởi vì hằng ngày da chúng ta tiếp xúc với đủ bụi bẩn cũng như các tác nhân gây hại từ môi trường khiến cho da không được sạch và dễ bị bít tắc lỗ chân lông. Lúc này bước tẩy trang sẽ giúp bạn lấy sạch những bụi bẩn trên da mặt mà vẫn giữ được độ ẩm tự nhiên trên da.</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/quangcao/2020/11/05/paula31.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Làm sạch giúp da thông thoáng và chắc khỏe&nbsp;<em>(Ảnh minh họa)</em></em></p>\r\n\r\n<p>Một làn da không sạch sẽ dễ xuất hiện các vấn đề lão hóa sớm như: hình thành nên các nếp nhăn, vết chân chim, nám, tàn nhang… Không những thế, da không sạch còn khiến quá trình phục hồi da bị gián đoạn, gây bít tắc lỗ chân lông, tích tụ vi khuẩn, khiến mụn “biểu tình” trên da.</p>\r\n\r\n<p>Do đó, bạn nên thêm bước tẩy trang và quy trình chăm sóc da hàng ngày. Làn da sạch sẽ chính là tiền đề để các dưỡng chất chăm sóc da thẩm thấu sâu và đem lại hiệu quả hơn.</p>\r\n\r\n<h2><span style=\"font-size:17px\">Rửa mặt sai cách</span></h2>\r\n\r\n<p>Không nên rửa mặt quá nhiều lần trong ngày hoặc rửa với nước quá nóng. Bởi vì việc rửa mặt thường xuyên sẽ làm cho bề mặt lớp biểu bì bị mất nước, khiến da trở nên thô ráp và bong tróc.</p>\r\n\r\n<p>Không những thế, nhiều bạn sở hữu thói quen chăm sóc da mặt không tốt cho làn da. Ví dụ khi rửa mặt nhiều bạn có thói quen miết hoặc massage trên da quá mạnh, hoặc theo chiều từ trên xuống khiến da dễ chảy xệ và xuất hiện các nếp nhăn sớm.</p>\r\n\r\n<h2><span style=\"font-size:17px\">Sử dụng tẩy da chết vật lý với tần suất dày đặc</span></h2>\r\n\r\n<p>Loại bỏ tế bào chết giúp da mặt thông thoáng và sạch hơn. Tuy nhiên, nếu lạm dụng việc tẩy da chết quá nhiều lần trong 1 tuần (đối với tẩy da chết vật lý) không những phản tác dụng mà còn khiến làn da dần trở nên khô nứt, dễ tổn thương, nhanh bắt nắng hơn.</p>\r\n\r\n<p>Đây là phương pháp chỉ lấy đi được da chết trên bề mặt, không tẩy sâu da chết trong da. Không những thế, tẩy tế bào chết vật lý rất dễ khiến da bị tổn thương, đặc biệt là với da nhạy cảm.</p>\r\n\r\n<p>Bạn có thể cân nhắc sử dụng phương pháp loại bỏ tế bào chết cho da hóa học. Đây là phương pháp tẩy da chết rất hữu hiệu và khoa học trong cộng đồng làm đẹp. Các thành quen thuộc có thể kể đến BHA/AHA. Đây là các hoạt chất có tác dụng thúc đẩy các tế bào chết tự bong ra mà không cần phải tác động từ bên ngoài cũng như không cần phải rửa lại với nước như phương pháp loại bỏ tế bào chết vật lý.</p>\r\n\r\n<h2><span style=\"font-size:17px\">Lười bôi kem chống nắng</span></h2>\r\n\r\n<p>Tia UV từ ánh nắng mặt trời là một trong những nguyên nhân khiến làn da xuất hiện những vết nám, đồi mồi... Khi làn da tiếp xúc thường xuyên với ánh nắng không chỉ khiến các kết cấu collagen bị phá vỡ mà còn thúc đẩy quá trình lão hóa đến nhanh hơn cũng như tăng cao nguy cơ ung thư da.</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/quangcao/2020/11/05/paula32.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Tia UV từ ánh nắng mặt trời là một trong những nguyên nhân khiến làn da lão hóa&nbsp;(Ảnh minh họa)</em></p>\r\n\r\n<p>Nếu không sử dụng kem chống nắng hàng ngày, việc dưỡng da của bạn là vô ích! Dù bạn thấy chúng phiền phức hay khó chịu tới cỡ nào, thì dùng kem chống nắng vẫn là một việc rất quan trọng mà bạn cần lưu tâm. Những người sử dụng kem chống nắng hàng ngày hạn chế được 24% khả năng lão hóa sớm và giảm được 50% khả năng ung thư da so với những người không dùng.</p>\r\n\r\n<p>Do đó, dù có gấp đến đâu thì trước khi ra đường, bạn vẫn nên dành thời gian bôi kem chống nắng để bảo vệ làn da của mình trước ánh nắng mặt trời.</p>\r\n\r\n<h2><span style=\"font-size:17px\">Sai thứ tự các bước dưỡng da</span></h2>\r\n\r\n<p>Nếu làm đúng quy trình chăm sóc da tuần tự thì làn da sẽ được chăm sóc và bảo vệ một cách tốt nhất. Tuy nhiên, nếu bạn làm sai bất kỳ bước dưỡng da nào thì lại vô tình gây phản tác dụng trên làn da của mình.</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/quangcao/2020/11/05/paula33.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>(Ảnh minh họa)</em></p>\r\n\r\n<p>Dưới đây là 3 nguyên tắc skincare bạn cần phải nằm lòng</p>\r\n\r\n<p>Thoa các sản phẩm theo kết cấu từ lỏng đến đặc</p>\r\n\r\n<p>Thoa các sản phẩm theo độ pH từ thấp tới cao</p>\r\n\r\n<p>Thoa các sản phẩm tùy theo độ linh hoạt.</p>\r\n\r\n<p>Gợi ý trình tự chăm sóc da cơ bản: Tẩy trang -&gt; Sữa rửa mặt -&gt; Loại bỏ tế bào chết hóa học (Exfoliant) -&gt; Nước hoa hồng (Toner) -&gt; Essence/First Essence -&gt; Serum (Booster) -&gt; Kem mắt -&gt; Kem dưỡng ẩm -&gt;Mặt nạ ngủ.</p>\r\n\r\n<p>Để mua các sản phẩm chăm sóc da chính hãng vui lòng liên hệ&nbsp;<a href=\"https://paulaschoice.vn/singles-day?utm_source=skds&amp;utm_medium=banner_skds&amp;utm_campaign=Singles_day_2020&amp;utm_term=skds&amp;utm_content=skds\" rel=\"nofollow\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\" target=\"_blank\">Paulaschoice.vn</a></p>\r\n\r\n<table border=\"1\" style=\"background:rgb(246, 246, 246); border-collapse:collapse; border-radius:10px; border-spacing:0px; border:1px solid rgb(204, 204, 204); font-family:noto serif; font-size:17px; font-stretch:inherit; font-variant-east-asian:inherit; font-variant-numeric:inherit; line-height:26px; margin:auto; padding:15px; text-rendering:geometricprecision; vertical-align:baseline\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; vertical-align:baseline\">\r\n			<p><strong>Paula’s Choice Việt Nam</strong></p>\r\n\r\n			<p>Hotline: 1900 6409</p>\r\n\r\n			<p>Địa chỉ: Tầng 44 tòa C5 – KĐT D’Capitale, 224 Trần Duy Hưng, Trung Hòa, Cầu Giấy, Hà Nội, Việt Nam</p>\r\n\r\n			<p>Website:&nbsp;<a href=\"https://paulaschoice.vn/\" rel=\"nofollow\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\" target=\"_blank\">https://paulaschoice.vn</a></p>\r\n\r\n			<p>Fanpage:<a href=\"https://www.facebook.com/paulaschoice.vn\" rel=\"nofollow\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\" target=\"_blank\">&nbsp;</a><a href=\"https://www.facebook.com/paulaschoice.vn\" rel=\"nofollow\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\" target=\"_blank\">https://www.facebook.com/paulaschoice.vn</a></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 'YZnCpeqY_vi-sao-duong-da-mai-van-khong-dep-len1604561502.jpg', 0, 0, '2020-12-27', 11, '2020-12-27 15:23:50', '2020-12-27 15:23:50'),
(24, 'Người viêm xương khớp nên tập luyện thế nào?', 'nguoi-viem-xuong-khop-nen-tap-luyen-the-nao-', '<p><strong>Hiện nay, các bệnh lý viêm xương khớp khá phổ biến. Khi trời lạnh, các bệnh xương khớp trở nên nặng hơn, gây đau đớn cho người bệnh. Việc tập luyện để cải thiện tình trạng bệnh, phục hồi các chức năng cho sụn khớp là cần thiết. Tuy nhiên nếu tập luyện không phù hợp, bệnh sẽ nặng lên. Vì vậy, người bệnh viêm xương khớp cần lựa chọn phương pháp tập luyện phù hợp.</strong></p>\r\n', '<p>Viêm xương khớp là bệnh lý nhiều người mắc, nhất là lứa tuổi trung niên trở lên. Bệnh gây đau đớn, khó khăn khi di chuyển. Kết hợp giữa dùng thuốc và tập luyện đúng cách sẽ giúp cải thiện tình trạng bệnh, phục hồi chức năng cho bệnh nhân viêm xương khớp.</p>\r\n\r\n<p>Bệnh viêm xương khớp không thể trị dứt. Tuy nhiên, hoàn toàn có thể kiểm soát bệnh. Bên cạnh việc dùng thuốc theo đơn của bác sĩ, hoạt động thể chất phù hợp giúp kiểm soát viêm xương khớp hiệu quả, các khớp được bôi trơn và co duỗi tốt, giảm đau đớn.</p>\r\n\r\n<h2><span style=\"font-size:17px\">Những bài tập phù hợp&nbsp;</span><span style=\"font-size:17px\">với người bị viêm xương khớp</span></h2>\r\n\r\n<p><em>Đi bộ:</em>&nbsp;Nhắc đến những bài tập tốt nhất giúp phục hồi chức năng cho bệnh nhân viêm khớp, phải kể đến cách đi bộ mỗi ngày. Đi bộ là bài tập rất đơn giản và dễ thực hiện. Bệnh nhân viêm khớp có thể thực hiện ở bất kì nơi nào và trong mọi hoàn cảnh.</p>\r\n\r\n<p><img alt=\"Rong mơ là thực phẩm quen thuộc, giàu iod, có tác dụng tiêu đờm, nhuyễn kiên, tán kết, lợi thủy; trị bướu cổ, tràng nhạc, mụn nhọt.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/17/3_resize.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Tập yoga mang lại hiệu quả với người bệnh khớp (ảnh minh họa).\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Tập yoga mang lại hiệu quả với người bệnh khớp (ảnh minh họa).</em></p>\r\n\r\n<p>Khi bắt đầu đi bộ, bạn nên đi với tốc độ chậm, sau đó có thể điều chỉnh tăng dần tùy theo mức đáp ứng của cơ thể. Khi đi bộ, bạn nên chú ý khoảng cách giữa những bước đi, chỉ nên duy trì khoảng cách vừa phải, không sải bước quá dài hay quá ngắn sẽ làm gia tăng áp lực lên phần khớp. Nên đi bộ với khoảng cách giữa các bước đi là 1 - 2 bước chân tùy theo từng bệnh nhân. Nên đi bộ từ nửa tiếng tới 1 tiếng mỗi ngày tùy theo tình trạng bệnh, sau khi đi bộ khoảng 15 phút nên dừng lại để nghỉ ngơi, tránh việc đi liên tục khiến các khớp không có thời gian nghỉ, gây quá tải, đau đớn.</p>\r\n\r\n<p><em>Tập yoga:&nbsp;</em>Việc tập luyện yoga mang lại hiệu quả đáng kể đối với các bệnh nhân đang bị viêm khớp. Sự kết hợp nhẹ nhàng giữa các động tác và nhịp thở sâu, đều đặn, các tư thế đa dạng sẽ giúp thư giãn xương khớp, làm tăng độ linh hoạt cho khớp.</p>\r\n\r\n<p><em>Bơi lội:&nbsp;</em>Bơi lội là hình thức tập dễ thực hiện và đạt hiệu quả cao trong việc phục hồi chức năng cho bệnh nhân viêm khớp. Trước khi bơi, bệnh nhân cũng nên khởi động với các thao tác nhẹ nhàng để tránh chuột rút và ăn nhẹ trước khi xuống hồ bơi khoảng 2 tiếng để phòng ngừa kiệt sức. Khi bơi trong bể nước, phần rất lớn trọng lượng cơ thể sẽ được nước gánh chịu, làm giảm bớt áp lực lên các gối, bàn chân, mắt cá chân. Việc bơi trên mặt nước giúp bệnh nhân vận động thuận lợi hơn. Khi bơi, toàn bộ cơ thể được hoạt động, giúp mạnh gân khỏe cốt, gia tăng sức khỏe. Bên cạnh đó, lượng máu được tăng cường huy động tới các vùng bị sưng viêm, giảm thiểu sự đau nhức do bệnh viêm khớp gây ra.</p>\r\n\r\n<p><em>Aerobic:&nbsp;</em>Một biện pháp tập luyện phù hợp cho bệnh nhân viêm khớp là tập thể dục nhịp điệu (aerobic). Bệnh nhân nên hỏi ý kiến bác sĩ hoặc các nhà trị liệu để được hướng dẫn bài tập phù hợp với tình hình sức khỏe, nên tập aerobic ít nhất 2 giờ 30 phút mỗi tuần. Thể dục nhịp điệu là cách tập luyện nhẹ nhàng và đem lại nhiều hứng thú, giúp bệnh nhân làm lỏng các cơ, thư giãn tinh thần, ổn định nhịp tim, kiểm soát được cân nặng và giảm bớt tải trọng lên các khớp bàn chân, khớp háng, cột sống,…</p>\r\n\r\n<p><em>Động tác Squat cường độ nhẹ:&nbsp;</em>Đây là động tác tập phù hợp với bệnh nhân viêm xương khớp. Trước tiên, người tập hãy đứng ở tư thế mà 2 chân rộng bằng vai. Họ có thể tìm một nơi nào đó để giữ ổn định cơ thể như vịn vào lan can hay quầy bếp trước mặt. Sau đó, giữ ổn định khớp hông, đầu gối từ từ uốn cong lại, hạ thấp mông đến khi đạt được tư thế như đang ngồi trên ghế. Tiếp đến, duỗi đầu gối trở về tư thế đứng thẳng và bắt đầu thực hiện lại một lần nữa.</p>\r\n\r\n<p><strong>Lưu ý:</strong>&nbsp;Nguyên tắc chính khi vận động đối với người bệnh cơ xương khớp là nên vận động ít nhưng thường xuyên (tránh vận động nặng, mang vác nặng gây quá tải khớp). Tập đúng động tác, kết hợp thở sâu, nhịp nhàng, nâng dần mức độ tập luyện để tăng cường tính linh hoạt của các khớp cột sống, tăng cường sức mạnh và sự dẻo dai của các khối cơ, dây chằng ở vùng lưng, giúp cho sự cân bằng cần thiết các tư thế cột sống, giảm dần cảm giác đau.</p>\r\n\r\n<p>Người bị các bệnh về cơ xương khớp cần tập luyện ít nhất 30 phút/ngày, 5 ngày/tuần và lý tưởng nhất là nên chia mỗi buổi tập ra thành từng đợt kéo dài từ 10-15 phút và không nên cố sức. Tuyệt đối tránh vận động khi đang bị viêm khớp cấp. Nên lựa chọn các bài tập thể dục đơn giản, nhẹ nhàng phù hợp với tình trạng sức khỏe, bệnh lý và sở thích của bản thân.</p>\r\n', 'iEWqBgcr_nguoi-viem-xuong-khop-nen-tap-luyen-the-nao1608144225.jpg', 1, 0, '2020-12-27', 12, '2020-12-27 15:24:37', '2020-12-27 15:24:37'),
(25, 'Mũi cao nhờ bơm thuốc?', 'mui-cao-nho-bom-thuoc-', '<p><strong>Báo Sức khỏe&amp;Đời sống mở chuyên mục Tư vấn thẩm mỹ nhằm giải đáp những thắc mắc của bạn đọc trong lĩnh vực làm đẹp, thẩm mỹ.</strong></p>\r\n', '<p><img alt=\"Người tư vấn: Đại tá - TS.BS. Nguyễn Huy Thọ (ảnh), nguyên Chủ nhiệm Khoa Phẫu thuật hàm mặt và Tạo hình - Bệnh viện TW Quân đội 108, Uỷ viên Hội đồng Khoa học Sở Y tế Hà Nội.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/24/a80d94cf4b8ca2d2fb9d_resize.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Người tư vấn: Đại tá - TS.BS. Nguyễn Huy Thọ (ảnh), nguyên Chủ nhiệm Khoa Phẫu thuật hàm mặt và Tạo hình - Bệnh viện TW Quân đội 108, Uỷ viên Hội đồng Khoa học Sở Y tế Hà Nội.\" /></p>\r\n\r\n<p><strong><em>Người tư vấn:</em></strong><em>&nbsp;</em>Đại tá - TS.BS. Nguyễn Huy Thọ&nbsp;<em>(ảnh)</em>, nguyên Chủ nhiệm Khoa Phẫu thuật hàm mặt và Tạo hình - Bệnh viện TW Quân đội 108, Uỷ viên Hội đồng Khoa học Sở Y tế Hà Nội.</p>\r\n\r\n<p><strong><em>Gần đây em nghe nói có một phương pháp mới, kiểu như bơm thuốc vào để làm mũi tẹt thành mũi cao mà không phải độn sụn vào, vừa đơn giản vừa an toàn vì không lo bị ai đó đánh cho vỡ mũi. Có cô ca sĩ khá nổi tiếng lại còn tự vặn mũi mình để chứng minh cho thiên hạ là cô ấy không phẫu thuật thẩm mỹ, nhưng dư luận cũng đồn thổi là do cô ấy chọn phương pháp mới nên mới được vậy. Thưa bác sĩ, liệu có phương pháp này không ạ? Và nếu có thì nó có để lại hậu quả làm cứng cơ mặt như khi dùng botox không?</em></strong></p>\r\n\r\n<p style=\"text-align:right\"><em>(thaonguyen… yahoo.com)</em></p>\r\n\r\n<p>Đúng như em đã tìm hiểu, có rất nhiều người chọn cách nâng mũi bằng các chất liệu bơm. Phương pháp này thuộc thẩm mỹ nội khoa, là phương pháp làm đẹp không cần mổ xẻ, sử dụng những yếu tố vật lý, hóa học để làm trẻ hóa da, tiêu mỡ, tiêm làm phồng (cấy độn) vào một số bộ phận trên cơ thể như mũi, cằm... Vấn đề là người ta sẽ tiêm chất gì?</p>\r\n\r\n<p>Việc sử dụng dị chất như silicon lỏng hiện nay đã bị cấm trên toàn thế giới, và thẩm mỹ nội khoa thay thế silicon bằng một số chất làm đầy (filer) như Restylen, Teosyal. Những chất này có thành phần Hyaluronic Axit (HA) có tác dụng làm đầy mô, được dùng phổ biến để làm giảm nếp nhăn trên mặt và để độn sống mũi, vùng cằm. Sau khi tiêm HA thường có những phản ứng viêm nhẹ, xảy ra các mức độ khác nhau tùy theo cơ thể và lượng thuốc đưa vào. Sau 1 lần tiêm, bạn có thể có ngay mũi cao, cằm nhọn, mặt chữ V như mong muốn. Thời gian tác dụng của những chất này thông thường từ 6 tháng đến 1 năm. Sau đó chất HA tự tiêu, được đào thải ra khỏi cơ thể.</p>\r\n\r\n<p>Với kinh nghiệm của một bác sĩ phẫu thuật thẩm mỹ, tôi thấy mũi phải đẹp trên một nền cứng là xương, phủ bên ngoài là da. Mũi cao nhờ bơm chất lỏng sẽ không có độ sắc nét như bình thường, thân mũi tù, tày. Lúc đầu mũi được vun nhọn, sau đó khi HA tự tiêu thì tụt dần xuống, giống như hình mái nhà ngói chuyển sang mái vòm. Tôi không phản bác lại thẩm mỹ nội khoa nhưng với những gì tôi thấy được thì theo tôi không nên bơm chất làm đầy vào mũi. Hiện đã có thanh silicon đúc sẵn để độn mũi, độn cằm rất tốt, tạo dáng mũi rất đẹp, thời gian sử dụng lâu dài, tại sao không dùng?</p>\r\n', 'J1HFLCuA_mui-cao-nho-bom-thuoc1608788000.jpg', 0, 0, '2020-12-27', 12, '2020-12-27 15:25:21', '2020-12-27 15:25:21'),
(26, 'Những “bộ cánh” bất hủ của Jennifer Lopez', 'nhung-?-?bo-canh?-bat-hu-cua-jennifer-lopez', '<p><strong>Ở Hollywood, hiếm ai toàn năng như Jennifer Lopez. Cô không chỉ sở hữu những bài hát hit đình đám, mà còn là một diễn viên điện ảnh được yêu thích, và tất nhiên, kĩ năng nhảy đỉnh cao cũng là một “vũ khí” đáng gờm.</strong></p>\r\n', '<p>Thật khó tin Lopez đã bước sang tuổi 50, dường như cuộc sống của cô ở thời điểm hiện tại và tương lai vẫn sẽ tốt hơn bao giờ hết. Để tạo được hình ảnh đáng ngưỡng mộ này phải kể đến gu thời trang sành điệu của cô. Dưới đây là những “bộ cánh” gây được ấn tượng mãnh liệt khi cô xuất hiện trước công chúng.</p>\r\n\r\n<p>1. Tại Liên hoan phim quốc tế Palm Springs năm 2019, ngôi sao 50 tuổi diện chiếc đầm hoa rực rỡ thương hiệu Richard Quinn để nhận giải thưởng Spotlight cho vai diễn trong phim bom tấn Hustlers. Cô đã khéo léo kết hợp chiếc bóp cầm tay màu hồng nhẹ nhàng và đôi giày cao gót thanh lịch. Tại sự kiện này, bà mẹ 2 con lựa chọn kiểu tóc búi cao để tôn lên bờ vai trần quyến rũ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/05/08/1.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>2. Trong một dịp xuất hiện tại thành phố New York, ngôi sao lựa chọn chiếc áo khoác oversized, kết hợp đôi bốt và túi xách chất liệu da sang trọng. Đây cũng là set đồ hoàn hảo để chống chọi với cái lạnh tháng 12 tại New York.</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/05/08/2.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>3. Tại một sự kiện trò chuyện nhằm mục đích quảng bá phim Hustlers, Lopez mặc một chiếc váy midi xù in họa tiết paisley thương hiệu YSL. Cô nhận được nhiều khen ngợi nhờ vẻ ngoài vô cùng sang chảnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/05/08/3.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>4. Lễ trao giải IFP Gotham tổ chức năm 2019 tại New York, nữ diễn viên mặc một chiếc váy màu đen lông vũ của Ralph &amp; Russo.</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/05/08/4.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>5. Tháng 12/2019, khi tham dự buổi công chiếu đặc biệt của phim Hustlers, Lopez đã diện bộ trang phục vô cùng cá tính của thương hiệu Dolce &amp; Gabbana. Bộ trang phục màu bạc được cách điệu với áo sơ mi trắng và áo khoác đen.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/05/08/5.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>6. Tại Liên hoan phim TIFF, Lopez đã gây ấn tượng mạnh khi diện bộ jumpsuit trắng lệch vai của Solace.</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/05/08/6.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>7. Cũng trong sự kiện của TIFF, vào ngày thứ 2, Lopez vẫn giữ kiểu tóc buông tự nhiên, nhưng trang phục đã thay đổi thành chiếc váy quấn sequin màu đỏ của thương hiệu Elie Saab.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/05/08/7.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p>8. Tại Liên hoan phim quốc tế Toronto năm 2019, Lopez xuất hiện với tư cách diễn viên kiêm nhà sản xuất phim Hustlers. Cô cũng chính là nhân vật nổi bật nhất sự kiện nhờ bộ đầm màu vàng cực kỳ điệu đà được thiết kế bởi Maison Yeya.</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/05/08/8.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>9. Trong một cuộc trò chuyện với truyền thông để quảng bá cho bộ phim mới chưa kịp ra mắt, Lopez xuất hiện vô cùng sành điệu trong chiếc váy midi màu xanh lấp lánh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/05/08/9.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>10. Tại Giải thưởng thời trang CFDA 2019, Lopez vinh dự được nhận giải Biểu tượng thời trang, và không để ai thất vọng, cô đã xuất hiện như một nữ thần trong chiếc đầm màu cam vô cùng quyến rũ của Ralph Lauren.</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/05/08/10.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>11. Trong một buổi gala khác của sự kiện CFDA, Lopez lại gây “choáng” khi diện chiếc đầm màu bạc lấp lánh khá táo bạo của thương hiệu Versace.</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/05/08/11.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>12. Tại Giải thưởng Grammy 2019, diễn viên 50 tuổi lại khiến đám đông “sửng sốt” đến thích thú khi diện một chiếc mũ rộng vành cùng bộ đầm quý phái trên thảm đỏ. Có thể nói, phong cách thời trang độc đáo và táo bạo của cô đã hoàn toàn thuyết phục được công chúng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/05/08/12.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n', 'I6e3nFH4_nhung-bo-canh-bat-hu-cua-jennifer-lopez1588919580.jpg', 1, 0, '2020-12-27', 13, '2020-12-27 15:37:41', '2020-12-27 15:37:41'),
(27, 'Những stylist định hình diện mạo âm nhạc thế giới', 'nhung-stylist-dinh-hinh-dien-mao-am-nhac-the-gioi', '<p><strong>Chấp nhận đứng sau ánh hào quang của ca sĩ, nên ít ai biết đến những cống hiến thầm lặng của đội ngũ stylist. Tuy nhiên, chính họ là những “kẻ chủ mưu” đằng sau hình tượng đột phá của FKA Twigs, Billie Eilish, Solange,…</strong></p>\r\n\r\n<p><strong>Quyền năng của các ngôi sao</strong></p>\r\n', '<p>Những ngày gần đây, Billie Eilish xuất hiện trên thảm đỏ của Gucci hoặc Chanel không chỉ với tư cách một nhạc sĩ kiêm ca sĩ, mà còn là một nhà sáng tạo thời trang. Âm nhạc luôn ảnh hưởng đến thời trang và hiện nay, mối quan hệ này không mang tính cực đoan mà là một sự hợp tác. Các ngôi sao nhạc pop thường mang vẻ ngoài táo bạo và họ có khả năng định hướng sàn catwalk. Khi Cardi B khoác lên mình bộ trang phục “kín cổng cao tường” của thương hiệu Richard Quinn, thì Eilish cũng không chịu thua chị kém em khi diện bộ đồ Burberry quá khổ tại Giải thưởng Brit Awards vừa qua.</p>\r\n\r\n<p><br />\r\n<img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/07/02/Billie_Eilish_theo_phong_cach_cua_Samantha_Burkhart.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Billie Eilish theo phong cách của Samantha Burkhart</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bản thân các nghệ sĩ có rất nhiều ý tưởng cho phong cách của họ, nhưng phía sau họ là sự hậu thuẫn của các stylist như Kollin Carter, Kyle Lưu, Samantha Burkhart, Kat typaldos, Matthew Josephs... Những cống hiến thầm lặng của giới stylist có thể là bệ phóng cho giới thiết kế.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/07/02/Solange_Knowles_luon_tin_tuong_nhung_gi_Kyle_Luu_lua_chon.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Solange Knowles luôn tin tưởng những gì Kyle Lưu lựa chọn</em></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Đối với Burkhart, stylist của Eilish, tình yêu dành cho những chiếc váy dạo phố quá khổ chính là xuất phát điểm cho sự nghiệp sau này của cô. Khi mới bắt đầu công việc khoảng 4 năm trước, Burkhart đã săn lùng những mẫu váy phù hợp để Eilish thực hiện một buổi chụp hình, sau đó cô nhận ra một điều thú vị: các thương hiệu thời trang sẵn sàng chỉnh sửa kích thước và hình dáng những mẫu váy của họ theo số đo của Eilish. Họ thừa thông minh để nhận ra cô gái này thực sự là tương lai của âm nhạc thế giới. Thậm chí Burkhart còn cảm thấy cô quyền lực đến mức có thể can thiệp vào các mẫu thiết kế theo mong muốn của&nbsp; Eilish.</p>\r\n\r\n<p><strong>Sự khác biệt giữa diễn viên và ca sĩ</strong></p>\r\n\r\n<p>Không thể phủ nhận, một số nghệ sĩ nổi tiếng đang góp phần nâng tầm đẳng cấp cho một số nhà thiết kế hiện nay. FKA Twigs từng mặc một chiếc váy Grecian của nhà thiết kế Hy Lạp Dimitra Petsa; Rosalía là một fan hâm mộ của nhãn hiệu Hàn Quốc Kimhēkim có trụ sở tại Paris; trong khi Eilish vẫn trung thành với các thương hiệu đậm màu đường phố như Siberia Hills, Freak City, Left Hand và The Incorporated. Lưu, stylist của Knowles, chia sẻ rằng khách hàng của cô đặc biệt cởi mở với những cái tên mới nổi. “Chúng tôi tìm kiếm trên Instagram những thứ mà chúng tôi yêu thích. Sau đó chúng tôi có thể tùy chỉnh chúng theo ý mình và sẵn sàng diện chúng trên thảm đỏ”.</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/07/02/Cardi_B_va_bo_trang_phuc_kin_cong_cao_tuong_cua_thuong_hieu_Richard_Quinn.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Cardi B và bộ trang phục “kín cổng cao tường” của thương hiệu Richard Quinn</em></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Khi Lưu làm việc với Knowles để quyết định hình ảnh nào sẽ xuất hiện trên bìa album mới, cô đã phải tìm đến các nhà sáng tạo từ khắp nơi trên thế giới, từ Thụy Điển, Trung Quốc cho đến Georgia. Một số người trong số họ thậm chí là các nhà thiết kế thời trang, và cuối cùng cô đã quyết định hợp tác với Esmay Wagemans đến từ Hà Lan, người đã thiết kế áo lót bằng nhựa dẻo trong suốt cho các hình ảnh trong album của Knowles.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/phamquynh/2020/07/02/FKA_Twigs_trung_thanh_voi_stylist_Matthew_Josephs.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>FKA Twigs trung thành với stylist Matthew Josephs</em></p>\r\n\r\n<p>Lưu cho biết: “Là một stylist, bạn phải luôn lắng nghe, quan sát và nhạy cảm với xu hướng. Ví như, khi một ngôi sao tham dự lễ trao giải Grammys, diện mạo của cô ta có thể trở nên hoang dã hơn một chút, nhưng nếu một diễn viên tham dự lễ trao giải Oscar, mọi chuyện sẽ khác. Tôi nghĩ các diễn viên nói chung đều cho rằng họ cần phải lựa chọn phong cách nghiêm túc, trang trọng, vì vậy tôi không ép họ phải mặc những bộ trang phục quá ồn ào và điên rồ. Nhưng các ca sĩ lại khác, họ có xu hướng muốn thể hiện cá tính riêng và tôi luôn đọc được suy nghĩ của họ”.</p>\r\n', 'mLr1lf2S_nhung-stylist-dinh-hinh-dien-mao-am-nhac-the-gioi1593688040.jpg', 0, 0, '2020-12-27', 13, '2020-12-27 15:38:31', '2020-12-27 15:38:31');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `NgayDang`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(28, 'Nhận diện “vi khuẩn ăn thịt người” từ lăng kính khoa học', 'nhan-dien-?-?vi-khuan-an-thit-nguoi?-tu-lang-kinh-khoa-hoc', '<div class=\"sapo_detail\" style=\"margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 700; font-stretch: inherit; font-size: 17px; line-height: 27px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; color: rgb(45, 45, 45);\">Trên thế giới nói chung, ở Việt Nam nói riêng, các loại “vi khuẩn ăn thịt người” hiện đang tái xuất, nhất là trong bối cảnh mưa bão kéo dài và đã cướp đi nhiều sinh mạng. Đặc biệt là hai loại khuẩn nguy hiểm, Burkholderia pseudomallei gây bệnh Whitmore và “Sát thủ giấu mặt” mang tên Vibrio.</div>\r\n\r\n<div id=\"content_detail_news\" style=\"margin: 0px 0px 30px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline;\">\r\n<p style=\"text-align:justify\"><strong>Bệnh ít gặp, nhưng nguy hiểm, gây tử vong cao</strong></p>\r\n</div>\r\n', '<p style=\"text-align:justify\">“Vi khuẩn ăn thịt người”(flesh-eating bacteria) là cụm từ được giới truyền thông truyền thông quốc tế sử dụng để nói về bản chất là các vi khuẩn gây hiện tượng viêm cân mạc hoại tử- necrotizing fasciitis (NF), chứ thực tế không có vi khuẩn nào ăn thịt người theo đúng nghĩa đen.</p>\r\n\r\n<p style=\"text-align:justify\">NF là một dạng nhiễm khuẩn sâu dưới da không thường gặp, tiến triển rất nhanh, do độc tố của vi khuẩn gây viêm, phá hủy các mô liên kết, mô mỡ và mô cơ. Vi khuẩn gây viêm NF là liên cầu khuẩn beta tan huyết nhóm A (group A beta hemolytic streptococcal - GABHS), vi khuẩn Burkholderia pseudomallei gây bệnh Whitmore (Melioidosis) hiện đang xuất hiện tại tại các tỉnh miền Trung Việt Nam.</p>\r\n\r\n<p style=\"text-align:justify\">Burkholderia pseudomallei gây mưng mủ, hoại tử nhiều cơ quan, trong đó có da. Bệnh ít gặp, không bùng phát thành dịch, nhưng tiến triển rất nghiêm trọng, gây tỷ lệ tử vong cao, nhất là ở những người mắc bệnh mãn tính.</p>\r\n\r\n<p style=\"text-align:justify\">NF xâm nhập vào cơ thể nhiều nhất qua các vết thương hở, vết cắt nhỏ, vết trầy xước, côn trùng cắn, hình xăm, phẫu thuật... Trong một số trường hợp, con đường nhiễm vi khuẩn gây viêm NF không rõ ràng. Một khi đã xuất hiện, NF thường tiến triển nhanh, phá hủy mô liên kết, mô mỡ và mô cơ.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"vi khuẩn ăn thịt người\" src=\"https://media.suckhoedoisong.vn/Images/nguyenkhanh/2020/12/22/Nhan_dien_vi_kuan_an_thit_nguoi_1.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"vi khuẩn ăn thịt người\" /></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Khuẩn gây bệnh Withmore</span></h2>\r\n\r\n<p style=\"text-align:justify\">Melioidosis hay Whitmore là bệnh lây nhiễm gây ra bởi vi khuẩn gram âmBurkholderia pseudomallei. Được tìm thấy trong nước bẩn, đất, lây lan sang người và động vật qua tiếp xúc trực tiếp với nguồn ô nhiễm.&nbsp; Whitmore có nhiều điểm tương đồng với một bệnh ở loài ngựa, lây qua người từ gia súc bị nhiễm bệnh. Bệnh phổ biến ở khu vực Đông Nam Á, Bắc Úc, Nam Thái Bình Dương, Châu Phi, Ấn Độ, và Trung Đông.</p>\r\n\r\n<p style=\"text-align:justify\">Thông tin từ BV Đà Nẵng cho biết, từ đầu năm 2020 đến tháng 9/2020 chỉ ghi nhận 4 ca bệnh Whitmore. Nhưng trong hai tháng 10,11 đã có tới 28 ca nhập viện vì bệnh này. Bệnh nhân chủ yếu đến từ Quảng Nam, Quảng Ngãi, Đà Nẵng và một số ở Nghệ An, Quảng Trị… Đặc biệt, có hai trường hợp bị vết bầm tím ở chân, ủ bệnh, phải nhập viện cấp cứu; ngón chân bệnh nhân có mủ, không đi được, bị tiêu chảy và sốt. Các bác sĩ nhận định, nếu không được cấp cứu kịp thời, các bệnh nhân sẽ có nguy cơ tử vong.</p>\r\n\r\n<p style=\"text-align:justify\">Nhiều nghiên cứu cho thấy, 80% những người bị bệnh Whitmore có một hoặc nhiều yếu tố nguy cơ, điều đó đồng nghĩa Whitmore có thể là một nhiễm trùng cơ hội. Phổ biến ở&nbsp; nhóm người bị bệnh đái tháo đường, người nghiên rượu, bệnh thận hoặc bệnh phổi mạn tính, người sử dụng corticoid, bệnh ung thư. Bệnh đái tháo đường có tỷ lệ mắc bệnh cao từ 37% - 60%, điều này cho thấy insullin có thể trực tiếp ảnh hưởng đến vi khuẩn B.Pseudomallei.</p>\r\n\r\n<p style=\"text-align:justify\">Bệnh Whitmore khó lây từ người sang người, thường&nbsp; có các triệu chứng lâm sàng khác nhau, do vậy dễ bị bỏ sót hoặc nhầm với bệnh lao và các bệnh lý viêm phổi thông thường. Thời gian ủ bệnh của Whitmore trung bình 9 ngày, có khi kéo dài tới 3 tuần, thậm chí tới hàng chục năm. Tỷ lệ tái của Whitmore vào khoảng 1/16 ca, 1/4 trường hợp bệnh tái lại là do tái nhiễm, số còn lại do tái phát từ một ổ nhiễm tồn tại dai dẳng.</p>\r\n\r\n<p style=\"text-align:justify\">Theo Trung tâm kiểm soát và phòng ngừa dịch bệnh Mỹ (CDC), hiện chưa có vắc xin phòng bệnh Whitmore, nên việc phòng tránh là rất quan trọng. Nên đi khám ngay nếu phát hiện dấu hiệu nghi ngờ. Hạn chế tiếp xúc với tác nhân gây bệnh, bao gồm đất hoặc nước bùn lầy, đặc biệt là những nơi có ô nhiễm nặng. Trang bị đầy đủ giày, dép và găng tay đối với những người thường xuyên làm việc ngoài trời, tránh tiếp xúc trực tiếp với đất và nước nhiễm bẩn.</p>\r\n\r\n<p style=\"text-align:justify\">Nếu nghi ngờ mắc phải các triệu chứng bệnh, cần đến cơ sở y tế để được thăm khám, phát hiện vi khuẩn gây bệnh để điều trị kịp thời. Mặc dù ít gặp và không lây lan trực tiếp từ người sang người, nhưng công tác phòng tránh bệnh Whitmore cần được thực hiện nghiêm túc để hạn chế khả năng mắc phải những triệu chứng nghiêm trọng của bệnh, nhất là với những đối tượng có nguy cơ cao.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"vi khuẩn ăn thịt người\" src=\"https://media.suckhoedoisong.vn/Images/nguyenkhanh/2020/12/22/Nhan_dien_vi_kuan_an_thit_nguoi_2.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"vi khuẩn ăn thịt người\" /></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">“Sát thủ giấu mặt”</span></h2>\r\n\r\n<p style=\"text-align:justify\">Đầu tháng 7/2019, sau một ngày tắm biển, người đàn ông Mỹ tên là Dave Bennet tỉnh dậy với triệu chứng sốt, chuột rút, đau dữ dội. Dave Bennet đến từ thành phố Memphis, bang Tennessee, ông đi du lịch cùng vợ và con gái tại vịnh Mexico. Thay vì kỳ nghỉ hấp dẫn như mong đợi, 48 giờ sau người đàn ông&nbsp; này đã bị… “khuẩn ăn thịt”. Hôm 5/7, cả nhà vẫn còn đang trên tàu, vui chơi gần khu vực Crab Island và tắm biển Destin ở bang Florida, nhưng sáng hôm sau, Dave tỉnh dậy với “cơn sốt cao, ớn lạnh và chuột rút”. Chiều 7/7, ông Dave Bennet đã trút hơi thở cuối cùng. Theo gia đình, trước đó ông Dave đã mắc ung thư nhiều năm, do vậy hệ miễn dịch suy yếu, nhưng vợ con không nghĩ rằng lần đi biển này là lần cuối cùng trong đời ông. Kiểm tra y tế sau đó xác nhận ông Dave Bennett bị nhiễm vi khuẩn Vibrio Vulnificus dẫn đến hoại tử và tử vong.</p>\r\n\r\n<div class=\"style-box box-right\" style=\"margin: auto auto 15px; padding: 20px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; background: rgb(255, 232, 232); width: 330px; border-radius: 5px; float: right;\">Theo Trung tâm kiểm soát và phòng ngừa dịch bệnh Mỹ (CDC), hiện chưa có vắc xin phòng bệnh Whitmore, nên việc phòng tránh là rất quan trọng. Cần đi khám ngay nếu phát hiện dấu hiệu nghi ngờ. Hạn chế tiếp xúc với tác nhân gây bệnh, đặc biệt là những nơi bị ô nhiễm nặng. Trang bị đầy đủ giày, dép và găng tay đối với những người thường xuyên phải làm việc ngoài trời.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Theo CDC, nhóm người có nguy cơ tử vong cao nhất thuộc diện mắc bệnh gan, ung thư, đái tháo đường, HIV và Thalassemia (bệnh thiếu máu di truyền ở vùng Đại Trung Hải). Theo CDC, vi khuẩn Vibrio thường gây các triệu chứng tiêu chảy, nôn mửa và đau bụng. Nhưng những người có vấn đề về y tế như bệnh gan, tiểu đường, rối loạn dạ dày hoặc các tình trạng khác mà nhiễm khuẩn thì hệ miễn dịch sẽ bị suy yếu nhanh chóng, dẫn đến nhiều biến chứng nghiêm trọng, có thể tử vong.</p>\r\n\r\n<p style=\"text-align:justify\">Đầu năm 2018, một phụ nữ ở bang Texas (Mỹ) đã chết sau khi ăn hàu sống khi đi nghỉ ở Louisiana. Tại bệnh viện, cô được chẩn đoán bị bệnh vẩy nến, một bệnh nhiễm trùng do vi khuẩn Vibrio gây ra, thường được gọi là “vi khuẩn ăn thịt người”. Tuy nhiên, theo CDC, bệnh do Vibriosis gây ra lại tách biệt khỏi viêm hoại tử thường được gọi là nhiễm trùng ăn thịt, do vi khuẩn thuộc nhóm A strep, E. coli và staph.</p>\r\n\r\n<p style=\"text-align:justify\">CDC ước tính khoảng 52.000 trường hợp này là do ăn thực phẩm bị ô nhiễm, chủ yếu là các loại động vật có vỏ sống hoặc chưa nấu chín. Kkhoảng 95% các ca nhiễm trùng nghiêm trọng tại Mỹ là do ăn sò sống vùng vịnh, bởi sò ở đây đều mang khuẩn Vibrio, nhất là trong những tháng mùa hè. Triệu chứng duy nhất là nôn ói và tiêu chảy, riêng nhóm khỏe mạnh không phải nhập viện là do có hệ miễn dịch tốt. Nhiễm trùng cũng có thể xảy ra khi phơi nhiễm vết thương hở với nguồn nước bị ô nhiễm, nhất là nước lợ, nước mặn, tuy nhiên cơ chế lây nhiễm này ít phổ biến hơn.</p>\r\n\r\n<p style=\"text-align:justify\">Mầm bệnh được phân lập lần đầu vào năm 1976 từ một loạt các mẫu nuôi cấy máu gửi tới CDC ở Atlanta, được mô tả là một “vibrio lactose dương tính”, sau đó được đặt tên&nbsp; Beneckea vulnifica, và cuối cùng mang tên chính thức Vibrio vulnificus vào năm 1979. Vibrio có thể sống trong môi trường nước biển, đặc biệt ở những vùng có độ mặn thấp (0,5 đến 2,0% muối). Vibrio ra đời là do biến đổi khí hậu, do ô nhiễm hóa chất hoặc sinh học.</p>\r\n\r\n<p style=\"text-align:justify\">Tại Mỹ, khuẩn Vibrio có nhiều tại vùng vịnh Mexico từ tháng 4 đến tháng 10. Đối với những người có sức khỏe tốt, vi khuẩn có thể gây hại ít hơn như nhiễm trùng dạ dày thể nhẹ. Riêng nhóm người bị bệnh gan, đái tháo đường, ung thư, HIV/AIDS, rối loạn dạ dày... thì nguy cơ mắc bệnh cao hơn. Nhiễm trùng có thể xảy ra từ 2 ngày sau khi ăn sò biển, trai, hay hến sống hoặc nấu chưa chín có chứa vi khuẩn hoặc có vết thương mở khi tiếp xúc với nước biển như các trường hợp đề cập ở trên.</p>\r\n\r\n<p style=\"text-align:justify\">Nghiên cứu chỉ ra rằng, estrogen có tác dụng chống nhiễm trùng, là lý do tại sao bệnh gặp nhiều hơn ở nam giới. Nam giới thường có lượng chất sắt cao hơn, thứ mà khuẩn Vibrio rất cần để tồn tại và phát triển.</p>\r\n\r\n<p style=\"text-align:justify\">Theo CDC, Vibrio là khuẩn nguy hiểm, nên việc điều trị kịp thời là yêu cầu quan trọng nhất. Khi có vết thương mở hoặc có hệ miễn dịch yếu, nên tránh xa môi trường nước biển nhiễm khuẩn và hải sản tươi sống. Có một thực tế, chúng ta có thể bảo vệ sức khỏe của mình bằng cách ăn hàu được nấu chín. Hàu sống chính là thủ phạm lây lan viêm gan A và norovirus - thường được gọi là bệnh “cúm dạ dày”, có thể gây buồn nôn, nôn và đau bụng.</p>\r\n\r\n<p style=\"text-align:justify\">Nếu có vết thương mới nên để lành, hay rửa vết thương thật kỹ càng sớm càng tốt bằng xà phòng và nước sạch. Đi khám ngay trong vòng 4-5 giờ nếu vết thương sưng đỏ. Sốt và buồn nôn cũng là những dấu hiệu cảnh báo nhiễm trùng vibrio. Phát hiện sớm, sử dụng đúng phác đồ điều trị, đặc biệt là kháng sinh liều cao có thể tránh được mối nguy hiểm do sốc nhiễm khuẩn Vibrio gây ra.</p>\r\n', 'yjaaWcq7_nhan-dien-vi-khuan-an-thit-nguoi-tu-lang-kinh-khoa-hoc1608650096.jpg', 1, 0, '2020-12-27', 14, '2020-12-27 15:39:33', '2020-12-27 15:39:33'),
(29, '“Mở kho” trí tuệ và dữ liệu sẽ thúc đẩy phát triển y học chính xác tại Việt Nam', '?-?mo-kho?-tri-tue-va-du-lieu-se-thuc-day-phat-trien-y-hoc-chinh-xac-tai-viet-nam', '<div class=\"sapo_detail\" style=\"margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 700; font-stretch: inherit; font-size: 17px; line-height: 27px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; color: rgb(45, 45, 45);\">“Dữ liệu cần được chia sẻ, thay vì nghiên cứu rồi cất giữ” và cơ chế chia sẻ dữ liệu linh hoạt là điều kiện cần để thúc đẩy cộng đồng nghiên cứu Y sinh cùng phát triển - như GS. Vũ Hà Văn, Giám đốc Khoa học VinBigdata (thuộc Tập đoàn Vingroup) chia sẻ. Vì thế, mới đây, Viện VinBigdata đã vừa công bố ra mắt Hệ thống quản lý, phân tích và chia sẻ dữ liệu Y sinh lớn nhất Việt Nam.</div>\r\n\r\n<div id=\"content_detail_news\" style=\"margin: 0px 0px 30px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline;\">\r\n<p style=\"text-align:justify\"><strong>Hợp lực khai thác dữ liệu gene người Việt</strong></p>\r\n</div>\r\n', '<p style=\"text-align:justify\">Năm 2018, Vingroup công bố dự án Xây dựng cơ sở dữ liệu biến thể di truyền của người Việt do VinBigdata chịu trách nhiệm triển khai. Với việc giải trình tự toàn bộ hệ gene của hơn 1.000 người Việt khỏe mạnh, đây là dự án có quy mô lớn nhất Việt Nam trong lĩnh vực này tính đến thời điểm hiện tại. Bộ dữ liệu thu thập được đảm bảo tính đại diện khi đáp ứng sự đa dạng về vùng miền cùng các đặc điểm sinh học khác như: giới tính, độ tuổi (trải dài từ 35-55 tuổi). Sau 2 năm, 80% dữ liệu đã được phân tích và chú giải.</p>\r\n\r\n<p style=\"text-align:justify\">Giải trình tự gene không còn là một bài toán mới, đặc biệt khi dữ liệu về hệ gene liên quan chặt chẽ đến bệnh học, hỗ trợ phát hiện sớm, ngăn ngừa bệnh nan y... Việc giải trình tự gene phục vụ điều trị bệnh cũng là xu hướng của nhiều quốc gia trên thế giới như Mỹ, Anh, Úc... Để việc giải trình tự gene người Việt được nhanh chóng, chính xác, VinBigdata cũng đẩy mạnh kết nối, chia sẻ nguồn lực, dữ liệu với các tổ chức khoa học có uy tín.</p>\r\n\r\n<p style=\"text-align:justify\">Cuối năm 2020, VinBigdata chính thức bắt tay với 21 đơn vị nghiên cứu hàng đầu trong lĩnh vực Y khoa, Sinh học phân tử, Khoa học máy tính, Tin sinh học của Việt Nam và quốc tế. Nổi bật trong số đó có thể kể đến Trung tâm khoa học dữ liệu ứng dụng, Đại học Chicago (Mỹ); Phòng Thí nghiệm hệ gene ung thư, Trung tâm ung thư MD Anderson (Mỹ), Phòng Dịch tễ học và Thống kê sinh học, Viện nghiên cứu Karolinska (Thụy Điển), Trường Khoa học Máy tính, Đại học Tel Aviv (Israel), Viện Sinh học phân tử, Đại học Queensland (Úc), Viện Tin sinh học Singapore, Đại học Y Hà Nội, Đại học Dược Hà Nội, Đại học Bách khoa Hà Nội...</p>\r\n\r\n<p style=\"text-align:justify\">Hợp tác toàn diện về nghiên cứu, kết nối và chia sẻ học thuật, VinBigdata hướng tới xây dựng mạng lưới nghiên cứu tri thức toàn cầu, làm cầu nối giữa các nhà khoa học Việt Nam với các chuyên gia đầu ngành trên thế giới. Đây là cơ hội để đội ngũ nghiên cứu trong nước tận dụng, tăng tính hiệu quả trong nghiên cứu và phát triển.</p>\r\n\r\n<p style=\"text-align:justify\">Thông qua hợp tác, các bên sẽ cùng tham gia các dự án như: nghiên cứu đặc điểm di truyền quần thể người Việt, phát triển hệ thống quản lý và phân tích dữ liệu gene quy mô lớn, nghiên cứu các giải pháp dự đoán nguy cơ bệnh và đáp ứng thuốc dựa trên gene, phát hiện và cảnh báo vi khuẩn kháng kháng sinh... với kỳ vọng sẽ đưa các giải pháp ứng dụng vào thực tế tại Việt Nam trong 2-3 năm tới.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"“Mở kho” trí tuệ và dữ liệu sẽ thúc đẩy phát triển y học chính xác tại Việt Nam\" src=\"https://media.suckhoedoisong.vn/Images/nguyenkhanh/2020/12/24/H_thng_phan_tich_va_qun_l_d_liu_Y_sinh.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"“Mở kho” trí tuệ và dữ liệu sẽ thúc đẩy phát triển y học chính xác tại Việt Nam\" /><em>Hệ thống phân tích và quản lý dữ liệu Y sinh lớn nhất Việt Nam đang lưu trữ hơn 1.200 TeraByte dữ liệu và gần 5000 mẫu sinh học liên quan đến dự án 1000 hệ gene người Việt.</em></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Mở kho dữ liệu phục vụ cộng đồng</strong></p>\r\n\r\n<p style=\"text-align:justify\">“Dữ liệu cần được chia sẻ, thay vì nghiên cứu rồi cất giữ”, và cơ chế chia sẻ dữ liệu linh hoạt là điều kiện cần để thúc đẩy cộng đồng nghiên cứu Y sinh cùng phát triển - như GS. Vũ Hà Văn, Giám đốc Khoa học VinBigdata chia sẻ tại sự kiện AI4VN diễn ra vào cuối tháng 11 vừa qua. Đây cũng là một trong những mục tiêu để đội ngũ các nhà khoa học tại VinBigdata phát triển Hệ thống Quản lý, Phân tích và Chia sẻ dữ liệu Y sinh (https://genome.vinbigdata.org) lớn nhất Việt Nam như một “kho dữ liệu mở” phục vụ cộng đồng.</p>\r\n\r\n<p style=\"text-align:justify\">Tính “mở” của hệ thống được thể hiện rõ ngay từ khâu thiết kế, xây dựng, phát triển đến công bố, ứng dụng. Dữ liệu từ dự án 1.000 hệ gene người Việt cũng như một số dự án ứng dụng quy mô lớn khác sẽ được chia sẻ thông qua hệ thống này. Hiện tại, hệ thống đang lưu trữ và xử lý hơn 1.200 TeraByte dữ liệu của gần 5.000 mẫu sinh học theo tiêu chuẩn của Viện Y tế Quốc gia Mỹ (NIH). Thiết kế của hệ thống đảm bảo bảo mật thông tin theo Quy định chung về bảo mật thông tin (GDPR) của châu Âu.</p>\r\n\r\n<p style=\"text-align:justify\">Với hệ thống này, VinBigdata và các đối tác có thể dễ dàng truy cập, tìm kiếm thông tin, phân tích và chia sẻ dữ liệu, đẩy nhanh tiến độ nghiên cứu. Người dùng có thể tra cứu (theo gene, theo biến thể gene, theo từng cá thể, theo mã tra cứu rsID...); tham chiếu, phân tích dữ liệu hệ gene người Việt phục vụ nghiên cứu, phát triển các ứng dụng lâm sàng; chỉ mất dưới 30 phút cho một mẫu phân tích toàn hệ gene. Dự kiến đến cuối năm 2021, hệ thống sẽ cập nhật thêm các bộ dữ liệu từ các dự án nghiên cứu ứng dụng về nguy cơ bệnh và tác dụng phụ của thuốc.</p>\r\n\r\n<p style=\"text-align:justify\">Trong bối cảnh có không ít nghiên cứu xây dựng được nguồn dữ liệu phong phú, có giá trị về người Việt, có khả năng tận dụng cho nhiều đề tài tiếp nối... nhưng gặp bất cập về cơ chế chia sẻ, hệ thống được kỳ vọng sẽ là bước khởi đầu nhằm giải quyết thách thức này, trở thành cổng thông tin dữ liệu mở uy tín, đáng tin cậy của Việt Nam, nhận được sự đóng góp dữ liệu từ cộng đồng nghiên cứu, doanh nghiệp và các tổ chức.</p>\r\n', '4D5ySk7y_mo-kho-tri-tue-va-du-lieu-se-thuc-day-phat-trien-y-hoc-chinh-xac-tai-viet-nam1608821690.jpg', 1, 0, '2020-12-27', 14, '2020-12-27 15:40:08', '2020-12-27 15:40:08'),
(30, 'Cập nhật thông tin liên quan đến tính an toàn của thuốc chứa mephenesin', 'cap-nhat-thong-tin-lien-quan-den-tinh-an-toan-cua-thuoc-chua-mephenesin', '<div class=\"sapo_detail\" style=\"margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 700; font-stretch: inherit; font-size: 17px; line-height: 27px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; color: rgb(45, 45, 45);\">Cục Quản lý Dược vừa có công văn gửi sở Y tế các tỉnh, thành phố trực thuộc Trung ương; các bệnh viện, viện có giường bệnh trực thuộc Bộ Y tế, cung cấp thông tin liên quan đến tính an toàn của thuốc chứa mephenesin.</div>\r\n\r\n<div id=\"content_detail_news\" style=\"margin: 0px 0px 30px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline;\">\r\n<p style=\"text-align:justify\"><strong>Tình hình lưu hành trên thế giới của thuốc chứa mephenesin</strong></p>\r\n</div>\r\n', '<p style=\"text-align:justify\">Ngày 21/6/2019, Cơ quan Quản lý Dược Pháp (ANSM) thông báo đình chỉ lưu hành thuốc giãn cơ decontractyl từ ngày 28/6/2019. Quyết định đình chỉ lưu hành decontractyl (hoạt chất mephenesin) bắt nguồn từ kết quả đánh giá lại cân bằng lợi ích - nguy cơ của decontractyl đường uống và dùng tại chỗ. Trong đó, các báo cáo phản ứng có hại đã được báo cáo với các chế phẩm này bao gồm:</p>\r\n\r\n<p style=\"text-align:justify\">- Với viên nén decontractyl: Báo cáo ca về lạm dụng và phụ thuộc thuốc, tăng liều, khó chịu, cảm giác chóng mặt và các phản ứng phản vệ.</p>\r\n\r\n<p style=\"text-align:justify\">- Với thuốc mỡ decontractyl Baume: Phản ứng trên da tại vị trí bôi thuốc như nóng bừng và nổi ban đỏ (một số biến cố đã xảy ra ở trẻ em khi tiếp xúc da với người lớn sử dụng thuốc mỡ).</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" class=\"field_photo img-responsive show-grid thumbnail\" src=\"data:image/jpeg;base64,\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Click here to upload image!\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>ANSM khuyến cáo bệnh nhân sử dụng các thuốc giảm đau nhóm 1 (paracetamol, NSAIDs) tùy thuộc vào đặc điểm cá thể từng người bệnh trong trường hợp đau kéo dài.</em></p>\r\n\r\n<p style=\"text-align:justify\">Dựa trên dữ liệu hiện có, ANSM cho rằng lợi ích từ các chế phẩm này không vượt trội so với nguy cơ, do hiệu quả hạn chế trong khi nguy cơ gặp các phản ứng có hại cao và có nhiều lựa chọn thay thế điều trị bằng các thuốc hoặc các biện pháp không dùng thuốc khác. ANSM cũng đã chỉ ra rằng việc ngừng điều trị bằng decontractyl không làm gia tăng nguy cơ và bệnh nhân có thể áp dụng các biện pháp điều trị thay thế không dùng thuốc (tập thể thao, thư giãn, vật lý trị liệu). Do đó, ANSM khuyến cáo bệnh nhân sử dụng các thuốc giảm đau nhóm 1 (paracetamol, NSAIDs) tùy thuộc vào đặc điểm cá thể từng người bệnh trong trường hợp đau kéo dài.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Động thái xử lý đối với thuốc chứa mephenesin của cơ quan chức năng tại Việt Nam</strong></p>\r\n\r\n<p style=\"text-align:justify\">Ngày 16/7/2019, Cục Quản lý Dược đã có công văn số 11841/QLD-ĐK về việc theo dõi tình trạng lưu hành, sử dụng và tính an toàn, hiệu quả của thuốc chứa dược chất mephenesin.</p>\r\n\r\n<p style=\"text-align:justify\">Ngày 22/01/2020, Cục Quản lý Dược đã có Công văn số 857/QLD-ĐK gửi Sở Y tế các tỉnh, thành phố trực thuộc Trung ương; các bệnh viện, viện có giường bệnh trực thuộc Bộ Y tế; các cơ sở sản xuất, đăng ký lưu hành thuốc tại Việt Nam thông báo: Ngừng tiếp nhận và xét duyệt đối với hồ sơ đề nghị cấp phép nhập khẩu, hồ sơ đề nghị cấp, gia hạn, duy trì hiệu lực giấy đăng ký lưu hành thuốc của các thuốc chứa hoạt chất mephenesin; yêu cầu các công ty ngừng sản xuất, nhập khẩu thuốc chứa hoạt chất mephenesin kể từ ngày ký công văn này. Đối với các thuốc chứa hoạt chất mephenesin đã được sản xuất, nhập khẩu vào Việt Nam trước ngày ký công văn này được phép lưu hành đến hết hạn dùng của thuốc.</p>\r\n\r\n<p style=\"text-align:justify\">Để đảm bảo sử dụng thuốc an toàn, hợp lý, Cục Quản lý Dược đề nghị Sở Y tế các tỉnh, thành phố trực thuộc Trung ương, các bệnh viện, viện có giường bệnh trực thuộc Bộ Y tế thực hiện các nội dung sau căn cứ ý kiến của Hội đồng Tư vấn cấp Giấy đăng ký lưu hành thuốc, nguyên liệu làm thuốc - Bộ Y tế xét duyệt đợt 104 - đối với thuốc nước ngoài như sau:</p>\r\n\r\n<p style=\"text-align:justify\">-Hạn chế sử dụng và thông báo cho các cơ sở khám bệnh, chữa bệnh, các cơ sở kinh doanh dược và người dân trên địa bàn các thông tin liên quan đến tính an toàn, hiệu quả của thuốc chứa mephenesin, để hạn chế sử dụng thuốc chứa mephenesin và sử dụng các biện pháp điều trị thay thế khác phù hợp.</p>\r\n\r\n<p style=\"text-align:justify\">- Thực hiện và hướng dẫn các cơ sở khám bệnh, chữa bệnh trên địa bàn tăng cường theo dõi, phát hiện, xử trí các trường hợp xảy ra phản ứng có hại của các thuốc nêu trên (nếu có) và gửi báo cáo phản ứng có hại của thuốc về: Trung tâm quốc gia về Thông tin thuốc và Theo dõi phản ứng có hại của thuốc hoặc Trung tâm khu vực về thông tin thuốc và theo dõi phản ứng có hại của thuốc TP. Hồ Chí Minh.</p>\r\n\r\n<div class=\"style-box\" style=\"margin: auto auto 15px; padding: 20px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; background: rgb(255, 232, 232); width: 660px; border-radius: 5px; text-align: justify;\">Mephenesin là thuốc giãn cơ có tác dụng trên hệ thần kinh trung ương, được dùng để điều trị triệu chứng đau cơ do co thắt cho các bệnh về cơ xương. Thuốc có tác dụng toàn thân và cũng có tác dụng tại chỗ khi dùng đường bôi ngoài da. Lợi ích lâm sàng của thuốc bị hạn chế do thời gian tác dụng ngắn.<br />\r\n<br />\r\nMephenesin dạng uống có thể có những tác dụng không mong muốn như co giật, run rẩy, co cứng bụng, buồn nôn, toát mồ hôi. Triệu chứng này thường xảy ra ở những người bệnh phải dùng thuốc kéo dài. Thường gặp: Mệt mỏi, buồn ngủ, uể oải, khó thở, yếu cơ, mất điều hòa vận động. Ít gặp hơn: Đau khớp, đau người, buồn nôn, bực tức, tiêu chảy, táo bón, nổi mẩn. Một vài trường hợp đặc biệt khác bị mất cảm giác ngon miệng, nôn mửa, ảo giác kích động và có thể có sốc phản vệ. Hiếm gặp: Phản ứng dị ứng, ngủ gà, phát ban. Sốc phản vệ rất hãn hữu xảy ra.</div>\r\n', 'UIwqvAte_cap-nhat-thong-tin-lien-quan-den-tinh-an-toan-cua-thuoc-chua-mephenesin1607349471.jpg', 0, 0, '2020-12-27', 15, '2020-12-27 15:40:59', '2020-12-27 15:40:59');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `NgayDang`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(31, 'Thuốc ho: Tuy quen thuộc, nhưng không dễ sử dụng', 'thuoc-ho-tuy-quen-thuoc-nhung-khong-de-su-dung', '<p><strong>Suckhoedoisong.vn - Ho là một triệu chứng thực thể gây nên không ít phiền toái, nhất là khi kéo dài, dai dẳng. Nhiều người đã tìm đến các loại thuốc ho có sẵn ở hiệu thuốc để mong chấm dứt ho càng sớm càng tốt.</strong></p>\r\n', '<p style=\"text-align:justify\">Nhưng, “dục tốc bất đạt”, thuốc ho cũng có rất nhiều loại, với nhiều cơ chế khác nhau. Việc lạm dụng sẽ gây nhiều hậu quả bất lợi cho bạn; đặc biệt là trẻ em, đối tượng rất dễ bị cảm, ho, sốt trong mùa lạnh hay vào thời khắc giao mùa.</p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Ho - nỗ lực bảo vệ đường hô hấp của cơ thể</span></h2>\r\n\r\n<p style=\"text-align:justify\">Ho là một phản xạ có điều kiện của cơ thể khi đường hô hấp trên bị kích thích. Là cơ chế bảo vệ có lợi nhằm làm sạch đường hô hấp, loại bỏ khói bụi, vi khuẩn, các tác nhân gây kích ứng và dịch tiết. Ho cũng biểu hiện như là triệu chứng của một số bệnh lý tai - mũi - họng và hô hấp như viêm, nhiễm trùng, hen suyễn, bệnh phổi tắc nghẽn mạn tính (COPD), lao phổi hoặc các bệnh lý khác; cần phải điều trị căn nguyên thì ho mới thuyên giảm. Nếu ho kéo dài sẽ gây khó chịu, khàn giọng, tắt tiếng; ảnh hưởng tới sức khỏe, thời gian nghỉ ngơi, sinh hoạt của người bệnh.</p>\r\n\r\n<p style=\"text-align:justify\">Triệu chứng ho bao gồm nhiều dạng khác nhau, có thể chia thành hai nhóm là ho khan và ho có đờm. Ho khan là tình trạng ho không có đờm và dịch tiết, cổ họng người bệnh có cảm giác ngứa rát khó chịu, đôi khi là cảm giác sâu trong họng. Cơn ho thường ngắn nhưng cũng có thể là nhiều cơn liên tục dữ dội. Khi người bệnh hít vào sau một cơn ho vừa dứt thì lại tiếp tục cơn ho khác, làm tăng áp lực lồng ngực, người bệnh đỏ mặt, chảy nước mắt và gây phản xạ nôn. Ho khan xảy ra trong thời gian ngắn khi bệnh nhân bị kích ứng, dị ứng. Trường hợp ho khan kéo dài mạn tính thường xảy ra ở bệnh nhân suy tim, trào ngược dạ dày - thực quản, hút thuốc lâu năm, bất thường trong tổ chức phổi như hen phế quản, xơ phổi, ung thư phổi... Ho khan cũng có thể do tác dụng không mong muốn khi sử dụng nhóm thuốc ức chế men chuyển (ACEi) kéo dài trong điều trị tăng huyết áp như captopril, enalapril, lisinopril...</p>\r\n\r\n<p style=\"text-align:justify\">Còn ho có đờm là tình trạng ho có xuất hiện chất nhầy, đờm nhớt trong đường hô hấp, phản xạ ho là để cố gắng tống khứ chúng ra ngoài. Có thể kèm theo khó thở và nặng ngực nếu lượng đờm nhiều, xảy ra khi mắc cảm cúm, có tình trạng viêm nhiễm ở phổi và vùng họng, mũi xoang…</p>\r\n\r\n<p style=\"text-align:justify\">Bệnh nhân ho nhẹ có thể sử dụng các liệu pháp từ thiên nhiên như ngậm mật ong, tắc mật ong, uống trà nóng: trà chanh và mật ong, trà gừng, trà cam thảo để làm dịu cơn ho thay cho dùng thuốc. Giữ ấm cơ thể và vùng họng. Bổ sung nước trái cây để cung cấp vitamin, tăng sức đề kháng cho cơ thể.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Thuốc ho\" src=\"https://media.suckhoedoisong.vn/Images/nguyenkhanh/2020/12/16/Thuoc_ho_tuy_quen_thuoc_nhung_khong_de_su_dung_1.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Thuốc ho: Tuy quen thuộc, nhưng không dễ sử dụng\" /></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Sử dụng thuốc điều trị ho</span></h2>\r\n\r\n<p style=\"text-align:justify\">Thuốc ho là những thuốc làm giảm phản xạ ho, bằng cơ chế này hay cơ chế khác. Tuy nhiên, bằng chứng về tác dụng, hiệu lực của các loại thuốc ho vẫn chưa đủ mạnh và chắc chắn đối với nhiều căn nguyên gây ho và mức độ ho khác nhau. Điều này không có nghĩa rằng các loại thuốc trị ho không có tác dụng, mà tác dụng của chúng cần được nghiên cứu, chứng minh rõ ràng hơn. Khi ho xuất hiện, nhất là ở trẻ em, chúng ta thường nhanh chóng sử dụng thuốc ho vì quan niệm rằng để lâu sẽ khó chữa, nhưng thực chất hệ miễn dịch của chúng ta đang hoạt động và triệu chứng ho có thể thuyên giảm sau vài ngày mà không cần dùng thuốc.</p>\r\n\r\n<p style=\"text-align:justify\">Cơ quan quản lý Thực phẩm và Dược phẩm Mỹ (FDA) khuyến cáo không nên sử dụng bất kỳ loại thuốc ho và cảm lạnh không kê đơn (OTC) nào cho trẻ sơ sinh và trẻ em dưới 2 tuổi, vì lợi ích mang lại không rõ ràng, có thể nguy hiểm đến tính mạng của trẻ.</p>\r\n\r\n<div class=\"style-box box-right\" style=\"margin: auto auto 15px; padding: 20px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; background: rgb(255, 232, 232); width: 330px; border-radius: 5px; float: right; text-align: justify;\">Ho là một phản xạ có lợi, cho nên chúng ta cần cân nhắc sử dụng thuốc điều trị triệu chứng ho chỉ khi ho ảnh hưởng nhiều đến cuộc sống và tình trạng sức khỏe của cơ thể. Việc đến gặp bác sĩ để điều trị nguyên nhân là điều cần thiết khi ho kéo dài trên một tuần, thường xuyên vào ban đêm, có dấu hiệu tiến triển nặng hơn đi kèm với các triệu chứng viêm nhiễm đường hô hấp đặc trưng như đờm đặc quánh. có màu xanh hoặc nâu và có mùi hôi, triệu chứng ho ra máu, đau tức ngực, phát ban, khó thở, sốt cao...<br />\r\n&nbsp;</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Nhiều loại thuốc ho, sirô ho chứa nhiều hoạt chất kết hợp với nhau, bao gồm cả hoạt chất hạ sốt, giảm đau như paracetamol, aspirin, vì vậy người sử dụng cần lưu ý để tránh quá liều khi dùng nhiều loại thuốc ho, hạ sốt, giảm đau cùng lúc. Hơn nữa, hiệu quả khi kết hợp nhiều hoạt chất với nhau chưa được kiểm chứng, tăng khả năng xảy ra các tác dụng không mong muốn và tương tác thuốc.</p>\r\n\r\n<p style=\"text-align:justify\">Các nhóm thuốc ho phổ biến hiện nay và lưu ý khi sử dụng:</p>\r\n\r\n<p style=\"text-align:justify\">- Nhóm thuốc tác động trên thần kinh trung ương: Giảm ho do ức chế trực tiếp trung tâm ho ở não. Các thuốc này được sử dụng phổ biến và quen thuộc, thuộc nhóm opioid như codein, hydrocodon, pholcodin (có thêm tác dụng giảm đau nhưng gây nghiện) và dextromethorphan… FDA đưa ra chống chỉ định sử dụng codein cho trẻ em dưới 12 tuổi, không khuyến cáo sử dụng codein và hydrocodon cho trẻ em dưới 18 tuổi và phụ nữ cho con bú vì những lợi ích mang lại thấp hơn nguy cơ nghiêm trọng về hô hấp, tim mạch và gây nghiện. Nhóm thuốc này còn chống chỉ định với trẻ em cắt amidan, trẻ em mắc chứng ngưng thở khi ngủ, cẩn trọng với bệnh nhân có tiền sử suy hô hấp, hen suyễn, bệnh phổi tắc nghẽn mạn tính (COPD) vì tác dụng không mong muốn ức chế nhẹ trung tâm hô hấp. Không nên sử dụng trong trường hợp ho có đờm cũng như dùng chung với các thuốc long đờm, tiêu đờm. Giảm ho trong trường hợp này khiến đờm ứ đọng trong đường hô hấp, gây nguy cơ tăng nặng tình trạng bệnh lý hô hấp khác.</p>\r\n\r\n<p style=\"text-align:justify\">- Nhóm thuốc tác động long đờm, tiêu đờm: Giúp phản xạ ho dễ dàng tống xuất đờm ra ngoài hơn, được sử dụng cho những trường hợp ho có đờm. Thuốc long đờm có tác dụng làm tăng tiết dịch trên niêm mạc đường hô hấp để làm loãng đờm, gồm terpin, guaifenesin, natribenzoat, eucalyptol… Thuốc tiêu đờm làm thay đổi cấu trúc của đờm và giảm độ nhớt của nó như bromhexin, carbocystein, acetylcystein, ambroxol… Nên uống nhiều nước trong thời gian dùng thuốc, không nên uống thuốc buổi tối vì khi ngủ sẽ bị ứ đọng đờm. Cần thận trọng đối với các bệnh nhân có tiền sử viêm loét dạ dày, có thể tổn hại đến lớp chất nhày bảo vệ niêm mạc dạ dày, làm bệnh tái phát.</p>\r\n\r\n<p style=\"text-align:justify\">- Nhóm thuốc kháng histamin: Đây là nhóm thuốc chống dị ứng, có tác dụng an thần, giảm ho, đồng thời cũng làm giảm dịch tiết nên có thể làm đờm đặc hơn. Nhóm thuốc này thường được sử dụng để trị ho khan do dị ứng, kích ứng, ho kèm sổ mũi. Gồm các hoạt chất diphenhydramin, chlopheniramin, alimemazin, promethazin… Thuốc có tác dụng phụ gây buồn ngủ, vì thế cần thận trọng khi làm việc, vận hành máy móc, lái xe.</p>\r\n\r\n<p style=\"text-align:justify\">- Nhóm các thuốc làm giảm phản xạ ho ở đường hô hấp: Làm dịu cổ họng, làm giảm sự nhạy cảm với kích thích ho ở ngoại biên. Gồm các chất như natri benzoat, glycerol… Menthol, eucalyptol, tinh dầu tràm, tinh dầu tần, mật ong còn có tác dụng sát khuẩn, trị ho rất tốt. Tuy nhiên, cần chú ý tuyệt đối không sử dụng mật ong cho trẻ dưới 1 tuổi vì hệ miễn dịch của trẻ còn rất yếu, nguy cơ ngộ độc độc tố botulism từ bào tử vi khuẩn Clostridium botulinum có thể khiến trẻ tử vong (loại độc tố trong patê chay gần đây mà nhiều người lớn bị ngộ độc, số ít mật ong nhiễm vi khuẩn này từ quá trình thu hoạch). Trường hợp khác, tinh dầu bạc hà hoặc dầu gió xanh, thường được các bậc cha mẹ sử dụng bôi ngoài da, vùng cổ họng cho trẻ, thậm chí nhỏ vào mũi, họng khi trẻ bị ho là điều không nên, vì menthol trong các chế phẩm này gây ức chế hô hấp, có thể làm ngưng thở ở trẻ nhỏ, đặc biệt là trẻ sơ sinh và trẻ có bệnh lý hô hấp.</p>\r\n', 'TEWj3dy3_thuoc-ho-tuy-quen-thuoc-nhung-khong-de-su-dung1608104773.jpg', 1, 0, '2020-12-27', 15, '2020-12-27 15:41:34', '2020-12-27 15:41:34'),
(32, 'Rủi ro tiềm ẩn khi dùng thuốc chống rối loạn nhịp tim', 'rui-ro-tiem-an-khi-dung-thuoc-chong-roi-loan-nhip-tim', '<p><strong>Không phải ai bị rối loạn nhịp tim cũng có thể dùng thuốc chống loạn nhịp. Tùy từng trường hợp bác sĩ sẽ chỉ định dùng liều khẩn cấp hoặc duy trì cho người bệnh. Rối loạn nhịp tim là một bệnh lý hết sức phức tạp, nguy hiểm, cần được các bác sĩ tim mạch thăm khám và điều trị liên tục, nhằm hạn chế các hậu quả không mong muốn.</strong></p>\r\n', '<p style=\"text-align:justify\">Có nhiều nguyên nhân khiến tim của bạn có thể đập quá nhanh hoặc đập không đều. Nếu nguyên nhân là do bạn đang hồi hộp, lo lắng, thì sau vài giây khi bình tĩnh trở lại thì nhịp tim cũng dần ổn định. Nhưng nếu nhịp tim đập nhanh kéo dài do mắc các bệnh lý liên quan đến tim mạch thì chắc chắn cần đến sự trợ giúp của thuốc điều trị rối loạn nhịp tim.</p>\r\n\r\n<p style=\"text-align:justify\">Thuốc chống rối loạn nhịp tim có tác dụng ngăn chặn sự xuất hiện xung điện bất thường trong tim, giúp làm giảm nhịp tim, điều hòa nhịp về trạng thái ổn định. Khi được sử dụng, mục tiêu của thuốc là khôi phục nhịp điệu bình thường - liên tục hoặc trong trường hợp cấp cứu tim. Trong trường hợp loạn nhịp tim mạn tính, những can thiệp dược lý này có thể được kết hợp với các phương pháp điều trị không dùng thuốc.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Không phải ai bị rối loạn nhịp tim cũng phải dùng thuốc.\" src=\"https://media.suckhoedoisong.vn/Images/duylinh/2020/12/20/7_resize.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Không phải ai bị rối loạn nhịp tim cũng phải dùng thuốc.\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Không phải ai bị rối loạn nhịp tim cũng phải dùng thuốc.</em></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Các loại thuốc chống loạn nhịp tim</span></h2>\r\n\r\n<p style=\"text-align:justify\">Các loại thuốc chống loạn nhịp tim khác nhau, có các cơ chế hoạt động khác nhau. Tất cả các loại thuốc này đều làm thay đổi độ dẫn của ion màng, trực tiếp hoặc gián tiếp, sau đó làm thay đổi các đặc điểm vật lý của điện thế hoạt động của tim. Dưới đây là một số loại thuốc chống loạn nhịp tim phổ biến nhất:</p>\r\n\r\n<p style=\"text-align:justify\">Nhóm thuốc chống loạn nhịp (amiodaron, dronedaron, sotalol, propafenon...) có tác dụng kéo dài thời gian trơ của tim và ngăn chặn tính tự động bất thường của nhịp tim.</p>\r\n\r\n<p style=\"text-align:justify\">Nhóm thuốc chẹn beta (metoprolol, atenolol, bisopropol) có tác dụng làm chậm nhịp tim, thư giãn cơ tim nên làm giảm gánh nặng hoạt động của tim và làm giảm dẫn truyền xung điện tim qua nút nhĩ thất.</p>\r\n\r\n<p style=\"text-align:justify\">Nhóm thuốc chẹn kênh canxi (verapamil, diltiazem…) có tác dụng giãn mạch và làm giảm dẫn truyền xung điện tim qua nút nhĩ thất.</p>\r\n\r\n<p style=\"text-align:justify\">Cũng có những thuốc chống loạn nhịp chưa được phân loại, chẳng hạn như adenosine, đạt được tác dụng chống loạn nhịp bằng cách làm chậm sự dẫn truyền của tim (đặc biệt ảnh hưởng đến sự dẫn truyền qua nút nhĩ thất).</p>\r\n\r\n<p style=\"text-align:justify\">Digoxin là một glycoside tim làm tăng sức co bóp cơ tim và giảm dẫn truyền xung điện tim qua nút nhĩ thất.</p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:17px\">Những rủi ro tiềm ẩn</span></h2>\r\n\r\n<p style=\"text-align:justify\">Cũng giống như với bất kỳ loại thuốc nào, thuốc chống loạn nhịp tim có nguy cơ gây ra các tác dụng phụ nghiêm trọng. Một trong những tác dụng phụ phổ biến nhất là chứng rối loạn nhịp tim trở nên nghiêm trọng hơn. Đây cũng là một nghịch lý khi cố gắng dùng thuốc để điều trị chứng loạn nhịp tim.</p>\r\n\r\n<p style=\"text-align:justify\">Những loại thuốc này cũng khá độc với số lượng quá nhiều và quá liều có thể gây nguy hiểm đến tính mạng. Hướng dẫn của Hiệp hội Tim mạch Hoa Kỳ quy định rằng chỉ nên dùng tối đa 2,2gam amiodarone trong vòng 24 giờ. Mức độ độc hại liên quan đến liều lượng không chính xác khác nhau, nhưng các triệu chứng có thể bao gồm: buồn nôn, nôn mửa, tiêu chảy, buồn ngủ và tác dụng kháng cholinergic khi dùng quá liều nhẹ và co giật, hạ huyết áp, nhịp tim chậm, loạn nhịp tim và hôn mê khi dùng quá liều đáng kể.</p>\r\n\r\n<p style=\"text-align:justify\">Thuốc điều trị rối loạn nhịp tim là loại thuốc không thể thiếu trong điều trị các bệnh lý rối loạn tim mạch. Giống như tất cả các loại thuốc Tây y khác, khi sử dụng đều có tác dụng phụ. Vì vậy, việc sử dụng thuốc cần tuân thủ chỉ định và hướng dẫn của bác sĩ để đạt được hiểu quả cao và tránh tác dụng không mong muốn có thể gặp phải. Người bệnh tuyệt đối không được tự ý thay đổi liều hay ngưng sử dụng mà chưa có ý kiến của bác sĩ điều trị. Và điều quan trọng cần nhớ là hãy chăm sóc sức khỏe bản thân thật tốt bằng cách tập thể dục vừa phải, không hút thuốc lá, duy trì cân nặng hợp lý và có chế độ ăn uống cân bằng sẽ giúp cho trái tim của bạn luôn ở trạng thái tốt nhất để ngăn ngừa rối loạn nhịp tim và các bệnh lý tim mạch khác.</p>\r\n', 'Lac9xbU4_rui-ro-tiem-an-khi-dung-thuoc-chong-roi-loan-nhip-tim1608402438.jpg', 1, 0, '2020-12-27', 16, '2020-12-27 15:47:26', '2020-12-27 15:47:26'),
(33, 'ÙNG THUỐC NÊN BIẾT Các phản ứng dị ứng có thể xảy ra khi tiêm vắc-xin COVID-19', 'ung-thuoc-nen-biet-cac-phan-ung-di-ung-co-the-xay-ra-khi-tiem-vac-xin-covid-19', '<p><strong>Cơ quan Quản lý Thực phẩm và Dược phẩm Hoa Kỳ đã nghiên cứu các phản ứng dị ứng có thể xảy ra khi tiêm vắc-xin ngừa COVID-19 và đưa ra các khuyến cáo cho người dân trong việc tiêm loại vắc-xin này.</strong></p>\r\n', '<p style=\"text-align:justify\">Trung tâm Phòng ngừa và Kiểm soát Dịch bệnh Hoa Kỳ (CDC) cho biết trong một tuyên bố hôm 19/12 rằng một số người bị phản ứng dị ứng nghiêm trọng - còn được gọi là \"sốc phản vệ\" - sau khi tiêm vắc-xin ngừa COVID-19.</p>\r\n\r\n<p style=\"text-align:justify\">Trung tâm CDC đã nói rằng bất kỳ ai đã bị phản ứng dị ứng nghiêm trọng sau khi tiêm mũi đầu tiên thì không nên tiêm mũi thứ hai. Theo đó, nếu ai đó đã từng bị phản ứng dị ứng nghiêm trọng với các loại vắc-xin khác hoặc liệu pháp tiêm, nên hỏi ý kiến bác sĩ trước khi quyết định tiêm vắc-xin ngừa COVID-19.</p>\r\n\r\n<p style=\"text-align:justify\">Một bản phân tích đầu tiên về<a href=\"https://suckhoedoisong.vn/the-gioi-chay-dua-tiem-vac-xin-ngua-covid-19-n183898.html\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\">&nbsp;vắc-xin ngừa COVID-19&nbsp;</a>của Pfizer-BioNTech, được Cơ quan Quản lý Thực phẩm và Dược phẩm (FDA) công bố mới đây đã nêu chi tiết những tác dụng phụ đầu tiên của loại vắc-xin này. Để liệt kê các tác dụng phụ, FDA đã dựa vào kết quả thử nghiệm lâm sàng của vắc xin. Thử nghiệm được đề cập bắt đầu vào tháng 7 vừa qua và được thực hiện ở nhiều quốc gia trên 44.000 người. Một nửa số người tham gia được tiêm vắc-xin ngừa COVID-19 và nửa còn lại nhận được giả dược.</p>\r\n\r\n<p style=\"text-align:justify\">Theo FDA, các tác dụng phụ chính của vắc-xin ngừa COVID-19 là phản ứng tại chỗ tiêm (84,1%), mệt mỏi (62,9%), đau đầu ( 55,1%), đau cơ (38,3%), ớn lạnh (31,9%), đau khớp (23,6%) và sốt (14,2%). Những phản ứng toàn thân hoặc cục bộ khác nhau này là những phản ứng phụ vẫn thường xảy ra sau khi tiêm bất kỳ loại vắc xin nào.</p>\r\n\r\n<p style=\"text-align:justify\">Phân tích cho thấy các phản ứng xảy ra thường xuyên hơn sau liều thứ hai của vắc-xin. Các tác dụng phụ thường ít gặp hơn ở người lớn so với những người tham gia trẻ tuổi.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Tiêm vắc-xin ngừa COVID-19 tại Nga\" src=\"https://media.suckhoedoisong.vn/Images/maihuong/2020/12/26/vaccine-covid-19-tiem-tren-nguoi_nqpi1.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"Vắc-xin ngừa COVID-19\" /><em>Tiêm vắc -xin ngừa COVID-19 tại Nga.</em></p>\r\n\r\n<p style=\"text-align:justify\">Một tác dụng phụ khác đó là chứng liệt Bell. Chứng liệt mặt này, đặc trưng bởi sự mất một phần hoặc hoàn toàn chức năng của một số cơ mặt, đã được báo cáo bởi bốn người tham gia đã được tiêm vắc-xin ngừa COVID-19. Theo báo cáo thì từ liều đầu tiên cho đến một tháng sau liều thứ hai, có bốn báo cáo về chứng liệt Bell trong nhóm vắc xin và không có trường hợp nào trong nhóm giả dược. Theo đó, các trường hợp này xảy ra vào 3, 9, 37 và 48 ngày sau khi tiêm chủng. Tác dụng phụ này biến mất ba ngày sau khi xuất hiện, nghĩa là người tham gia sẽ có phản ứng này ba ngày sau khi tiêm vắc-xin. Tuy nhiên, chứng liệt Bell vẫn hiện diện ở ba tình nguyện viên khác.&nbsp;<em>\"Dù được điều trị hay không, phần lớn bệnh nhân sẽ hồi phục hoàn toàn (khỏi chứng liệt Bell) sau vài tháng\"</em>, theo trang Cẩm nang MSD.</p>\r\n\r\n<p style=\"text-align:justify\"><em>\"Sau khi vắc-xin đã được phê duyệt, việc sử dụng vắc-xin ở một số lượng lớn người có thể gây ra các tác dụng phụ bổ sung, có khả năng ít thường xuyên hơn và/hoặc nghiêm trọng hơn không được phát hiện trong thử nghiệm được thực hiện trên 44.000 người tham gia trong giai đoạn theo dõi hiện nay”</em>, các nhà khoa học của FDA cho biết.</p>\r\n', 'm0upZrW5_cac-phan-ung-di-ung-co-the-xay-ra-khi-tiem-vac-xin-covid-191608933188.jpg', 1, 0, '2020-12-27', 16, '2020-12-27 15:47:59', '2020-12-27 15:47:59'),
(34, 'Có thể ăn bưởi khi dùng metformin trị đái tháo đường?', 'co-the-an-buoi-khi-dung-metformin-tri-dai-thao-duong-', '<p><strong>Tôi đang dùng thuốc metformin trị đái tháo đường, nhưng tôi lại rất thích ăn bưởi. Gần đây tôi đọc được thông tin cho rằng, ăn bưởi hoặc nước bưởi trong khi dùng thuốc sẽ nguy hiểm. Xin hỏi, tại sao lại như vậy? Với trường hợp của tôi, uống metformin có thể ăn bưởi được không? Tôi cần phải lưu ý gì khi dùng thuốc? Tôi xin cảm ơn.</strong></p>\r\n', '<p style=\"text-align:justify\">Có thể nói metformin là thuốc thường được bác sĩ kê đơn cho những người mắc bệnh đái tháo đường type 2. Thuốc làm giảm nồng độ glucose trong huyết tương, khi đói và sau bữa ăn, ở người bệnh đái tháo đường type 2 (không phụ thuộc insulin). Một ưu điểm của thuốc là làm giảm sự tăng đường huyết nhưng không gây tai biến hạ đường huyết (trừ trường hợp nhịn đói hoặc phối hợp thuốc hiệp đồng tác dụng). Thuốc có thể dùng một mình (khi chế độ ăn uống đơn thuần không kiểm soát được đường huyết) hoặc dùng phối hợp với các thuốc trị đái tháo đường khác (khi chế độ ăn và khi dùng metformin đơn thuần không có hiệu quả kiểm soát đường huyết một cách đầy đủ.</p>\r\n\r\n<p style=\"text-align:justify\">Nước ép bưởi và bưởi là nguồn cung cấp dinh dưỡng lành mạnh như kali, vitamin C… Cả 2 dưỡng chất này đều là những chất dinh dưỡng cần thiết cho sức khỏe. Tuy nhiên, nước bưởi cũng chứa các hợp chất như furanvitymarin, có thể ngăn chặn chức năng của CYP3A4, một loại enzyme giúp cơ thể chuyển hóa khoảng 50% thuốc. Chặn enzyme này có nghĩa là một số loại thuốc tồn tại trong cơ thể lâu hơn bình thường và tích tụ trong máu, có thể làm tăng nguy cơ ảnh hưởng có hại và trong một số trường hợp, có thể gây tử vong. Tác dụng của furanvitymarin đối với CYP3A4 là không thể đảo ngược, và cơ thể có thể mất khoảng 3 ngày để tạo ra CYP3A4 mới. Vì vậy, thậm chí chỉ cần 200ml nước bưởi, có thể đủ để gây ra sự tương tác nguy hiểm này.</p>\r\n\r\n<p style=\"text-align:justify\">Tuy nhiên, hiện tại không có bằng chứng nào cho thấy việc ăn bưởi có thể ảnh hưởng đến việc sử dụng metformin trị đái tháo đường. Điều này có thể do metformin không bị chuyển hóa ở gan nên không bị ảnh hưởng bởi men chuyển hóa thuốc CYP3A4. Thay vào đó, metformin bài tiết ở ống thận và thải trừ qua nước tiểu.</p>\r\n\r\n<p style=\"text-align:justify\">Khi dùng metformin bạn cần tuân thủ đơn thuốc của bác sĩ, kết hợp với chế độ ăn uống và luyện tập hợp lý mới đạt hiệu quả điều trị tối đa. Trong quá trình dùng thuốc nếu xảy ra bất thường nào cần thông báo cho bác sĩ biết để được khắc phục, xử lý thích hợp…</p>\r\n', 'PAGu9Vid_co-the-an-buoi-khi-dung-metformin-tri-dai-thao-duong1608402661.jpg', 0, 0, '2020-12-27', 17, '2020-12-27 15:49:43', '2020-12-27 15:49:43'),
(35, 'Có nên bôi mỡ tetracyclin trị khô da?', 'co-nen-boi-mo-tetracyclin-tri-kho-da-', '<p><strong>Khi trời lạnh, da tôi thường bị khô nẻ. Tôi đã uống nhiều nước, đắp mặt nạ và dùng kem dưỡng da... Nhưng các biện pháp trên đều không hiệu quả, da vẫn bị bong tróc, nhất là da mặt bị “mốc” khiến tôi rất thiếu tự tin. Có người mách tôi dùng thuốc mỡ tra mắt tetracyclin bôi mặt sẽ hết. Xin cho biết tôi có nên bôi loại thuốc này để trị khô da không?</strong></p>\r\n', '<p style=\"text-align:justify\">Vào mùa lạnh, sự trao đổi chất tăng để duy trì thân nhiệt, vì vậy nhu cầu về nước theo đó tăng cao. Lớp da bên ngoài bị ảnh hưởng bởi gió lạnh khô hanh, nên da thường khô, dễ nứt nẻ, bong tróc. Da mặt mỏng nhất và dễ bị khô nhất, vì vậy ngoài các biện pháp như bạn đã dùng thì bạn nên chườm khăn ẩm nhưng không được ấm nóng, cứ khoảng 2-3 tiếng bạn nên lau nhẹ và đắp khăn ẩm lên mặt khoảng 1-2 phút. Về da toàn bộ cơ thể, bạn không nên dùng xà&nbsp; phòng và nước nóng để tắm (chỉ tắm nước vừa&nbsp; đủ ẩm để tránh bị lạnh). Nên sử dụng sửa tắm dưỡng ẩm phù hợp với da.</p>\r\n\r\n<p style=\"text-align:justify\">Bạn đừng quá nóng vội khi da khô mà tiến hành dùng thuốc không theo chỉ định của bác sĩ, vì rất dễ sử dụng nhầm các thuốc điều trị bệnh lý ở da, không có tác dụng chữa nẻ dẫn đến hậu quả tệ hại như teo da, rộp da... Đối với thuốc mỡ tetracyclin 1%, dùng để điều trị các bệnh nhiễm khuẩn ở mắt, không dùng để chữa nẻ.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/nguyenkhanh/2020/12/24/boi_mo.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p style=\"text-align:justify\">Thuốc mỡ tetracyclin là sự phân tán của kháng sinh tetracyclin trong hỗn hợp tá dược thuốc mỡ thân dầu. Những tá dược này không tan trong nước vì thế khi bôi lên da có tác dụng giữ nước, làm mềm da, tránh khô da. Do đó mà người ta thấy sau khi bôi thuốc mỡ tetracyclin sẽ làm hết nẻ nên lầm tưởng thuốc này có tác dụng chữa nẻ. Thực tế là tetracyclin không có vai trò gì trong việc chữa nẻ, thậm chí còn gây hại. Bôi kháng sinh tetracyclin dài ngày, nhất là vùng da bị nẻ rất dễ gây ngấm thuốc có thể gây tác dụng phụ toàn thân.</p>\r\n\r\n<p style=\"text-align:justify\">Với tình trạng khô da, nứt nẻ vào mùa đông có thể giải quyết hiệu quả bằng nhiều loại sáp nẻ, kem dưỡng ẩm. Kem bôi da vaselin trên thị trường hiện nay rất phong phú, nhiều loại, đây là loại kem bôi nẻ khá hiệu quả. Ngoài ra người dùng có thể lựa chọn các loại kem dưỡng ẩm có bổ sung collagen, vitamin E có tác dụng rất tốt, vừa giữ ẩm lại vừa bổ sung dưỡng chất cho da.</p>\r\n', '55Jxju1S_co-nen-boi-mo-tetracyclin-tri-kho-da1608822032.jpg', 1, 0, '2020-12-27', 17, '2020-12-27 15:50:18', '2020-12-27 15:50:18'),
(36, 'Vì sao viêm âm đạo xử lý mãi không dứt?', 'vi-sao-viem-am-dao-xu-ly-mai-khong-dut-', '<p><strong>Viêm âm đạo là bệnh phụ khoa khá phổ biến ở phụ nữ đã sinh hoạt tình dục và trong độ tuổi sinh đẻ. Viêm âm đạo gây ảnh hưởng lớn đến sinh hoạt và sức khỏe sinh sản của chị em. Căn bệnh này lại khá dai dẳng, khó dứt. Vậy giải pháp nào cho tình trạng này?</strong></p>\r\n', '<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/quangcao/2020/12/03/SKDS_VAD_2-image_1.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>(Ảnh minh họa)</em></p>\r\n\r\n<p><strong>Điểm danh những tác nhân khiến phụ nữ bị viêm âm đạo</strong></p>\r\n\r\n<p>Viêm âm đạo là tình trạng âm đạo bị nhiễm khuẩn dẫn đến tiết dịch nhiều, ngứa và đau rát do mất cân bằng hệ nấm men và vi khuẩn sống trong âm đạo.</p>\r\n\r\n<p>Nguyên nhân thường gặp là do: nấm Candida Albicans, ký sinh trùng Trùng roi Trichomonas, tạp khuẩn, vi khuẩn Gram âm, Chlamydia, … Viêm âm đạo có thể kéo theo nhiều bệnh phụ khoa khác, như viêm lộ tuyến cổ tử cung, viêm phần phụ…</p>\r\n\r\n<p><strong>Các biểu hiện nhận diện bệnh viêm âm đạo gồm:</strong></p>\r\n\r\n<p>- Khí hư bất thường, có thể ra rất nhiều hoặc rất ít,&nbsp;<a href=\"https://thegioiduocvinhgia.vn/huyet-trang-khi-hu/mau-trang-duc\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\">khí hư có màu trắng đục</a>, vàng xanh, lợn cợn như bã đậu, có thể có bọt hoặc lẫn máu,… kèm thêm mùi hôi khó chịu.</p>\r\n\r\n<p>- Ngứa âm đạo, hoặc bị dị ứng</p>\r\n\r\n<p>- Vùng kín sưng đỏ, tiểu buốt, tiểu rát</p>\r\n\r\n<p>- Đau, rát hoặc có thể chảy máu khi quan hệ tình dục…</p>\r\n\r\n<p><strong>Tại sao viêm âm đạo dai dẳng, dễ tái phát</strong><strong>?</strong></p>\r\n\r\n<p>Viêm âm đạo dễ tái phát là do độ pH âm đạo bị thay đổi và chưa được cân bằng trở lại. Thực tế, căn bệnh này thường khó trị dứt điểm được do:</p>\r\n\r\n<p><strong><em>Dùng thuốc kháng sinh thời gian dài hoặc lạm dụng kháng sinh:</em></strong>&nbsp;Nếu dùng thời gian dài hoặc lạm dụng, thuốc kháng sinh vô tình diệt luôn cả vi khuẩn có lợi. Do đó, môi trường pH âm đạo bị thay đổi, khiến các vi khuẩn, virus phát triển, dễ dàng xâm nhập, gây bệnh trở lại.</p>\r\n\r\n<p><strong><em>Vệ sinh không đúng cách:</em></strong>&nbsp;Vệ sinh qua loa hoặc thụt rửa sâu cũng sẽ khiến bệnh chữa mãi không khỏi.</p>\r\n\r\n<p><strong><em>Suy giảm, rối loạn miễn dịch:</em></strong>&nbsp;Khi hệ miễn dịch của cơ thể bị suy giảm, nấm, hại khuẩn sẽ lấn át lợi khuẩn, làm cho tình trạng viêm nhiễm âm đạo khó dứt điểm.</p>\r\n\r\n<p><strong><em>Điều trị không đúng cách:</em></strong>&nbsp;Không theo phác đồ trị liệu của bác sĩ mà tự xử lý tại nhà cũng là nguyên nhân khiến bệnh viêm âm đạo dai dẳng.</p>\r\n\r\n<p><strong><em>Tình dục không an toàn:</em></strong>&nbsp;<a href=\"https://thegioiduocvinhgia.vn/viem-am-dao/nhiem-nam-am-dao\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\">Nấm âm đạo</a>, Chlamydia hay do trùng roi là những dạng có thể lây qua đường quan hệ tình dục. Khi quan hệ không an toàn, vi khuẩn, vi trùng tồn tại ở cơ quan sinh dục nam giới sẽ lại tiếp tục lây truyền sang nữ giới.</p>\r\n\r\n<p><strong>Sự ảnh hưởng của viêm âm đạo dai dẳng kéo dài đến sức khỏe chị em</strong></p>\r\n\r\n<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/quangcao/2020/12/03/SKDS_VAD_2-image_2.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Ảnh minh hoạ</em></p>\r\n\r\n<p>Bệnh viêm âm đạo nếu không được xử lý kịp thời và triệt để thì có thể gây ra nhiều biến chứng nguy hiểm ảnh hưởng đến sinh hoạt, sức khỏe, đặc biệt là khả năng làm mẹ của chị em, như:</p>\r\n\r\n<p>-&nbsp; Dễ dẫn đến tình trạng lây lan ngược các bộ phận bên trong cơ quan sinh sản, gây viêm nhiễm cổ tử cung, vòi trứng, buồng trứng… làm khó thụ thai, ảnh hưởng đến việc sinh đẻ, nghiêm trọng hơn có thể bị vô sinh.</p>\r\n\r\n<p>-&nbsp; Gây bất tiện trong sinh hoạt hằng ngày, khiến chị em cảm thấy tự ti, giảm ham muốn, ngại “yêu”, làm ảnh hưởng đến hạnh phúc hôn nhân.</p>\r\n\r\n<p>-&nbsp; Ảnh hưởng trực tiếp đến quá trình sinh đẻ, nếu bị viêm nhiễm từ trước, người mẹ sau sinh có thể bị nhiễm khuẩn hậu sản, nặng hơn là nhiễm trùng tử cung, viêm khúc mạc khi đẻ.</p>\r\n\r\n<p>-&nbsp; Có nguy cơ ảnh hưởng trực tiếp đến trẻ sơ sinh, vì lúc sinh, trẻ đi qua đường âm đạo của người mẹ có nhiễm khuẩn, nấm, virus nên bé dễ mắc các chứng như tưa lưỡi, viêm tuyến lệ, viêm giác mạc…</p>\r\n\r\n<p><strong>Cách xử lý viêm âm đạo tại nhà</strong></p>\r\n\r\n<p>Để xử lý bệnh viêm âm đạo, bác sĩ thường kê thuốc diệt mầm bệnh (dạng thuốc đặt, thuốc uống, thuốc bôi) kết hợp sản phẩm vệ sinh phù hợp. Với những người mà nguyên nhân gây bệnh là trùng roi âm đạo, nấm … thì cần kết hợp xử lý cho cả chồng (bạn tình) để trị bệnh hiệu quả và triệt để. Tuy nhiên nếu chị em không làm theo đúng chỉ dẫn, hoặc làm không đủ liệu trình thì vẫn có thể bị tái phát.</p>\r\n\r\n<p>Các chuyên gia sản phụ khoa khuyến cáo, để tránh bị mạn tính, tái phát, dai dẳng khi mắc viêm âm đạo, điều quan trọng là phải cân bằng được độ pH âm đạo. Tiếp theo là phải tăng sức đề kháng của cơ thể. Đồng thời, nên sử dụng viên uống có thành phần thảo dược có tác dụng hỗ trợ cải thiện triệu chứng viêm âm đạo hiệu quả, an toàn.</p>\r\n\r\n<p>Chị em nên chọn sản phẩm viên uống chứa các thành phần thảo dược trong viêm nhiễm phụ khoa có tác dụng hỗ trợ diệt khuẩn, làm lành tổn thương do viêm, giúp cân bằng pH âm đạo, kiểm soát dịch vùng kín, tăng cường sức đề kháng. Nhờ đó giúp ngăn chặn tình trạng viêm âm đạo tái đi tái lại, dai dẳng.</p>\r\n\r\n<p>Vệ sinh vùng kín hàng ngày sạch sẽ cũng là việc vô cùng quan trọng. Sản phẩm vệ sinh chuyên dụng có pH=(4-6), nano bạc, tinh chất chè xanh, tinh dầu bạc hà sẽ giúp cân bằng pH vùng kín, ngăn ngừa viêm nhiễm, nấm ngứa và khử mùi hôi, không gây kích ứng.</p>\r\n\r\n<p>Ngoài ra, chị em cũng lưu ý duy trì chế độ nghỉ ngơi điều độ, ăn uống đầy đủ dinh dưỡng và quan hệ tình dục an toàn để bệnh sớm khỏi, ngăn ngừa tái phát.</p>\r\n', 'vCJoLUfT_vi-sao-viem-am-dao-chua-mai-khong-khoi1606962464.jpg', 1, 0, '2020-12-27', 18, '2020-12-27 15:51:10', '2020-12-27 15:51:10'),
(37, 'Tư vấn tăng chiều cao cho học sinh tại khu vực phía Nam', 'tu-van-tang-chieu-cao-cho-hoc-sinh-tai-khu-vuc-phia-nam', '<p><strong>Thành phố Hồ Chí Minh, Đồng Nai, Bình Dương là 3 tỉnh thành mà nhãn hàng Vipteen – viên uống hỗ trợ tăng chiều cao cho trẻ từ 09 -18 tuổi của Cty CP Dược phẩm Vinh Gia lựa chọn để thực hiện chuỗi hoạt động thăm khám, tư vấn nhằm thực hiện sứ mệnh: “Nâng tầm cao và trí tuệ Việt”.</strong></p>\r\n', '<p><img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/quangcao/2020/12/24/vipteen_1920x1080_2.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p>Chuỗi hoạt động Chương trình tư vấn tăng chiều cao, chăm sóc sức khỏe thể chất và trí não cho trẻ trong độ tuổi đến trường chính thức khởi động từ ngày 04/01/2021.</p>\r\n\r\n<p>Sự phát triển thể chất và trí tuệ của trẻ em không chỉ là mối quan tâm hàng đầu ở mỗi gia đình mà còn là của toàn xã hội. Chính phủ đưa ra đề án tổng thể phát triển thể lực, tầm vóc người Việt Nam với mục tiêu tăng chiều cao, nâng tầm vóc người Việt. Hàng loạt các hoạt động góp sức vào mục tiêu chung này được các doanh nghiệp, chính quyền phối hợp thực hiện như Chương trình sữa học đường, các hoạt động thể chất, dinh dưỡng…</p>\r\n\r\n<p>Nhận thấy tầm quan trọng của dưỡng chất trong công cuộc cải thiện vóc dáng trẻ em Việt, nhãn hàng viên uống hỗ trợ tăng chiều cao Vipteen Vinh Gia phối hợp cùng các trường trung học cơ sở tại địa bàn 3 tỉnh thành là Tp Hồ Chí Minh, Đồng Nai và Bình Dương tổ chức hoạt động thăm khám, tư vấn tăng chiều cao, chăm sóc sức khỏe cho học sinh ngay tại trường học.</p>\r\n\r\n<p>Dự kiến chương trình diễn ra trong 2 đợt: Đợt 1 tại TP.HCM từ ngày 04.01.2021 đến 04.02.2021 và được tiếp diễn tại TP.HCM, Đồng Nai, Bình Dương sau kỳ nghỉ Tết Nguyên đán Tân Sửu 2021.</p>\r\n\r\n<p>Theo đó, tại chương trình học sinh sẽ được các chuyên gia là các dược sĩ, bác sĩ tư vấn miễn phí giải pháp toàn diện, kết hợp bộ 3 biện pháp: Dinh dưỡng - rèn luyện thể thao - chế độ sinh hoạt để phát triển thể lực, trí lực và cả vóc dáng một cách toàn diện nhất.</p>\r\n\r\n<p>Chương trình mong nhận được sự ủng hộ và quan tâm của đông đảo các bậc cha mẹ cùng các em học sinh.</p>\r\n\r\n<p>Lịch khám và tư vấn dự kiến đợt 1 tại HCM như sau :</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"background:rgb(246, 246, 246); border-collapse:collapse; border-radius:10px; border-spacing:0px; border:1px solid rgb(204, 204, 204); font-family:noto serif; font-size:17px; font-stretch:inherit; font-variant-east-asian:inherit; font-variant-numeric:inherit; line-height:26px; margin:auto; padding:15px; text-rendering:geometricprecision; vertical-align:baseline\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p><strong>TT</strong></p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p><strong>Trường</strong></p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p><strong>Địa chỉ</strong></p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p><strong>Quận</strong></p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p><strong>Ngày thực hiện</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>1</p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>THCS PHẠM HỮU LẦU</p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>Khu tái định cư Phú Mỹ, Khu phố 2, Phạm Hữu Lầu, Phú Mỹ</p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>7</p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>4 - 5/1/2021</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>2</p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>THCS NGUYỄN HIỆN</p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>66 Số 1, Bình Thuận, Quận 7, Hồ Chí Minh</p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>7</p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>6 - 7/1/2021</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>3</p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>THCS PHÚ THỌ</p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>1122 Ba Tháng Hai , Phường 6</p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>11</p>\r\n			</td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; text-align:center; vertical-align:baseline\">\r\n			<p>8 - 9/1/2021</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"5\" style=\"border-color:rgb(221, 221, 221) !important; vertical-align:baseline\">\r\n			<p><em>Danh sách các trường sẽ được cập nhật thêm…</em></p>\r\n\r\n			<div>&nbsp;</div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 'zCIFUeDw_tu-van-tang-chieu-cao-cho-hoc-sinh-tai-khu-vuc-phia-nam-dot-1-2021-1608801090.jpg', 1, 0, '2020-12-27', 18, '2020-12-27 15:51:58', '2020-12-27 15:51:58'),
(38, 'Quy trình khám chữa bệnh', 'quy-trinh-kham-chua-benh', '<p><strong>Lấy số thứ tự để đăng ký khám bệnh ở quầy đón tiếp (xác định mục đích khám), mua sổ khám bệnh, điền đầy đủ thông tin (họ tên, ngày sinh, nơi ở).</strong></p>\r\n', '<p>1.Lấy số thứ tự để đăng ký khám bệnh ở quầy đón tiếp (xác định mục đích khám), mua sổ khám bệnh, điền đầy đủ thông tin (họ tên, ngày sinh, nơi ở).</p>\r\n\r\n<p>2.Ngồi ở khu vực chờ khám bệnh đợi nhân viên y tế đọc số thứ tự khám.</p>\r\n\r\n<p>3.Quan sát sơ đồ, bảng chỉ dẫn tại bộ phận phòng khám, chờ đến lượt làm thủ tục đăng ký và nộp tiền khám bệnh.</p>\r\n\r\n<p>4.Khi có phiếu khám bệnh và biên lai thu tiền khám, đến các phòng khám tổng quát theo chỉ dẫn có ghi trên phiếu khám và sự hướng dẫn của nhân viên y tế.</p>\r\n\r\n<p>5.Nộp phiếu khám bệnh tại phòng khám tổng quát theo số thứ tự.</p>\r\n\r\n<p>6.Bác sỹ chỉ định cần làm xét nghiệm cận lâm sàng nào thuộc chuyên khoa gì (siêu âm, hay chụp X-quang… ). Bệnh nhân quay lại quầy đón tiếp và thu ngân để nộp tiền xét nghiệm.</p>\r\n\r\n<p>7.Nộp phiếu thử máu, siêu âm, chụp X-quang… tại các khu vực xét nghiệm và chờ đến lượt để được thực hiện. Ngồi chờ, lấy kết quả các xét nghiệm cận lâm sàng.</p>\r\n\r\n<p>8.Trở lại phòng khám tổng quát để bác sỹ kết luận, hướng dẫn cách điều trị, kê đơn thuốc và căn dặn, hoặc phải nhập viện (nếu cần), hẹn lịch tái khám.</p>\r\n\r\n<p>9.Theo đơn thuốc bác sĩ đã kê, ra quầy bán thuốc của Bệnh viện (phòng khám) mua thuốc điều trị.</p>\r\n', 'Gg7bTnKm_kham-suc-khoe-1444294085023.jpg', 0, 0, '2020-12-27', 19, '2020-12-27 15:52:57', '2020-12-27 15:52:57'),
(39, 'Khám tầm soát', 'kham-tam-soat', '<p><strong>Nếu việc khám tổng quát cho những kết quả sơ lược, theo dõi định kỳ thì khám tầm soát (KTS) với việc khám tỉ mỉ, chuyên biệt,</strong></p>\r\n', '<p>Nếu việc khám tổng quát cho những kết quả sơ lược, theo dõi định kỳ thì khám tầm soát (KTS) với việc khám tỉ mỉ, chuyên biệt, chuyên sâu sẽ giúp xác định và đánh giá nguy cơ các bệnh lý nguy hiểm tiềm ẩn vào từng giai đoạn (Tầm soát ung thư). Điều này giúp cho việc xác định các phương pháp chẩn đoán kế tiếp để xác nhận chẩn đoán và tiến hành điều trị.</p>\r\n\r\n<p>KTS có vai trò quan trọng trong việc chủ động phòng chống bệnh tật hiệu quả ở các độ tuổi khác nhau, nhất là việc tầm soát và phát hiện sớm các khối u toàn thân.</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; display: inline-block; width: 660px; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\"><img alt=\"Khám tầm soát\" id=\"img_182709\" src=\"http://skds3.vcmedia.vn/k:thumb_w/640/2015/kham-tam-soat-1444294795739/kham-tam-soat.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n\r\n<p><strong>QUY TRÌNH TẦM SOÁT CÁC ĐỘ TUỔI</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; display: inline-block; width: 660px; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\"><img alt=\"Khám tầm soát\" id=\"img_182710\" src=\"http://skds3.vcmedia.vn/k:thumb_w/640/2015/kham-tam-soat1-1444294795783/kham-tam-soat.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>Lưu ý: Các gói tầm soát được thiết kế theo yêu cầu nhằm phù hợp với nguy cơ mắc bệnh của mỗi cá nhân (tư vấn di truyền, người có nguy cơ cao...)</p>\r\n', 'QWG3pZ2w_kham-tam-soat-ava-1444294795851.jpg', 0, 0, '2020-12-27', 19, '2020-12-27 15:53:56', '2020-12-27 15:53:56');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `NgayDang`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(40, 'Để có cơ bụng săn chắc', 'de-co-co-bung-san-chac', '<p><strong>Mỗi ngày chỉ cần bỏ ra 15 phút để tập những động tác dưới đây, chỉ sau một thời gian ngắn kiên trì tập luyện, bạn sẽ có vòng 2 như ý.</strong></p>\r\n', '<p><strong>Bài tập bụng trên</strong></p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; display: inline-block; width: 660px; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\"><img alt=\"Để có cơ bụng săn chắc\" id=\"img_183453\" src=\"http://skds3.vcmedia.vn/k:2015/bung-san-chac-1444623247893/de-co-co-bung-san-chac.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n\r\n<p><strong>Động tác 1:</strong>&nbsp;Nằm thẳng, hai tay đan để sau gáy, hai chân duỗi thẳng, nhịp một bật vai thấp kết hợp với thở bằng miệng, sau đó nhịp hai hạ vai trở về vị trí cũ đồng thời hít sâu bằng mũi. Thực hiện 12 lần.</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; display: inline-block; width: 660px; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\"><img alt=\"Để có cơ bụng săn chắc\" id=\"img_183454\" src=\"http://skds3.vcmedia.vn/k:2015/bung-san-chac1-1444623247902/de-co-co-bung-san-chac.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n\r\n<p><strong>Động tác 2:</strong>&nbsp;Nằm thẳng, hai tay duỗi trên đầu, nhịp một bật vai và tay xoay qua bên trái đồng thời nâng co chân trái, kết hợp thở bằng miệng, nhịp hai trở về vị trí ban đầu, hít sâu bằng mũi, sau đó đổi bên. Thực hiện 12 lần.</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; display: inline-block; width: 660px; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\"><img alt=\"Để có cơ bụng săn chắc\" id=\"img_183455\" src=\"http://skds3.vcmedia.vn/k:2015/bung-san-chac2-1444623247911/de-co-co-bung-san-chac.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n\r\n<p><strong>Động tác 3:&nbsp;</strong>Nằm thẳng, hai tay duỗi trên đầu, nhịp một bật vai lên cao, đồng thời co hai chân song song với mặt đất, hai tay chạm cổ chân, thở mạnh bằng miệng. Nhịp hai hít sâu bằng mũi, trở về vị trí ban đầu. Thực hiện 12 lần.</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; display: inline-block; width: 660px; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\"><img alt=\"Để có cơ bụng săn chắc\" id=\"img_183456\" src=\"http://skds3.vcmedia.vn/k:2015/bung-san-chac4-1444623247925/de-co-co-bung-san-chac.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n\r\n<p><strong>Bài tập bụng dưới</strong></p>\r\n\r\n<p><strong>Động tác 4:&nbsp;</strong>Nằm thẳng, tư thế chuẩn bị hai tay đan sau gáy, nhấc vai lên. Nhịp một hai chân co xéo qua bên trái, đồng thời khuỷu tay trái chạm gối chân phải, kết hợp thở mạnh bằng miệng. Nhịp hai trở về vị trí ban đầu, hít sâu bằng mũi, sau đó đổi bên. Thực hiện 12 lần.</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; display: inline-block; width: 660px; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\"><img alt=\"Để có cơ bụng săn chắc\" id=\"img_183457\" src=\"http://skds3.vcmedia.vn/k:2015/bung-san-chac4-1444623247925/de-co-co-bung-san-chac.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n\r\n<p><strong>Động tác 5:</strong>&nbsp;Nằm thẳng, tư thế chuẩn bị hai tay đan sau gáy, nhấc vai lên, hai chân duỗi thẳng. Nhịp một nâng hai chân lên 900 xéo qua bên trái, đồng thời khuỷu tay trái chạm gối chân phải, kết hợp thở mạnh bằng miệng. Nhịp hai trở về vị trí ban đầu, hít sâu bằng mũi sau đó đổi bên. Thực hiện 12 lần.</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; display: inline-block; width: 660px; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\"><img alt=\"Để có cơ bụng săn chắc\" id=\"img_183458\" src=\"http://skds3.vcmedia.vn/k:2015/bung-san-chac5-1444623247934/de-co-co-bung-san-chac.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n\r\n<p><strong>Động tác 6:</strong>&nbsp;Nằm thẳng, tư thế chuẩn bị hai tay đan sau gáy, nhấc vai lên, hai chân nâng lên 450. Nhịp một nâng hai chân lên 900 kết hợp thở mạnh bằng miệng. Nhịp hai đưa hai chân trở về vị trí ban đầu 450, hít sâu. Thực hiện 12 lần.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" dir=\"center\" id=\"ObjectBoxContent_1444623442656\" style=\"margin: 0px auto; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 1px solid rgb(153, 153, 153); font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; background: rgb(230, 230, 250); float: none; width: 594px;\">\r\n<p><strong>Chú ý:</strong>&nbsp;Để làm giảm các lớp mỡ nhanh chóng, cần đi bộ hoặc chạy bộ từ 30-60 phút/ngày hoặc bơi lội 2 buổi/tuần (mỗi lần khoảng 40 phút). Để đạt hiệu quả, việc tập luyện phải thường xuyên và đủ thời gian, bên cạnh đó phải kết hợp với chế độ ăn uống hợp lý.</p>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" id=\"VCFootNews1444623445346\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline;\">\r\n<div class=\"VCObjectBoxRelatedNewsContentWrapper\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">\r\n<table style=\"background:rgb(246, 246, 246); border-collapse:collapse; border-radius:10px; border-spacing:0px; border:1px solid rgb(204, 204, 204); display:inline-block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; line-height:26px; margin:auto; padding:15px; text-rendering:geometricprecision; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; vertical-align:baseline; width:206.667px\"><a href=\"http://suckhoedoisong.vn/ren-luyen/bai-tap-cho-nguoi-gia-201510121112525.htm\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\"><img alt=\"Bài tập cho người già\" src=\"http://skds3.vcmedia.vn/k:zoom/200_150/2015/bai-tap-cho-nguoi-gia2-1444622859983/de-co-co-bung-san-chac.jpg\" style=\"border:0px; box-sizing:border-box; display:block; float:left; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:200px; min-width:150px; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></a><a href=\"http://suckhoedoisong.vn/ren-luyen/bai-tap-cho-nguoi-gia-201510121112525.htm\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\">Bài tập cho người già</a></td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; vertical-align:baseline; width:206.667px\"><a href=\"http://suckhoedoisong.vn/ren-luyen/bai-tap-de-tieu-hoa-thuc-an-nhanh-20151012111015217.htm\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\"><img alt=\"Bài tập để tiêu hoá thức ăn nhanh\" src=\"http://skds3.vcmedia.vn/k:zoom/200_150/2015/tieu-hoa-thuc-an1-1444622860030/de-co-co-bung-san-chac.jpg\" style=\"border:0px; box-sizing:border-box; display:block; float:left; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:200px; min-width:150px; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></a><a href=\"http://suckhoedoisong.vn/ren-luyen/bai-tap-de-tieu-hoa-thuc-an-nhanh-20151012111015217.htm\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\">Bài tập để tiêu hoá thức ăn nhanh</a></td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; vertical-align:baseline; width:206.667px\"><a href=\"http://suckhoedoisong.vn/ren-luyen/aerobic-2015101211031351.htm\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\"><img alt=\"Aerobic\" src=\"http://skds3.vcmedia.vn/k:zoom/200_150/2015/erobic1a-1444622482202-crop1444622491246p/de-co-co-bung-san-chac.jpg\" style=\"border:0px; box-sizing:border-box; display:block; float:left; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:200px; min-width:150px; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></a><a href=\"http://suckhoedoisong.vn/ren-luyen/aerobic-2015101211031351.htm\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; color: rgb(17, 131, 228); outline: 0px;\">Aerobic</a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n</div>\r\n', 'TyZkzdH9_bung-san-chac-1444623247893.jpg', 1, 0, '2020-12-27', 20, '2020-12-27 15:55:10', '2020-12-27 15:55:10'),
(41, 'Bài tập để tiêu hoá thức ăn nhanh', 'bai-tap-de-tieu-hoa-thuc-an-nhanh', '<p><strong>Suckhoedoisong.vn - Một số thức ăn cần nhiều thời gian để tiêu hóa, do vậy vài phút với những động tác sau sẽ rất tốt cho việc thúc đẩy tiêu hóa trong ngày hoặc buổi tối trước khi ngủ.</strong></p>\r\n', '<div class=\"d-flex align-items-center mb30\" style=\"margin-top: 0px; margin-right: 0px; margin-left: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit; font-family: Roboto, Arial, Helvetica, sans-serif; vertical-align: baseline; display: flex; flex-wrap: wrap; position: relative; align-items: center; color: rgb(45, 45, 45); margin-bottom: 30px !important;\"><a class=\"thumb60x60 thumbblock radius100\" href=\"https://suckhoedoisong.vn/-e/\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font: inherit; vertical-align: baseline; text-decoration-line: none; color: rgb(45, 45, 45); display: block; overflow: hidden; position: relative; width: 60px; border-radius: 100%; height: 60px !important;\"><img src=\"https://suckhoedoisong.vn/pc2019/static/images/no_photo.jpg\" style=\"border:0px; box-sizing:border-box; font:inherit; margin:0px; max-width:100%; min-width:100%; object-fit:cover; padding:0px; text-rendering:geometricprecision; vertical-align:middle\" /></a>\r\n<div class=\"flex-one ml20\" style=\"margin: 0px 0px 0px 20px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font: inherit; vertical-align: baseline; -webkit-box-flex: 1; flex: 1 1 0%;\">\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<div id=\"content_detail_news\" style=\"margin: 0px 0px 30px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline;\">\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; display: inline-block; width: 660px; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\"><img alt=\"Bài tập để tiêu hoá thức ăn nhanh\" id=\"img_183437\" src=\"http://skds3.vcmedia.vn/k:2015/tieu-hoa-thuc-an-1444622860017/bai-tap-de-tieu-hoa-thuc-an-nhanh.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>1. Nằm úp thẳng trên mặt nền cứng chẳng hạn như sàn nhà (không nằm trên giường hoặc ghế).</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; display: inline-block; width: 660px; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\"><img alt=\"Bài tập để tiêu hoá thức ăn nhanh\" id=\"img_183438\" src=\"http://skds3.vcmedia.vn/k:2015/tieu-hoa-thuc-an1-1444622860030/bai-tap-de-tieu-hoa-thuc-an-nhanh.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>2. Tiếp tục gập đầu gối, hai tay giữ lấy cổ chân.</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; display: inline-block; width: 660px; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\"><img alt=\"Bài tập để tiêu hoá thức ăn nhanh\" id=\"img_183439\" src=\"http://skds3.vcmedia.vn/k:2015/tieu-hoa-thuc-an2-1444622860042/bai-tap-de-tieu-hoa-thuc-an-nhanh.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" title=\"\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>3. Ngẩng đầu, rướn cổ về phía trước.</p>\r\n\r\n<p>4. Lắc lư lưng và chuyển động giống như một con ngựa bập bênh.</p>\r\n\r\n<p>5. Đếm đến 20 và nghỉ ngơi. Trở lại các bước trên một vài lần.</p>\r\n\r\n<p>6. Chuyển động lắc lư giúp dạ dày bé tiêu hóa thức ăn nhanh hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" dir=\"center\" id=\"ObjectBoxContent_1444622948245\" style=\"margin: 0px auto; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 1px solid rgb(153, 153, 153); font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; background: rgb(230, 230, 250); float: none; width: 594px;\">\r\n<p><strong>Tập nhai khi trẻ mọc răng:</strong></p>\r\n\r\n<p>Khi nhai, răng cửa và răng hàm hoạt động để cắt và nghiền thức ăn, trộn thức ăn với nước bọt chứa men tiêu hóa giúp quá trình tiêu hóa và hấp thu tốt hơn. Vậy nên:</p>\r\n\r\n<p>+ Tập cho trẻ tự cắn một số thứ mềm (chuối, đu đủ, khoai lang, trứng luộc..).</p>\r\n\r\n<p>+ Băm, nghiền thức ăn (không xay) các loại thịt, tôm, cá từ rất nhỏ đến nhỏ vừa, rồi thái lát nhỏ.</p>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" id=\"VCFootNews1444622822715\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">\r\n<div class=\"VCObjectBoxRelatedNewsContentWrapper\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">\r\n<table style=\"background:rgb(246, 246, 246); border-collapse:collapse; border-radius:10px; border-spacing:0px; border:1px solid rgb(204, 204, 204); display:inline-block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; line-height:26px; margin:auto; padding:15px; text-rendering:geometricprecision; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; vertical-align:baseline; width:206.667px\"><a href=\"http://suckhoedoisong.vn/ren-luyen/aerobic-2015101211031351.htm\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\"><img alt=\"Aerobic\" src=\"http://skds3.vcmedia.vn/k:zoom/200_150/2015/erobic1a-1444622482202-crop1444622491246p/bai-tap-de-tieu-hoa-thuc-an-nhanh.jpg\" style=\"border:0px; box-sizing:border-box; display:block; float:left; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:200px; min-width:150px; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></a><a href=\"http://suckhoedoisong.vn/ren-luyen/aerobic-2015101211031351.htm\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\">Aerobic</a></td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; vertical-align:baseline; width:206.667px\"><a href=\"http://suckhoedoisong.vn/ren-luyen/tap-the-hinh-20151012110028911.htm\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\"><img alt=\"Tập thể hình\" src=\"http://skds3.vcmedia.vn/k:zoom/200_150/2015/tap-the-hinh-1444622141896/bai-tap-de-tieu-hoa-thuc-an-nhanh.jpg\" style=\"border:0px; box-sizing:border-box; display:block; float:left; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:200px; min-width:150px; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></a><a href=\"http://suckhoedoisong.vn/ren-luyen/tap-the-hinh-20151012110028911.htm\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\">Tập thể hình</a></td>\r\n			<td style=\"border-color:rgb(221, 221, 221) !important; vertical-align:baseline; width:206.667px\"><a href=\"http://suckhoedoisong.vn/ren-luyen/4-luu-y-sau-khi-tap-the-thao-20151012105400525.htm\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; text-decoration-line: none; color: rgb(17, 131, 228);\"><img alt=\"4 lưu ý sau khi tập thể thao\" src=\"http://skds3.vcmedia.vn/k:zoom/200_150/2015/4-luu-y-khi-luyen-tap-1444621420857-crop1444621952749p/bai-tap-de-tieu-hoa-thuc-an-nhanh.jpg\" style=\"border:0px; box-sizing:border-box; display:block; float:left; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:200px; min-width:150px; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></a><a href=\"http://suckhoedoisong.vn/ren-luyen/4-luu-y-sau-khi-tap-the-thao-20151012105400525.htm\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline; color: rgb(17, 131, 228); outline: 0px;\">4 lưu ý sau khi tập thể thao</a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n</div>\r\n</div>\r\n', 'CT8zwmMm_tieu-hoa-thuc-an1-1444622860030.jpg', 1, 0, '2020-12-27', 20, '2020-12-27 15:56:08', '2020-12-27 15:56:08'),
(42, 'Trẻ bị co giật, có nên đút ngón tay, nhét thìa, khăn vào mồm để tránh bị cắn vào lưỡi?', 'tre-bi-co-giat-co-nen-dut-ngon-tay-nhet-thia-khan-vao-mom-de-tranh-bi-can-vao-luoi-', '<p><strong>Mới đây trên mạng xã hội chia sẻ hình ảnh chiến sỹ cơ động \"nén\" đau nhét ngón tay vào mồm cháu bé bị sốt co giật được cộng đồng bày tỏ sự xúc động về hành động đẹp của chiến sĩ cảnh sát cơ động.</strong></p>\r\n', '<p>Bức ảnh được cư dân mạng chia sẻ trên được cho là tại sân vận động Thiên Trường, Nam Định. Trong khi trận bóng đang diễn ra thì có một cổ động viên nhí có dấu hiệu bị co giật. Lúc này lực lượng CSCĐ có mặt kịp thời đưa cháu đi cấp cứu. Trong phút giây nguy kịch ấy, một đồng chí CS cơ động đã dùng tay chịu đau để ngăn bé cắn lưới. Với những biểu cảm trên khuôn mặt của đồng chí cảnh sát này ai cũng có thể nhận ra sự đau đớn như thế nào. Bức ảnh đã nhận được nhiều lời khen ngợi, cảm phục và&nbsp; phải khẳng định đây là hành động rất đẹp, đáng hoan nghênh... Dù vậy, dưới góc độ y học thì lại có thể gây nguy hiểm cho trẻ.</p>\r\n\r\n<p>Theo quan niệm dân gian khi trẻ bị co giật thì nhét thìa,hay khăn thậm chí cả ngón tay vào miệng để đề phòng trẻ cắn vào lưỡi, tuy nhiên theo các bác sĩ việc làm này có thể sẽ gây nguy hiểm cho trẻ vì có thể làm tắc đường thở của trẻ.</p>\r\n\r\n<p>Cơn co giật toàn thể (grand-mal seizures) là một tình trạng co giật cơ tần suất cao và kéo dài. Khi co giật như vậy, người bệnh thường mất tri giác, ngừng hô hấp (các cơ hô hấp co giật liên tục hay co cứng nên phổi không giãn nở được), tăng tiết đàm nhớt đường hô hấp và không nuốt được gây sùi bọt mép, mắt trợn, tím tái. Co giật như vậy cũng hay gặp ở trẻ nhỏ từ 6 tháng tới 5 tuổi bị co giật do sốt cao lành tính, thường kéo dài dưới 5 phút, không để di chứng. Tuy nhiên nếu không xử trí đúng, có thể gây các biến chứng không nên có.<img alt=\"\" src=\"https://media.suckhoedoisong.vn/Images/haiyen/2019/08/05/anh.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Sơ cứu khi trẻ bị co giật</em></p>\r\n\r\n<p>LÀM GÌ KHI MỘT NGƯỜI BỊ CO GIẬT</p>\r\n\r\n<p>- Bình tĩnh, chừa một không gian đủ lớn cho người co giật, giữ khoảng cách giữa mọi người xung quanh và người đang co giật. Người đang co giật mất tri giác, có thể gây tổn thương tới người xung quanh.</p>\r\n\r\n<p>- Bảo đảm môi trường an toàn cho người co giật ĐƯỢC CO GIẬT TRONG AN TOÀN. Tránh xa vùng có nước, điện, thuỷ tinh, vật sắc nhọn, đồ dùng có thể ngã đổ gây chấn thương.</p>\r\n\r\n<p>- Kê vật mềm dưới đầu người co giật để tránh chấn thương đầu trong lúc co giật.</p>\r\n\r\n<p>- Nới lỏng quần áo quanh cổ, nữ trang, cravat nếu cần để không bị quấn chặt cổ gây nghẹt thở.</p>\r\n\r\n<p>- KHÔNG CẦN đưa bất cứ vật gì vào miệng, không có tác dụng gì nhưng có nguy cơ tổn thương niêm mạc miệng, gãy răng gây hít sặc nhất là trẻ nhỏ còn răng sữa chưa ổn định. Tuyệt đối không nặn chanh vào miệng, người co giật đang mất ý thức sẽ không nuốt được và sặc vào phổi gây viêm phổ, suy hô hấp.</p>\r\n\r\n<p>- Ghi lại thời gian co giật nếu có thể.</p>\r\n\r\n<p>-Nhẹ nhàng xoay bệnh nhân nằm nghiêng để làm thông thoáng đường thở, đàm nhớt sẽ chảy ra bên ngoài miệng tránh việc hít sặc nước bọt của chính mình. Khi trẻ con sặc sữa cũng nằm nghiêng là vì lý do này.</p>\r\n\r\n<p>- KHÔNG ĐÈ CHẶT BỆNH NHÂN, co giật cơ là tự phát không ý thức, đè chặt hay trói người bệnh không hề làm ngưng co giật mà sẽ gây chấn thương cho người co giật, tôi đã gặp người nhà cố sống cố chết đè người co giật làm bị té và chấn thương đầu.</p>\r\n\r\n<p>- Sau khi người co giật tỉnh lại, đừng bỏ người ta một mình mà phải theo dõi xem đã hồi phục tri giác chưa, có yếu cơ hay liệt không, không cho ăn uống gì cho tới khi chắc chắn đã hồi phục. Giải thích cho người bị co giật chuyện gì đã xảy ra.</p>\r\n\r\n<p>NÊN GỌI CẤP CỨU KHI</p>\r\n\r\n<p>- Co giật lần đầu tiên.<br />\r\n- Co giật hơn 5 phút<br />\r\n- Co giật hơn 1 lần, đợt này tiếp ngay sau đợt khác<br />\r\n- Người co giật không tỉnh lại sau khi co giật đã chấm dứt<br />\r\n- Nạn nhân bị chấn thương khi đang co giật.</p>\r\n\r\n<p>NGƯỜI BỊ CO GIẬT CÓ CẮN LƯỠI HAY KHÔNG?</p>\r\n\r\n<p>Có &nbsp;gặp nhưng rất ít và thường là nhẹ, không gây tác hại gì. Một khảo sát trên 106 trẻ bị co giật thì có 8 trẻ cắn lưỡi, tất cả là vùng viền hai bên lưỡi.</p>\r\n\r\n<p>Tại sao vậy?</p>\r\n\r\n<p>Khi co giật không có ai thè lưỡi ra cả, mà thường thụt nhẹ vào. Thè lưỡi là hành động có ý thức, đang co giật hàm cắn chặt trợn trắng cả ra làm sao mà thè lưỡi. Có lỡ cắn phải cũng chỉ là vùng viền hai bên lưỡi thôi, giống như nhai vội tự cắn lưỡi mình thôi, chả sao cả.</p>\r\n\r\n<p>Có một chi tiết rất thú vị trong khảo sát này là cắn lưỡi là một yếu tố phân biệt quan trọng giữa co giật và ngất (syncope), hễ có cắn lưỡi vùng viền hai bên luỡi gần như chắc chắn là co giật.</p>\r\n\r\n<p>Co giật cũng không làm tuột lưỡi gây nghẹt thở như lời đồn. Cố gắng chèn vật hay ngón tay vào giữa hàm răng người đang co giật là vô bổ vì không có tác dụng gì, mà sẽ gây chấn thương rách niêm mạc miệng, gãy răng, gây hít sặc vào phổi, chấn thương ngón tay, tăng nguy cơ nhiễm bệnh qua lại giữa người bị co giật và người cấp cứu.</p>\r\n\r\n<p>Trên đây là nguyên tắc cốt lõi để sơ cứu những người đang co giật là giúp đỡ người đó được co giật trong an toàn.</p>\r\n', 'EDcQL4Qh_tre-co-giat-dut-ngon-tay-nhet-thia-khan-vao-mom-de-tranh-bi-can-vao-luoi-dung-hay-sai1564982603.jpg', 1, 0, '2020-12-27', 21, '2020-12-27 15:56:57', '2020-12-27 15:56:57'),
(43, 'Dạy trẻ kỹ năng thoát hiểm khi bị bỏ quên trong ô tô', 'day-tre-ky-nang-thoat-hiem-khi-bi-bo-quen-trong-o-to', '<p><strong>Một đứa trẻ bị bỏ quên lại trong xe ô tô có thể tử vong nhanh chóng vì sốc nhiệt hay không? cách nào phòng tránh? dưới đây là lý giải của các bác sĩ BV Nhi Đồng TP. HCM</strong></p>\r\n', '<p>Sốc nhiệt là nguyên nhân tử vong hàng đầu trong các tai nạn xe cộ không va chạm ở trẻ dưới 15 tuổi.</p>\r\n\r\n<p>⚠️&nbsp;Cơ thể không thể điều hòa nhiệt độ kịp.<br />\r\n⚠️&nbsp;Cơ thể trẻ con nóng lên nhanh gấp 3 - 5 lần người lớn.<br />\r\n⚠️&nbsp;Các cơ quan trong cơ thể sẽ rối loạn khi chạm mốc 40 độ C và tiếp tục tăng.<br />\r\n⚠️&nbsp;Trẻ sẽ chết vì sốc nhiệt khi chạm mốc 42 độ C!<br />\r\n⚠️&nbsp;Xe nóng lên cực nhanh, chỉ trong 10 phút, nhiệt độ xe có thể lên tới 7 độ mỗi 10 phút!<br />\r\n⚠️&nbsp;Mở một cửa sổ không đủ để làm mát xe, nếu động cơ đã tắt.<br />\r\n⚠️&nbsp;Sốc nhiệt trong xe nóng có thể xảy ra kể cả khi ngoài trời chỉ 18 độ C.</p>\r\n\r\n<p>TAI NẠN NÀY CÓ THỂ XẢY RA CẢ Ở AI CẨN THẬN NHẤT, VÌ CHỈ MỘT PHÚT LƠ LÀ LÀ CÓ THỂ QUÊN!</p>\r\n\r\n<p>Cách duy nhất là tạo ra các quy tắc để không quên:</p>\r\n', '2GB94108_vi-sao-tre-nho-co-the-chet-vi-soc-nhiet-neu-bi-bo-quen-trong-o-to-khi-troi-nong1565148928.jpg', 1, 0, '2020-12-27', 21, '2020-12-27 15:57:30', '2020-12-27 15:57:30'),
(44, 'Ô nhiễm hóa chất', 'o-nhiem-hoa-chat', '<div class=\"sapo_detail\" style=\"margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 700; font-stretch: inherit; font-size: 17px; line-height: 27px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; color: rgb(45, 45, 45);\">Trong 50 năm qua, con người đã tạo ra khoảng 80.000 loại hóa chất, trong đó có các chất ô nhiễm hóa học hữu cơ được biết đến với nhóm 12 chất ô nhiễm hữu cơ khó phân hủy (POP), rất độc hại đối với con người.</div>\r\n\r\n<div id=\"content_detail_news\" style=\"margin: 0px 0px 30px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline;\">\r\n<p><strong>Bạn hiểu gì về ô nhiễm  hóa chất?</strong></p>\r\n</div>\r\n', '<p>• Thế giới đang thay đổi nhanh chóng. Môi trường, các bãi biển và rừng đang bị ảnh hưởng rất xấu bởi ô nhiễm do dân số ngày càng tăng trên thế giới. Trong 50 năm qua, con người đã tạo ra khoảng 80.000 loại hóa chất, trong đó có các chất ô nhiễm hóa học hữu cơ được biết đến với nhóm 12 chất ô nhiễm hữu cơ khó phân hủy (POP), rất độc hại đối với con người. Các chất ô nhiễm này liên tục thâm nhập vào cơ thể con người thông qua thực phẩm, đồ uống và phá huỷ các bộ phận của cơ thể.</p>\r\n\r\n<p><strong>Hậu quả</strong></p>\r\n\r\n<p>• Do bị các hóa chất POP tấn công, quá trình phát triển bình thường của phôi bị ảnh hưởng, kết quả là các trẻ sinh ra thường nhẹ cân, tỷ lệ tử vong cao hơn.</p>\r\n\r\n<p>• Ngoài ra, các hóa chất này còn gây gây ra vô sinh, các vấn đề về phổi và ngứa.</p>\r\n\r\n<p>• Đioxin và Furan còn gây ra bệnh Clorance với triệu chứng nôn, giảm thị lực, khả năng nghe kém, các vấn đề về= hô hấp, giảm cân, đau đầu, thay đổi các chức năng thông thường của gan, tụy và thận.</p>\r\n\r\n<p>• Theo một nghiên cứu của các nhà khoa học Pháp, bệnh tự kỷ có thể bị gây ra bởi việc não bộ của bào thai bị nhiễm độc bởi một số hóa chất do người mẹ nhiễm phải khi mang thai.</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\"><img alt=\"Ô nhiễm hóa chất\" id=\"img_183788\" src=\"http://skds3.vcmedia.vn/k:2015/1-china-polution45-1444707664427/o-nhiem-hoa-chat.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></div>\r\n</div>\r\n', 'u53lP5Yj_1-china-polution45-1444707664427.jpg', 0, 0, '2020-12-27', 22, '2020-12-27 15:58:34', '2020-12-27 15:58:34'),
(45, 'Cách sử dụng thuốc chống muỗi an toàn', 'cach-su-dung-thuoc-chong-muoi-an-toan', '<p><strong>Để việc dùng thuốc chống muỗi hiệu quả và an toàn, mọi người cần dùng thuốc đúng phương pháp</strong></p>\r\n', '<p>Trước khi sử dụng cho toàn thân, nên thử dùng trước cho một vùng da nhỏ mặt trong cánh tay. Nếu không xuất hiện kích ứng, mẩn ngứa thì từ đó mới sử dụng cho toàn bộ cơ thể. Không xịt trực tiếp thuốc lên người, nên xịt ra tay và xoa lên các vùng có nguy cơ bị muỗi đốt. Khi bôi cần tránh vùng mắt, mũi, miệng, vết thương hở. Có thể bôi thuốc lên quần áo, chăn, chiếu, màn... cũng cho tác dụng chống muỗi đốt. Chỉ sử dụng thuốc khi thực sự cần thiết (đi đến nơi xa lạ, du lịch...).</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 26px; font-family: &quot;Noto Serif&quot;; vertical-align: baseline; display: inline-block; width: 660px; text-align: center;\">\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\"><img alt=\"\" id=\"img_170146\" src=\"http://skds3.vcmedia.vn/2015/cach-su-dung-thuoc-chong-muoi-1440474301063.jpg\" style=\"border:0px; box-sizing:border-box; display:block; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:auto; line-height:26px; margin:0px auto; max-width:100%; padding:0px; text-rendering:geometricprecision; vertical-align:middle; width:auto\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricprecision; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 26px; vertical-align: baseline;\">Thuốc chống muỗi cũng phải được dùng đúng hướng dẫn.</div>\r\n</div>\r\n\r\n<p><strong>Lưu ý đặc biệt khi dùng thuốc chống muỗi cho trẻ em</strong></p>\r\n\r\n<p>Các loại thuốc chống muỗi đều có khả năng tác động lên hệ hô hấp và ảnh hưởng đến làn da của bé. Một số loại hóa chất tổng hợp có trong thuốc chống muỗi có thể nguy hiểm cho cơ thể bé khi chúng xâm nhập vào trong da. Nhất là đối với những bé dưới 6 tháng tuổi, không được sử dụng bất kỳ sản phẩm chứa DEET nào. Với bé trên 6 tháng tuổi, nên tránh dùng kem chống muỗi bôi trực tiếp lên da. Không bôi thuốc lên tay trẻ, vì trẻ thường xuyên cho tay vào miệng. Một số loại kem (dầu) chống muỗi có mùi hương và nồng độ rất mạnh, dễ gây dị ứng cho làn da nhạy cảm của bé. Sử dụng thuốc dạng nước hoặc kem thay vì thuốc phun xịt. Việc đó giúp cho trẻ không bị hít quá nhiều thuốc côn trùng, vì khi phun, thuốc dạng bụi nước có xu hướng lan ra khắp nơi. Nếu muốn sử dụng thuốc cho quần áo, hãy xịt thuốc khi không có trẻ và trước khi trẻ mặc chừng 30 phút.Sau khi không cần thiết phải bôi thuốc cho trẻ nữa, phải tắm rửa cho trẻ sạch sẽ để loại bỏ hóa chất có hại.</p>\r\n', 'SybmRpls_cach-su-dung-thuoc-chong-muoi-1440474301063.jpg', 1, 0, '2020-12-27', 22, '2020-12-27 15:59:00', '2020-12-27 15:59:00');

